style.css:
@-moz-document domain('apexfusion.com'), url-prefix('https://apexfusion.com/apex') {
body {
    background-color: #2a3132;
}

div.dash-probe-info-name {
    color: #90afc5;
}

div.dash-selector-name {
    color: #90afc5;
}

div.dash-switch-name {
    color: #90afc5;
}

div.dash-feed-name {
    color: #90afc5;
}

div.dash-note-name {
    color: #90afc5;
}

div.dash-mlog-name {
    color: #90afc5;
}

div.dash-reminder-name.dash-actionable {
    color: #90afc5;
}

div.dash-ebg-name {
    color: #90afc5;
}

p.text-muted {
    color: #90afc5;
}

.bg-light {
    background-color: #2a3132!important;
}

#nav-main-menu-name {
    color: #90afc5;
}

.navbar-light .navbar-nav .nav-link {
    color: #90afc5;
}
#nav-main-menu-total,
#nav-user-menu-total {
    background-color: #336b87;
}

div.dash-probe-info-data {
    color: #336b87;
}

div.dash-ebg-gauges {
    background-color: #336b87;
}

div.dash-reminder-list {
    background-color: #336b87;
}

div.dash-clock-user {
    background-color: #336b87;
}
div.dash-clock-apex {
    background-color: #336b87;
}
button.btn.btn-dash.btn-dash-feed-cancel {
    background-color: #763626;
    border-color: #763626;
}

span.dash-selector-state.on {
    color: #336b87;
}

span.dash-selector-state.off {
    color: #763626;
}

span.dash-selector-state.auto {
    color: #90afc5;
}

.dash-selector-slide.on {
    background-color: #336b87;
    border-color: #336b87;
}

.dash-selector-slide.auto {
    background-color: #90afc5;
    border-color: #90afc5;
}

.dash-selector-slide.off {
    background-color: #763626;
    border-color: #763626;
}

.btn-dash-feed-start.dash-feed-active {
    background-color: #336b87;
    border-color: #336b87;
}

.dash-ebg-header .dash-ebg-name {
    color: #90afc5;
}
.dash-tri-display {
    background-color: #336b87;
}
.dash-tri-header .dash-tri-name {
    color: #90afc5;
}
.dash-tri-header .dash-tri-status {
    color: #90afc5;
}
.dash-dos-name-0 {
    color: #90afc5;
}
.dash-dos-name-1 {
    color: #90afc5;
}
.dash-ddr-name-0,
.dash-ddr-name-1 {
    color: #90afc5;
}
.dash-dos-display {
    background-color: #336b87;
}
.dash-switch-slide.closed {
    background-color: #336b87;
    border-color: #336b87;
}
.dash-switch-slide.open {
    background-color: #763626;
    border-color: #763626;
}
.dash-ddr-display {
    background-color: #336b87;
}
rect.highcharts-background {
    fill: #336b87;
}
rect.highcharts-plot-background {
    fill: #336b87;
}
.dash-feed {
    height: 110px;
}
.btn-dash-feed-start {
    padding: 0 1.4em;
    line-height: 35px;
}
.btn.btn-dash.btn-dash-feed-cancel {
    position: absolute;
    margin-top: 45px;
    line-height: 35px;
    padding: 0 1em;
}
}
