style.css:
@-moz-document url-prefix("https://www.finn.no") {
/* BODY, HEADER, FOOTER, GLOBAL */
html {
    height: 100%;
    /* Fixed height */
}

body,
.pageholder,
.container.bg-ice {
    background: #222 !important;
    color: #ddd;
}
.page-header {
    color: #ddd;
}
.top-bar,
nav.top-bar__nav {
    background: #111;
    border-color:#444;
}

nav.top-bar__nav a {
    color: #ddd !important;
    font-weight: bold;
}
.top-bar__menu-item,
.top-bar__home {
    border-color: #222 !important;
}
.top-bar__menu-item--is-active,
.top-bar__menu-item:focus,
.top-bar__menu-item:hover {
    border-color: #06befb !important;
}

.fp-map-button {
    background-image:none;
    border-color:#444 !important;
}

.use-fade-effect::after, .read-more::before {
    background-image:linear-gradient(180deg, rgba(34,34,34,0) 0%, rgba(34,34,34,1) 70%) !important;
}

a,
a:link,
a:visited,
.link,
.link:link,
.link:visited {
    color: #06befb !important;
}

.u-r-size1of3 {
    padding-left: 10px;
}

.footer,
.footer__pageholder {
    background-color: #222;
    border-color: #111;
    color: #ddd;
}

.panel,
.css-1ciockf-Box,
.css-89a5ka-Container,
.scc_tr-reputation {
    background-color: #222;
    color: #ddd;
}

.css-q28aux-Title,
.scc_tr-reputation-aspect-worded__category,
.scc_tr-reputation-aspect-worded__score,
.scc_tr-reputation_worded-score,
.u-licorice {
    color: #ddd
}

.panel--info,
.panel--tips {
    background-color: #444;
}

.dark-links a:visited,
a.dark-links:visited {
    color: #777;
}

.dark-links a,
.dark-links a:link,
a.dark-links,
a.dark-links:link {
    color: #999;
}

/* MELDINGER */
.msg-list .msg-list__items,
.msg-chat,
.msg-input,
.msg-input .msg-input__textarea,
.msg-list__edit-bar {
    background-color: #222;
}

.msg-list__edit-bar-primary-button {
    color:#06befb;
}

.msg-header {
    background-color:#111;
}

.msg-dropdown {
    background-color: #333;
}

.msg-list .msg-list__items {
    color: #ddd;
}

.msg-list .msg-list__items .msg-list__item .msg-list__text .msg-list__product,
.msg-list__message span {
    color: #bbb;
}

.msg-list .msg-list__items .msg-list__item.selected {
    background-color: #111;
}

.msg-layout .msg-layout__secondary {
    border-color: #666;
}

.msg-header .msg-header__link {
    color: #ddd !important;
}

.msg-header .msg-header__product .msg-header__meta .msg-header__title,
.msg-header .msg-header__product .msg-header__meta .msg-header__price {
    color: #ddd !important;
}

.msg-chat .msg-chat__items .msg-chat__item .msg-chat__bubble.msg-chat__bubble_third-person {
    background-color: #444;
    color: #fff;
}

.msg-input .msg-input__textarea,
.msg-dropdown .msg-dropdown__item,
.msg-chat__time {
    color: #eee;
}

.msg-list .msg-list__items .msg-list__item:hover {
    background-color: #111;
}

.msg-list__product:hover {
    background-color:inherit;
}

.msg-dropdown__item {
    background-color:#333;
}

/* VARSLINGER */
.mod .inner {
    background-color: #222 !important;
}

body:not(.probably-touch) a.mod.clickable:hover .inner,
body:not(.probably-touch):not(.no-focus-ring) a.mod.clickable:focus .inner {
    background-color: #111;
}

.spaden .licorice,
.spaden a.licorice,
.licorice,
a.licorice,
.scc_tr-review-rating__text,
.scc_tr-review-rating__text-empty,
.scc_tr-review-meta__date,
.reputation-aspect-worded__score,
.reputation_worded-score {
    color: #ccc;
}

.link--dark,
.link--dark a,
.link--dark:link,
.link--dark a:link,
.link--dark:visited,
.link--dark a:visited,
.link--dark:hover,
.link--dark a:hover,
.link--dark:focus,
.link--dark a:focus,
.link--dark:active,
.link--dark a:active {
    color: #ddd;
}

.list.list--vertical a {
    color: lightblue;
}

/* INPUTS */
.input-toggle label,
.input__sub-text,
.input .label,
.input label {
    color: #ddd;
}

.input input,
.input--select__wrap select,
.button,
a.button,
.frontpage-search__input,
.frontpage-search__submit,
.input--textarea textarea {
    background-color: #222;
    color: #fff;
}

.button-group label,
a.button,
a.icon,
button,
input[type=button],
input[type=reset],
input[type=submit],
label.button {
    background-color: #222;
    color: #fff;
}

.button:hover,
a.button:hover,
.button-group label:hover,
a.button:hover,
a.icon:hover,
button:hover,
input[type=button]:hover,
input[type=reset]:hover,
input[type=submit]:hover,
label.button:hover {
    background-color: #444;
}

.button--pill,
a.button--pill {
    color: #999;
    background-color: #222;
}



/* FRONTPAGE */
.u-bg-ice,
.u-bg-milk {
    background-color: transparent;
}

.ad-preview__click-area,
.ad-preview__click-area:link,
.ad-preview__click-area:visited,
.ad-preview__click-area:hover,
.ad-preview__click-area:focus,
.ad-preview__click-area:active,
.ad-stream__location {
    color: #ddd;
}

/* REALESTATE */
.ads__unit {
    background-color: #222;
    margin-bottom: 5px;
}

.ads__unit:not(.ads__unit--banner):focus-within,
.ads__unit:not(.ads__unit--banner):hover {
    background-color: #111;
}

.ads__unit__link,
.ads__unit__link:active,
.ads__unit__link:focus,
.ads__unit__link:hover,
.ads__unit__link:link,
.ads__unit__link:visited {
    color: #ddd;
}

.ads__unit__content__details {
    color: #aaa;
}

.nabolag-widget section {
    background: none;
}

.nabolag-widget section h2 {
    color: #eee;
}

/* CARS */
.spaden .bg-ice,
.newtab--active {
    background-color: #222 !important;
}
.bKpBbG {
    background:inherit !important;
}
.scc_tr-identity,
.scc_tr-identity-extended {
    background-color: #222;
}

.scc_tr-communication {
    color: #ddd;
}
.scc_tr-review-item__main {
    background-color: #191919;
    color: #ddd;
}

.motor-tabs .tabs__control__tab.tabs__control__tab--is-active svg,
.motor-tabs .tabs__control__tab:hover svg {
    color: #06befb !important;
}

.tabs__control__tab--is-active,
.tabs__control__tab[data-selected],
.tabs__content {
    background-color: #151515;
    color: #fff;
}

.broadcast .button--pill {
    background-color:#fff;
}
.broadcast .broadcast__content {
    color:#333;
}

.modal__container.u-bg-ice {
    background-color:#222 !important;
}
.panel-loop--tips {
    background-color:#111;
}

.panel-loop--tips .panel-loop__header {
    background-color: #333;
}

.panel--is-clickable-hack:hover, .panel--is-clickable-hack:focus, .selectedPackageHover {
    border: 2px solid #0063FB;
}

.selectedPackage .panel-loop__header, .selectedPackageHover .panel-loop__header {
    background-color: #0063FB;
}

.vehicleListCard {
    background: #111;
}

.tabs__control {
    background: transparent;
}

}
