style.css:
@-moz-document regexp("((https:\\/\\/|http:\\/\\/|www.|https:\\/\\/www\\.|http:\\/\\/www.|)mixmods\\.com\\.br.*)") {
* {
border-color:#e0e0e0!important
}

#main-wrapper * {
font-size:14px;
line-height:24px;
}

.vcard *,.comment__header .author a {
font-size:16px!important
}

.post-title.entry-title * {
font-size:24px!important
}

.top-menulite li:hover a:hover,#inv-navigation li:hover a:hover {
background-color:#444!important;
color:#e0e0e0!important
}

body,html,input,.banner,.banner2,li,.footer-column h3:before,.footer-column,.menusearch,.widget,.topics,.widget-content div,.footerbar,.panel,.posts,.lastpost,.inner *,.copyright,input::placeholder,#content-wrapper,#wrap,#footer-wrapper,#blog-pager a:link,#qr_posts *,#blog-pager a:visited,#disqusshow,#disqus_thread,#sidebar summary,#footer-end,#inv-navigation {
background-color:#e0e0e0!important;
color:#454545
}

#header {
background-color:#e0e0e0!important;
background-image:none!important
}

img,#player {
-moz-box-shadow:0 0 15px 4px #454545 inset;
-webkit-box-shadow:0 0 15px 4px #454545 inset;
-webkit-filter:grayscale(65%);
box-shadow:0 0 15px 4px #454545 inset;
filter:grayscale(65%)
}

.connect__button {
-webkit-filter:grayscale(100%);
filter:grayscale(100%)
}

iframe {
-webkit-filter:grayscale(100%) saturate(0.0);
filter:grayscale(100%) saturate(0.0)
}

#google_image_div,#banner,#adContent,#adContent *,.avatar img,.fp-facebook,#adContent.text-square {
-webkit-filter:grayscale(100%) saturate(0.0) contrast(0.5);
filter:grayscale(100%) saturate(0.0) contrast(0.5)
}

a,.lastpost a {
color:#07a!important
}

.englishwarning span {
color:#a00!important
}

#inv-navigation ul,#inv-navigation li,.message-box textarea,#page-body,#qr_posts div {
-moz-box-shadow:0 0 0 .01em #454545 inset;
-webkit-box-shadow:0 0 0 .01em #454545 inset;
background-color:#e0e0e0!important;
box-shadow:0 0 0 .01em #454545 inset
}

.message-box textarea,#qr_posts {
-moz-box-shadow:0 0 4px .1px #454545 inset;
-webkit-box-shadow:0 0 4px .1px #454545 inset;
box-shadow:0 0 4px .1px #454545 inset
}

li.doremifa.icons a,.cc-btn,.forabg,.post-container :target,.headerbar,.headerbar *,.showpagePoint,.jump-link,.jump-link a {
background-color:#454545!important;
border-color:#454545!important;
color:#e0e0e0!important
}

.top-menulite li.donate a{
background-color:#a00!important;
color:#e0e0e0!important
}

table,tbody,td,tr,article,.viennalitetop-wrapper,.input-wrapper input,.post-actions,.post-title-header,.goog-te-combo,.button,.navbar,fieldset.quick-login input,.activitybutton,.label-block,.textarea,.placeholder .commentnote,#sidebar h2,#search-box,#inv-navigation a,#search-box i,#keywords,#sidebar-wrapper div,.blog-pager {
-moz-box-shadow:0 0 0 .01em #454545 inset;
-webkit-box-shadow:0 0 0 .01em #454545 inset;
background-color:#e0e0e0!important;
box-shadow:0 0 0 .01em #454545 inset;
color:#454545!important
}

#sidebar-wrapper * {
list-style:square
}

#Stats1_content:after {
    content: "CSS modificado por DeltaHL do Fórum MixMods.";
    font-size: 13px
}

.relhead *,h1,h2,h3,li a {
color:#454545!important
}

.label-count {
color:#fff0
}

.commentnote:after {
content:"Se você quebrar alguma regra, será convidado a visitar o Hue.";
font-weight:700
}
}
