style.css:
@-moz-document domain("www.roblox.com"), domain("roblox.com") {
.ad,
.ad *,
.img_ad,
.ad-annotations,
#google_image_div,
#aw0,
#AdvertisingLeaderboard {
    display: none !important;
}
.ad,
.ad *,
.img_ad,
.ad-annotations,
#google_image_div,
#aw0,
#AdvertisingLeaderboard {
    display: none !important;
}
/*Ad removal */

/* i want to fill up this space lik rlly bad */

#catalog-content {
    background-color: rgba(228, 0, 255, .1);
    border: 5px solid transparent;
}


#header {
    background-color: #ff91ff;
    border-color: #ff91ff
}
.navigation .notification-icon,
.rbx-highlight {
    background: #ff91ff;
}
#chat-header {
    background: #ff91ff;
    /*Party header*/
    
    ;
}
.slide-item-container-left,
.profile-avatar-right {
    background: #ffaaff;
}
.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
.rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active {
    box-shadow: 0 -4px 0 .6 #ff91ff inset;
    ;
}
.forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th {
    background: #ffaaff !important;
    /*Forum colors and messaging underline color*/
    
    ;
}
.rbx-upgrade-now #upgrade-now-button,
.rbx-btn-secondary-xs.btn-more,
#AjaxCommentsContainer > div > div.comment-form > form > button,
.rbx-vip-server-create,
.btn-secondary-xs {
    background-color: #ffaaff !important;
    border-color: #0000;
    /*Upgrade button*/
    
    ;
}
.rbx-upgrade-now #upgrade-now-button:hover,
.rbx-btn-secondary-xs.btn-more:hover,
#AjaxCommentsContainer > div > div.comment-form > form > button:hover,
.rbx-vip-server-create:hover,
.btn-secondary-xs:hover {
    background: #ffaaff !important;
    border-color: #0000;
}
#GamesListsContainer > div {
    background-color: transparent !important;
    width: 100% !important;
}
#navbar-search-input,
#navbar-search-btn {
    border: 1px solid #fff0;
    /*Search border color*/
    
    ;
}
body {
    background: transparent !important;
}
.content {
    background: transparent !important;
}
#fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://yt3.ggpht.com/m1AO7gmuv5l4g87PZxdr_SDLm9rl4nZRjO2VXeTUfgoFQaeaQ3BVsHCYQP8XtDNDgQ_8-QrFqw=w2120-fcrop64=1,00000000ffffffff-nd-c0xffffffff-rj-k-no") !important;
    /* Background */
    
    background-size: cover !important;
    background-attachment: fixed !important;
}
#party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off {
    background: linear-gradient(to bottom, #ffaaff, #ff91ff, 166, 240);
}
#ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(10),
#Body > table > tbody > tr > td:nth-child(10) {
    display: #ff91ff;
}
#GamesPageLeftColumn {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
    background: rgba(255, 255, 255, 0) !important;
}

.dropdown-menu {
    animation: fadein 0.5s;
    box-shadow: 0px 0px 0px;
}
.roblox-popover-content.bottom .notification-stream-wrap.open {
    box-shadow: 0px 0px 100px;
}
.chat-main {
    box-shadow: 0px 0px 100px;
}
@keyframes blur {
    from {
        filter: blur(25px);
    }
    to {
        filter: blur(0px)
    }
}
.menu-vertical .menu-option .menu-secondary-option {
    animation: fadein 0.5s;
}
.menu-secondary-container {
    animation: fadein 0.5s;
}
.menu-vertical .menu-option .menu-secondary-option {
    animation: fadein 0.5s;
}
.menu-secondary-container {
    animation: fadein 0.5s;
    background-color: transparent !important;
    width: 100% !important;
}
#navigation {
    background-color: rgba(255, 106, 255, .15)
    /*Left side navi*/
    ;
}
.rbx-btn-primary-lg {
    background-color: rgba(255, 205, 255, 0.5);
}
.rbx-btn-primary-lg:hover,
.rbx-btn-primary-lg:focus {
    background-color: rgba(255, 180, 255, 0.5);
}
.chat-container.collapsed .chat-main,
.chat-main,
.dialog-container,
.dialog-container.collapsed {
    transition: height 1s
}
.chat-friend-list .chat-friend-container,
.input-group-btn .dropdown-menu li a {
    transition: box-shadow 1s
}
.game-card-container {
    transition: height 0.5s, top 0.5s;
    height: 200px;
    overflow: hidden;
    position: relative;
    top: 25px
}
.game-card-container:hover {
    transition: height 1s, top 1s;
    height: 107%;
    top: 0px;
    box-shadow: 7px 7px 25px
}
#navigation {
    transition: width 1s
}
}
