  {
    border-color: #c704ad transparent;
  }

  ._work:before
  {
    box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.05);
  }

  ._2AOtfl9 svg
  {
    fill: #eee;
  }

  ._2JPrxI4:after
  {
    background: linear-gradient(90deg,hsla(0,0%,100%,0),#424242);
  }

