/* ad */

  section.ad iframe, .side-menu.comic-container iframe
  {
    display: none;
  }

/* Custom scrollbar - chrome */

  ::-webkit-scrollbar
  {
    width: 12px;
  }

  ::-webkit-scrollbar-track
  {
    background: #777;
  }

  ::-webkit-scrollbar-thumb
  {
    background: #555;
  }

  ::-webkit-scrollbar-thumb:hover
  {
    background: #8c0072;
  }
