style.css:
@-moz-document domain("myzcloud.me") {
body {
    background-color: #222629; 
    margin: 0;
    font-family: Roboto,sans-serif;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #747a80;
}
/* Grey Theme */
[data-ma-theme=blue-grey] .navigation__active:before, [data-ma-theme=blue-grey] .navigation__active>a, [data-ma-theme=blue-grey] .navigation__sub--active>a, [data-ma-theme=blue-grey] .playlist__actions, [data-ma-theme=blue-grey] .playlist__actions a, [data-ma-theme=blue-grey] .playlist__control {
    color: #55585b;
}
[data-ma-theme=blue-grey] .alphabet-shortcuts__helper, [data-ma-theme=blue-grey] .btn, [data-ma-theme=blue-grey] .header, [data-ma-theme=blue-grey] .login__block__btn, [data-ma-theme=blue-grey] .login__block__header, [data-ma-theme=blue-grey] .player-footer, [data-ma-theme=blue-grey] .top-menu {
    background-color: #2a2e32;
}
[data-ma-theme=blue-grey] .btn:hover, [data-ma-theme=blue-grey] .progress {
    background-color: #232629;
}
[data-ma-theme=blue-grey] .progress-bar {
    background-color: #393e44
}
/* Green Theme */
[data-ma-theme=green] .navigation__active:before, [data-ma-theme=green] .navigation__active>a, [data-ma-theme=green] .navigation__sub--active>a, [data-ma-theme=green] .playlist__actions, [data-ma-theme=green] .playlist__actions a, [data-ma-theme=green] .playlist__control {
    color: #28a11c;
}
[data-ma-theme=green] .alphabet-shortcuts__helper, [data-ma-theme=green] .btn, [data-ma-theme=green] .header, [data-ma-theme=green] .login__block__btn, [data-ma-theme=green] .login__block__header, [data-ma-theme=green] .player-footer, [data-ma-theme=green] .top-menu {
    background-color: #28a11c;
}
[data-ma-theme=green] .btn:hover, [data-ma-theme=green] .progress {
    background-color: #276621;
}
[data-ma-theme=green] .progress-bar {
    background-color: #12370e
}
.content--full {
    background-color: #282828;
    border: 1px solid #31363b;
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.2) !important;
}
.card {
    position: relative;
    display: flex;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    background-color: #282828;
    background-clip: border-box;
    border: 1px solid transparent;
    border-radius: 2px;
    
}
.playlist:not(.playlist--inverse) .active {
    background-color: #2a2e32;
}
.playlist__heading a {
    color: #eff0f1;
    text-decoration: underline;
}
.playlist__heading a:hover {
    color: #168de2;
    text-decoration: underline;
}
.desc-switch {
    color: #168de2;
    display: inline;
    border-bottom: 1px dotted #4d4d4d;
}
.listview__heading {
    font-size: 1rem;
    color: #eff0f1;
    position: relative;
}
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    margin-bottom: .5rem;
    font-family: inherit;
    font-weight: 500;
    line-height: 1.2;
    color: #eff0f1;
}
.playlist__heading {
    font-size: 1.3rem;
    color: #eff0f1;
    position: relative;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
.playlist:not(.playlist--inverse).playlist--hover .playlist__item:hover, .playlist:not(.playlist--inverse).playlist--striped .playlist__item:nth-child(even) {
    background-color: #404347;
}
.listview:not(.listview--inverse).listview--hover .listview__item:hover, .listview:not(.listview--inverse).listview--striped .listview__item:nth-child(even) {
    background-color: #404347;
}
.jp-volume {
    width: 140px!important;
    padding-right: 10px!important;
    margin-top: 5px;
    height: 40px!important;
}
.table-hover tbody tr:hover, .table-striped tbody tr:nth-of-type(odd) {
    background-color: #282828;
}
.table td, .table th {
    padding: 1rem 1.5rem;
    vertical-align: top;
    border-top: 1px solid #31363b;
}
.link, table a:not(.btn) {
    color: #eff0f1;
    text-decoration: underline;
}
.toolbar:not(.toolbar--inner) {
    background-color: #55585b;
    border-radius: 2px;
    margin-bottom: 15px;
    box-shadow: 0 0 0 0 rgba(0,0,0,.075);
}
[data-ma-theme=blue-grey] .nav .nav-link {
    color: #fff;
}
.nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active {
    color: #495057;
    background-color: #2a2e32;
    border-color: #dee2e6 #dee2e6 #FFF;
}
[data-ma-theme=blue-grey] .nav .nav-link {
    color: #fff;
}
.album-info, .profile__info {
    font-size: 1.25rem;
    color: #fff;
    text-decoration: none;
}
.album-info a:not(.btn), .profile__info a:not(.btn) {
    font-size: 1.25rem;
    color: #fff;
    text-decoration: underline;
}
.contacts__item {
    background-color: #2a2e32;
    border-radius: 2px;
    box-shadow: 0 0 0 0 rgba(0,0,0,.075);
    padding: 1.8rem 1.5rem 1.15rem;
    margin-bottom: 30px;
}
/* Sidebar */
.sidebar--hidden {
    background-color: #2a2e32;
    transition: transform .3s,opacity .3s;
}
.navigation li a {
    color: #eff0f1;
    transition: background-color .3s,color .3s;
    font-weight: 500;
    display: block;
}
/* Comments */
.col-lg-9 {
    flex: 0 0 75%;
    max-width: 75%;
    Color: #eff0f1;
}
.messages__content {
    position: relative;
    flex: 1 1 auto;
    padding: 2.5rem;
    overflow: hidden;
    height: 100%;
    background-color: #282828;
}
.messages__actions>small a {
    color: #168de2;
}
.toolbar--inner {
    border-bottom: 1px solid #31363b;
    margin-bottom: 1rem;
    border-radius: 2px 2px 0 0;
    background-color: #282828;
}
.messages__reply {
    border-top: 1px solid #282828;
    position: relative;
    background-color: #282828;
}
.form-control {
    display: block;
    width: 100%;
    padding: .6rem 1rem;
    font-size: 1rem;
    line-height: 1.154;
    color: #eff0f1;
    border: 1px solid #31363b;
    border-radius: 0;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}
/* Scrollbar */
::-webkit-scrollbar {
  width: 12px;
  height: 10px;
}
::-webkit-scrollbar-button {
  width: 0;
  height: 0;
}
::-webkit-scrollbar-thumb {
  background: #595959;
  border: 0 none;
  border-radius: 1px;
}
::-webkit-scrollbar-thumb:hover {
  background: #9f9f9f;
}
::-webkit-scrollbar-thumb:active {
  background: #6e6e6e;
}
::-webkit-scrollbar-track {
  background: #313131;
  border: 1 none;
  border-radius: 1px;
  border-color: #242424 !important;
}
::-webkit-scrollbar-track:hover {
  background: #404040;
}
::-webkit-scrollbar-track:active {
  background: #353535;
}
::-webkit-scrollbar-corner {
  background: transparent;
}

}
