style.css:
@-moz-document domain("lib.rs") {
body {
    background-color: #222;
    color: white;
}

hr {
    border-color: #ffffff15;
}

main a:link h4,
a:link,
header nav > ul > li a:hover:link {
    color: #789ce3 !important;
    text-shadow: 1px 1px 0 #2227;
}

main a:visited h4,
a:visited,
header nav > ul > li a:hover:visited {
    color: #a878e3 !important;
}

main a .desc,
main .desc::before {
    color: white !important;
    opacity: 0.6 !important;
}
main li > a:hover .desc {
    color: white;
    opacity: 0.75;
}
main  li > a:hover,
a:link code {
    background: #ffffff0a !important;
}
main .downloads,
main .downloads b {
    color: white !important;
}
main .labels > span.nightly {
    color: #8e9bff !important;
    border-color: #8e9bff !important;
    background-color: #0007 !important;
}
main a:hover .nightly {
    background-color: #8e9bff !important;
    color: black !important;
    text-shadow: none !important;
}

main .version {
    color: #ee5028 !important;
}
main .version.stable {
    color: #2bd591 !important;
}

#category-crates a:hover code,
#category-subcategories a:hover code,
#deps .cfg {
    color: black;
}


code {
    color: white !important;
    background-color: #fff2 !important;
}
pre {
    color: white !important;
    background-color: #fff1 !important;
}
pre code {
    background-color: transparent !important;
}


#home {
    background: linear-gradient(#372542, #4b2364);
    color: white;
}

#home input[type=search] {
    color: white;
    background-color: #0003;
    border-color: black;
}

#category, #serp {
    background: linear-gradient(#233735, #27534d);
}

#package {
    background: #333;
}
#package.nightly {
    background: linear-gradient(#252337, #272c53);
}

header nav > ul > li a:link {
    color: white !important;
    background-color: transparent;
}
header nav > ul > li a:hover {
    color: white;
    background-color: #0005;
    border-color: transparent;
}
header nav > ul > li.active {
    color: white;
    background-color: #222;
}

.readme thead > tr > td,
.readme thead > tr > th {
    background-color: black;
    border-color: #444444;
}
.readme table,
.readme td,
.readme th {
    border-color: #444444;
}
.readme tr:nth-child(2n) td,
.readme tr:nth-child(2n) th {
    background-color: #171717;
}

a:link:not(:hover) code {
    border-color: #777;
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
}

#results .tryalso {
    border-color: #3a3a3a;
}




.k, .k-ctrl, .k-ot, .stor-ty-fn, .stor-mod-lf, .stor-ty, .stor, .stor-mod, .var-parm-opt {
    color: #e970ff;
}
.sup-mac-rs, .m-ann-rs .var-ann, .m-ann-rs .var-fn {
    color: #97ee6d;
}
.m-gen .pun-def, .m-gen .pun-sec {
    color: white;
}
.m-gen, .ent-ot-inherited, .sup-ty {
    color: #e3a362;
}
.k-op, .sup-const, .sup-fn, .var-parameter {
    color: #7ebff0;
}
.str-q {
    color: #ecb671;
}
.str-q .const-ot, .str-q .pun-def-var, .str-q .var-ot {
    background: transparent;
    color: #fff4e5;
}
code .const-lang, code .const-num,
pre .const-lang, pre-const-num {
    color: #ab9fff;
}
.const-char, .const-ot, .pun-def-var, .var-ot, .var-parm {
    color: #6bbdc6;
}
.com-ln .pun-def {
    color: #aeb9ae;
}
.com-ln, .src-toml .com-ln .pun-def {
    color: #7d8e7d;
}
}
