style.css:
@-moz-document domain('github.com'), domain('gist.github.com') {
  pre,
  code,
  tt,
  kbd:not(.badmono),
  pre,
  samp,
  .blob-code,
  .file-data pre,
  .line-data,
  #gist-form .file .input textarea,
  .blob-code-inner {
    font-family: 'Fira Code', monospace !important;
  }
}
