style.css:
@-moz-document domain("usq.edu.au"), domain("uconnect.usq.edu.au"), domain("edu.au") {
/* Do most of the formatting */
section, body {
    background-color: #262626 !important;
    color: white;
}

header {
    background-color: #262626 !important;
    color: white;
}

.footer * {
    background-color: black !important;
}

aside * {
    background-color: black !important;
    color: white !important;
}

 /* Do specific things and fix issues */

span, .no-overflow * {
    color: white !important;
    background-color: #262626 !important;
}

aside header input::placeholder {
    color: white !important;
}

aside .container-fluid, aside input{
    position: relative;
}

aside time * {
    background-color: black !important;
}

#packeryGrid .block-wrapper, #block-announcement .block-wrapper, #block-studydesk .block-wrapper {
    background-color: black !important;
    box-shadow: 4px 4px #4d4d4d;
    border: 1px solid white;
}

#packeryGrid .info-panel * {
    color: black !important;
    background-color: white !important;
}

#packeryGrid .social-hub-body * {
    background-color: black !important;
}

#block-student-centre .panel {
    color: black;
    background-color: white !important;
    padding: 1px;
}

#block-studydesk .semester * {
    color: white;
}

#block-studydesk a * {
    background-color: black !important;
}

#breadcrumb-bar {
    background-color: black;
}

#breadcrumb-bar li * {
    color: white;
    background-color: black !important;
}

.desktop-first-column .content {
    border: 1px solid white;
    padding: 10px;
}

#page-heading h1 {
    color: white;
}

.event-list * {
    color: white !important;
    background-color: black !important;
}

/* Hide the annoying stuff*/

#block-google-drive, .menu a {
    display: none !important;
}
}
