style.css:
@-moz-document domain("afisha.ru") {
.button, .button:hover, .ui-button, .ui-button:hover, .menu-wrapper {
    background-color: #ce2125
}
a, a:hover, .filter-button, .filter-button:hover, .calendar__month-name, .calendar__link, .card__link:hover .card__title {
    color: #ce2125
}
.button, .ui-button, .ui-button:hover, .button:hover, .rating-badge_color_blue, .rating-badge_color_blue:hover {
    border: 1px solid #ce2125
}
.rating-badge_color_blue, .rating-badge_color_blue:hover {
    background: #ce2125
}
.afisha-choice {
    border-color: #ce2125 transparent #ce2125 #ce2125
}
.page__footer-rules, .page__footer-section_nav__cell__title, .page__footer-company {
    color: #858585
}
.page__footer-subscribe-form-input, .page__footer {
    background-color: #323232
}
.page__footer .page__footer-app, .page__footer:hover .page__footer-app, .page__footer-app-name {
    color: black
}
.like, .like:before, .filter-button:after, .object__section-header-link:after, yatag > yatag > yatag:nth-child(3) {
    filter: grayscale(1);
}
.page__footer-section_header:after {
    background-image: url(https://i.imgur.com/u6Wbwl4.png);
}
.page__footer .page__footer-app_android {
    background-image: url(data:image/svg+xml;utf8;base64,
);
    background-size: 25.5px 30px;
    background-position: 14px 50%;
}
.page__footer .page__footer-app_apple {
    background-image: url(data:image/svg+xml;utf8;base64,
);
    background-size: 25.5px 30px;
    background-position: 12px 40%;
}

.button, .button:hover, .ui-button, .ui-button:hover, .menu-wrapper {
    background-color: #ce2125;
    color: #fff;
}

}
