.mw-body {
    max-width: 760px;
    margin: auto;
    border: none;
    margin-top: 5em;
}
.firstHeading, .mw-headline, h2, h1, h3 {
    font-family:'Lora', serif !important;
    font-weight: bold;
}
.firstHeading {
    font-size: 3em !important;
}
#mw-panel, #right-navigation, #left-navigation, #simpleSearch *, .thumbcaption {
    font-family:'Libre Franklin', sans-serif !important;
}
.thumbcaption {
    font-size: 12px !important;
}
#mw-content-text {
    font-family:'Lora', serif;
    font-size:1.3em;
}
.cnotice {
    display: none;
}
body, #mw-head {
    background: #fff;
}
#toc {
    border-radius: 3px;
    padding: 15px 20px;
}
.thumbinner {
    border-radius: 3px;
}
.wikitable {
    font-size: 80%;
    font-family:'Libre Franklin', sans-serif !important;
}
.infobox_v2, .infobox {
    clear: right !important;
    width: 100% !important;
    margin: 0;
    margin-top: 1.5em;
    border-radius: 3px;
    margin-bottom: 1.5em;
}
.infobox tbody > tr > th {
    text-align: right !important;
    padding-right: 5px;
}
.cabecera {
    text-align: center !important;
}
.mergedtoprow * hr, hr.mergedtoprow {
    margin: 20px;
}
td.noprint *, .toctogglespan * {
    color: #989898 !important;
    padding-top:15px;
    font-size: 10px;
    font-family:'Libre Franklin', sans-serif !important;
}
table.ambox {
    margin: 1.5em 0 !important;
}
.rellink {
    padding-left: 0;
    margin-bottom: 0;
}
table.ambox * {
    font-family:'Libre Franklin', sans-serif !important;
    font-size: 13px !important;
}
.vectorTabs li, .vectorTabs span {
    background-image: none;
}
}
