style.css:
@-moz-document url-prefix("http://scp-wiki.wikidot.com"), domain("scp-wiki.net"), domain("scpwiki.com") {
body {
    background-color: rgb(25, 25, 25);
    color: rgb(170, 170, 170);
}

p {
    color: rgb(170, 170, 170);
}

#side-bar p {
    color: rgb(200, 10, 10);
}

div#container-wrap {
    background-image: none;
}

.content-panel .panel-body {
    background-color: rgb(70, 70, 70);
    background-size: 100% 180%;
    background-position: top center;
}

.international-entry:nth-child(2n),
.alternate:nth-child(2n) {
    background-color: transparent;
}

.news-block .news-title {
    color: inherit;
}

blockquote {
    background-color: rgb(70, 70, 70);
    color: rgb(170, 170, 170);
}

table.wiki-content-table th,
.scp-image-caption,
.scp-image-caption > p {
    background-color: rgb(70, 70, 70);
    color: rgb(170, 170, 170);
}

a {
    color: rgb(200, 20, 20);
    text-shadow: 0px 0px 3px rgba(0, 0, 0, .75);
}

h1 {
    color: rgb(200, 20, 20);
    text-shadow: 0px 0px 3px rgba(0, 0, 0, .75);
}

.content-panel {
    box-shadow: none;
}

.content-panel.standalone {
    background-color: rgb(60, 60, 60);
}

.content-panel .content-toc {
    background-color: rgb(50, 50, 50);
}

.side-block {
    background-color: rgb(70, 70, 70) !important;
}

.side-block a {
    color: rgb(170, 170, 170) !important;
}

#side-bar .side-block {
    background-color: rgb(60, 60, 60) !important;
}

#side-bar div.menu-item a {
    color: rgb(100, 10, 10);
}

#side-bar div.menu-item .sub-text {
    color: rgb(200, 200, 200);
}

.thread-container .post .head {
    background-color: rgb(90, 90, 90);
    background-image: none;
}

.thread-container .post .head a {
    color: rgb(100, 10, 10);
    font-weight: bold;
    text-shadow: none;
}

.forum-thread-box .description-block {
    background-color: rgb(70, 70, 70);
    box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.15), 0px 1px 0px rgba(200, 200, 200, 0.8) inset, 0px 10px 5px rgba(200, 200, 200, 0.25) inset, 0px -15px 30px rgba(0, 0, 0, 0.1) inset;
}

.forum-thread-box .description-block .statistics {
    color: rgb(200, 200, 200);
}

div.new-post a {
    background-color: rgb(70, 70, 70);
}

.yui-navset .yui-content {
    background-color: rgb(70, 70, 70);
}

.yui-content p span {
    color: rgb(10, 10, 10);
}
#top-bar a {
    color: #aaa;
}
#top-bar ul {
    background-color: rgb(50, 50, 50);
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background-color: rgb(50, 50, 50);
    color: #808080;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background-color: rgb(70, 70, 70);
}
.page-rate-widget-box .rate-points {
    background-color: #4e4e4e !important;
    border: solid 1px #4e4e4e;
    color: #aaa;
}
#search-top-box-form input[type=submit] {
    border: solid 1px #999;
    color: #ccc;
    background-color: #4e4e4e;
    background: #4e4e4e;
}
#search-top-box-input {
    border: solid 1px #999;
    color: #ccc;
    background-color: #4e4e4e;
    box-shadow: inset 1px 1px 3px rgba(0, 0, 0, .5);
}
#header h1 a {
    color: #4e4e4e;
}
#header h2 span {
    color: #990012;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
     background-color: #4e4e4e;
    border-top: solid 1px #4e4e4e;
    border-bottom: solid 1px #4e4e4e;
    color: #000;
}
.page-rate-widget-box .cancel {
    background-color: #4e4e4e;
    border: solid 1px #4e4e4e;
}
.page-rate-widget-box .cancel a {
    color: #000;
}
.scp-image-block .scp-image-caption {
    background-color: #464646;
}
element.style {
    border: dashed 1px #999999;
    background: #4a4a4a;
}
.page {
    background-color: transparent;
    background-image: linear-gradient(to top, rgb(97, 97, 97) 0%, rgb(97, 97, 97) 8%);
}
element.style {
    border: solid 1px #ff0000;
    background: #3c3c3c;
    padding: 5px;
    margin-bottom: 10px;
}
.heritage-rating-module {
    background-color: #4e4e4e;
    border: solid 1px #464646;

}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    background-color: #4e4e4e;
    border-top: solid 1px #4e4e4e;
    border-bottom: solid 1px #4e4e4e;
    font-weight: bold;
}
}
