@-moz-document regexp("https?:\\/\\/1peluchinga\\.com\\/[^\\/]+\\/(?:comentario\\/)?[\\d]+(?:\\/.+)?(?:\\?.+)?") {
label.required {
    margin: 10px 7px 10px 0px;
}
form[name="backendbundle_user"] label[for="backendbundle_user_text"] {
    margin-top: 10px;
}
.publication-item .avatar {
    margin-right: 10px;
    width: 60px;
    height: 60px;
    border-radius: 5px;
    box-shadow: 0 0 15px 0px #00376757;
}
.publication-item p:nth-child(1) {
    margin: initial;
}
.pull-right.like hr {
    display: none;
}
.publication-item {
    position: relative;
    /* padding-bottom: 0px; */
    /* margin-top: 15px; */
    padding-top: 15px;
    border-top: 1px solid #aaaaaa40;
    padding-bottom: 20px;
    /* border-bottom: 1px solid red; */
}
.pull-right.like {
    position: absolute;
    bottom: 4px;
    right: 8px;
    /* display: none; */
}
.comentarioPost {
    /* padding-bottom: 150px; */
}
.publication-item hr {
    margin-top: 35px;
    display: none;
}
a#likesdinamiclink {
    transform: translate(0px, 10px);
    display: inline-block;
    margin-right: 10px;
    /* margin-left: 0px; */
    COLOR: #5ca9c1;
    /* font-size: 18px; */
    /* font-weight: 600; */
    font-family: Roboto, Arial, sans-serif;
    line-height: 1.8rem;
    max-height: 3.6rem;
    overflow: hidden;
    font-size: 1.3rem;
    font-weight: 400;
    text-transform: none;
}
.publication-item .publication-item-cntent > a:nth-last-child(1) {
    position: absolute;
    left: 90px;
    /* display: none; */
    bottom: 15px;
    /* font-size: 12px; */
    /* opacity: 0.5; */
}
.publication-item .publication-item-cntent > a:nth-last-child(2) {
    position: absolute;
    left: 140px;
    /* display: none; */
    bottom: 15px;
    /* font-size: 12px; */
    /* opacity: 0.5; */
    /* margin-left: 70px; */
}
.publication-item a#likesdinamiclink span {
    padding: 0px;
}
.publication-item .publication-item-cntent > *:not(#likesdinamiclink) {
    margin-left: 70px;
}
.publication-item-conten > * {
    margin-left: 70px;
}
iframe, object {
    max-width: 100%;
}
.publication-item .pub-image {
    margin-bottom: 15px;
}
.col-lg-10.col-ld-offset-2.col-md-10.col-md-offset-2.col-sm-10.col-sm-offset-2.col-xs-10.col-xs-offset-2.publication-item {
    min-height: 90px;
    padding-bottom: 0px;
}
.pull-right.like span:nth-child(1) {
    /* background: #3dbb3f; */
    padding: 4px 7px 4px 8px;
    margin: 0px;
    border-radius: 5px 0px 0px 5px;
    transform: translate(4px, 0px);
    /* color: white; */
    border: 1px solid;
}
.pull-right.like span:nth-child(3) {
    /* background: #ff5353; */
    padding: 4px 7px 4px 7px;
    /* margin: 0px; */
    border-radius: 0px 5px 5px 0px;
    border: 1px solid;
}
.pull-right.like span:nth-child(1):hover {
    border: 1px solid #14b3e4;
    z-index: 100;
}
.pull-right.like span:nth-child(3):hover {
    border: 1px solid #14b3e4;
    z-index: 100;
}
.pull-right.like span::before {
    color: #5ca9c1;
    font-size: 14px;
}
.pull-right.like span:hover::before {
    color: #14b3e4;
    font-size: 14px;
}
.pull-right .active.btn-like {
    background: #ddffd2 !important;
    color: #3fb618 !important;
}
.pull-right span.active.btn-like::before {
    color: #3fb618 !important;
}
.pull-right .active.btn-unlike {
    background: #ffd2d2 !important;
    color: #ff0f0f !important;
}
.pull-right span.active.btn-unlike::before {
    color: #ff0f0f !important;
}
@media(max-width: 767px) {
    .publication-item {
        padding-top: 15px !important;
        padding-bottom: 20px !important;
        border-top: 1px solid #aaaaaa40;
    }
    .pull-right.like {
        right: 0px;
    }
    .publication-item .publication-item-cntent > *:not(#likesdinamiclink) {
        margin-left: 0px;
    }
    .publication-item-conten > * {
        margin-left: 0px;
    }
    .publication-item .publication-item-cntent > a:nth-last-child(2) {
        left: 50px;
    }
    .publication-item .publication-item-cntent > a:nth-last-child(1) {
        left: 0px;
    }
}
a#likesdinamiclink i::before {
    color: #5ca9c1;
    transform: translate(0px, 0px);
    display: block;
}
