style.css:
@-moz-document regexp("http(s)?://(|www.)?soundcloud.com/((?!jobs|settings/connections).*)") {
/*ANIMATIONS*/

@keyframes right-menu-load {
    0% {
        transform: translateX(0)
    }
    50% {
    	transform: translateX(-15px)                 
	}
    100% {
        transform: translateX(0)
    }
}


@keyframes scale-load {
    0% {
        transform: scale(0)
    }
    100% {
        transform: scale(1)
    }
}

@keyframes appear-load {
    0% {
        opacity: 0
    }
    100% {
        opacity: 1
    }
}

@keyframes appear-load-dialog {
    0% {
        opacity: 0
    }
    100% {
        opacity: .95
    }
}

@keyframes fly-load {
    0% {
        transform: translatey(200px);
    }
    100% {
        transform: translatey(0);
    }
}
}

@-moz-document regexp("http(s)?://(|www.)?soundcloud.com/((?!jobs|settings/connections).*)") {
/*INIT*/

/*elements*/
[aria-role=img], .image {
    max-width: 100% !important;
    height: auto !important;
}
[aria-role=img] {
    padding-top: 100%
}
input[type=text] {
    min-width: 200px;
}
[style^="background-image: url(\"https://i1.sndcdn.com/avatars-TrsBiiK6yDoZ8MWl-kmvjcw-t200x200.jpg\")"] {
    background-size: 25%;
    background-image: url("data:image/png;base64,") !important
}
/*grid*/
 .l-container {
    max-width: 100%;
}
.header__inner {
    display: flex;
    justify-content: space-around;
    flex-direction: row;
}
.g-flex-row-centered-spread {
    flex-wrap:wrap;
}
.g-flex-row-centered-spread>* {
    width: unset;
}
.announcements {
    z-index: 1000000;
    bottom: -50px;
}
header {
    z-index: 100000 !important;
}
/*PAGE*/

/*search page*/
 .searchTitle {
    padding: 15px 0;
}
/*user page*/
 .userInfoBar {
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap-reverse;
    padding: 10px 0;
}
.userInfoBar>* {
    position: unset;
    bottom: unset;
    top: unset;
    right: unset;
    left: unset;
}
.userInfoBar__tabs, .userInfoBar__tabs>* {
    width: 100%
}
.l-top.sc-border-box.g-z-index-fixed.sc-browsers-enable-gpu.l-inner-fullwidth {
	margin: 0 !important;
	left: 0;
    right: 0;
    width: auto;
}
.userNetworkInfo {
    left: unset;
    right: unset;
    top: unset;
    bottom: unset;
    width: 100%;
    max-width: unset;
    height: auto;
    max-height: unset;
}


.profileHeader{
        height: auto !important;
        padding: 0 !important;
    }
    .profileHeaderBackground
    {
        position: relative;
        display: flex;
		flex-direction: column;
        justify-content: center;
        background-color: transparent;
        inset: unset;
    }
    .profileHeaderBackground__visual {
		position: unset;
        background-position: center;
        background-size: contain;
        background-color: transparent;
		aspect-ratio: 9.5/2;
		box-shadow: currentColor 0px 0px 0px 0px;
        width: 100%;
    }

.profileHeader .profileHeaderInfo__avatar.sc-media-image {
    margin: 0;
    padding: 0;
    width: 16%;
    aspect-ratio: 1/1;
    position: relative;
}
.profileHeader .profileHeaderInfo__avatar.sc-media-image > span {
	position: absolute;
    inset: 0;
}
.profileHeaderInfo__content {
    display: flex;
    flex-direction: column;
    height: 100% !important;
    justify-content: center;
}
.profileHeaderInfo__content > * {
    display: block;
    position: relative;
    width: fit-content
}
.profileHeader .profileHeaderInfo.sc-media {
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: left;
    align-items: center;
    padding-left: 2.4%;
}
.profileHeader .profileHeaderInfo__content.sc-media-content {
    margin: 0;
    width: auto;
    height: auto;
    padding: 0 .5em;
}




.userInfoBar__buttons
{
    position: relative !important;
}

.l-user-hero
{
    padding: 40px 0 !important;
}

.profileHeader, .profileHeader__info, .profileHeaderInfo, .profileHeaderInfo__content {
    overflow: visible;
}

.l-user-hero {
    overflow: hidden;
    display: flex;
    flex-direction: column;
    justify-content: center;
    position: relative;
    padding: 0;
    margin-left: -20px;
    margin-right: -20px;
}

.profileHeaderInfo__content {
    height: auto !important;
}

.profileHeader__info
{
    position: unset;
}

.profileHeaderBackground {
	position: absolute;
    inset: 0;
    z-index: -1
}

.profileHeader {
	position: unset !important;
    margin: 0 !important;
}

.editImage {
	width: 100% !important
}


/* station */
 .stationHeader__title.sc-font-light {
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}
/*VISUAL*/
 .profileMenu, .dropbar__content, header, .l-top, .l-fixed-top, .playControls, .dialog {
    opacity: .95;
    z-index: 1000;
}
.backgroundGradient {
    filter: brightness(.9);
}
.dialog {
    border: none;
    animation: appear-load-dialog .4s;
}
/* G-Tabs */
 ul.g-tabs {
	display: flex;
	flex-wrap: wrap;
	justify-content: center;
}
ul.g-tabs::before, ul.g-tabs::after
{
    content: unset;
}
li.g-tabs-item {
    margin: 0 !important;
    padding: 0 1em;
    float: unset;
    display: inline-block;
}
/*TILES (BADGE) (COVER) (ITEM)*/
 .queue__itemWrapper {
    height: unset;
}
.tileGallery__sliderPanelSlide, .userBadge, .soundBadge, .badgeList__item, .sound__coverArt {
    transition: transform 100ms;
    transform: scale(1);
    padding: 2px
}
.tileGallery__sliderPanelSlide:hover, .userBadge:hover, .soundBadge:hover, .badgeList__item:hover, .sound__coverArt:hover {
    transform: scale(1.025);
}
.playableTile__playButton, .soundBadge__playButton, .soundBadge__actions {
    display: block;
    visibility: unset !important;
    transition: opacity 500ms;
    opacity: 0;
}
.playableTile__playButton:hover, .soundBadge__playButton:hover, .soundBadge:hover .soundBadge__actions, .soundBadge.active .soundBadge__actions {
    visibility: unset !important;
    opacity: 1;
}
/*COVER*/
 .playableTile__artwork, .sound__coverArt {
    box-shadow: inset 0 0 1px 0 currentColor;
    animation: appear-load 1s;
    overflow: hidden;
}
.playableTile__artwork .image, .sound__coverArt .image {
    visibility: hidden;
}
.playableTile__artwork .m-loaded, .sound__coverArt .m-loaded {
    visibility: visible;
    animation: appear-load .4s
}
/*BADGES*/
 .badgeList > ul {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
}
.badgeList__item:empty {
    display: none;
}
.badgeList__item {
    min-width: 115px;
}
.badgeList {
    margin: 0 !important;
}
.soundBadgeList, .badgeList, .user-badge {
    overflow: visible;
}
/* mini songs, stats */
 .trackItem__actions {
    position: absolute;
    margin: 0;
    right: 0;
}
.trackItem__actions .sc-button-group {
    display: flex;
}
.trackItem__actions .sc-button-group>* {
    margin: 0;
}
.hover .trackItem__playCount.sc-ministats {
    display: block !important;
}
.listenDetails__trackList {
    max-width: 90vw;
}
/*PLAY CONTROLS*/
 .playControls__bg {
    display: none !important;
}
.playControls__wrapper {
    min-width: 0 !important;
    max-width: 100% !important;
    width: 100% !important;
    margin: 0;
}
.playControls__inner {
    height: 100%;
    padding: 10px;
}
.playControls__elements {
    justify-content: space-around;
    margin: auto;
    max-width: 900px;
    width: 100%;
}
.playControls__control {
    height: unset;
}
.playControls__volume {
    z-index: 200;
}
.volume__sliderWrapper {
    border: none;
}
.playControls__queue {
    max-width: 100%;
    bottom: 55px
}
/*timeline*/
 .playControls__timeline {
    flex-grow: 0;
    margin-right: 0;
}
.playbackTimeline__duration::before {
    content:"-";
    margin: 0 4px;
}
.playbackTimeline__progressWrapper {
    position: fixed;
    width: 100%;
    left: 0;
    top: 0;
    padding: 0px;
    margin: 0;
    transition: transform 200ms;
    z-index: 100
}
.playbackTimeline__progressBar, .playbackTimeline__progressBackground {
    height: 5px;
    transition: height 200ms;
}
.playbackTimeline__progressHandle {
    visibility: hidden;
    border-radius: 5px;
    transition: height 200ms, width 200ms, margin-left 300ms;
}
*:hover > .playbackTimeline__progressBar, *:hover > .playbackTimeline__progressBackground {
    height: 25px;
}
*:hover > .playbackTimeline__progressHandle {
    visibility: visible;
    height: 35px;
    width: 20px;
    margin-left:-20px;
    border-radius: 0
}
.playbackTimeline__progressWrapper:hover {
    transform: translateY(-12.5px);
}
}

@-moz-document regexp("http(s)?://(|www.)?soundcloud.com/((?!jobs|settings/connections).*)") {
/** -MOBIL- **/

@media(max-width: 1080px) {
    /*INIT*/
    /*grid*/
    header {
        overflow: hidden;
        height: unset !important;
        max-height: 46px;
        transition: all .5s!important;
    }
    header:hover {
        max-height: 200px;
    }
    .header__inner {
        flex-direction: column;
    }
    .header__inner > *, .header__inner > * > * {
        display: flex !important;
        justify-content: center;
    }
    .header__navMenu > * {
        max-width: 33.333%;
    }
    .header__search, .headerSearch {
        width: 100%
    }
    .l-container.l-content {
        box-sizing: border-box;
        padding: 46px 10px;
        overflow: hidden;
        width: 100%;
    }
    /*LEFT RIGHT AREA*/
    /*main*/
    .l-main, .l-about-main {
        margin: 0 !important;
        transition: opacity .5s;
        opacity: 1;
    }
    *:hover>.l-main:not(:hover), *:hover>.l-about-main:not(:hover) {
        opacity: .1;
    }
    /*right*/
    .l-sidebar-right {
        margin-right: -320px !important;
        border-left: solid 1px currentColor;
        padding: 0 10px;
        min-height: 90vh;
        transition: margin .5s;
        animation: right-menu-load .5s cubic-bezier(0.21, -0.4, 0.77, 1.38);
        animation-delay: 3s
    }
    .l-sidebar-right:hover {
        margin-right: -10px !important
    }
    /*left*/
    .l-fixed-left {
        margin-left: -245px !important;
        border-right: solid 1px currentColor;
        padding: 0 10px;
        min-height: 90vh;
        transition: margin .5s;
        z-index: 10;
    }
    .l-fixed-left:hover {
        margin-left: -10px !important
    }
    /*top*/
    .l-fixed-top {
        top: -5px !important;
    }
    /*PAGE*/
    /*playlist edit*/
    .baseFields {
        display: flex;
        flex-wrap: wrap;
        justify-content: space-around;
        padding: unset;
        max-width: 600px;
        margin: auto;
    }
    .baseFields>* {
        position: relative;
        width: 100%;
    }
    .baseFields__image {
        width: auto;
    }
    .editPlaylistBasicTab__fields {
        margin: 0;
    }
    /*user page*/
    .userInfoBar {
        justify-content: space-around;
    }
    .profileHeaderInfo__content > * {
        margin: 0
                                                                                
    }
    .profileHeaderInfo__content > br {
        display: none
    }
    
    .l-user-hero {
    	overflow: visible;
	}
        
    .profileHeader .featuredProfiles {
    z-index: 99;
    transform: translateY(0px);
    height: auto !important;
    top: calc(100% - 35px);
    bottom: unset;
}

.profileHeader .featuredProfiles .featuredProfiles__scroll {
	position: relative;
    top: unset !important;
    bottom: unset !important;
}

.profileHeader .featuredProfiles .featuredProfiles__wrapper {
	height: auto !important;
}

.profileHeader .featuredProfiles:not(:hover) .featuredProfiles__title {
	margin-bottom: 0;
}

.profileHeader .featuredProfiles:not(:hover) .featuredProfiles__list {
    display: none;
}

    /*song page up*/
    .fullListenHero {
        height: unset !important;
        display: grid;
        justify-items: center;
    }
    
    .fullListenHero > [style="height: 100%;"]
    {
        position: absolute;
        inset: 0;
    }
    
    .fullListenHero .fullHero__foreground
    {
        width: 100%;
        display: grid;
        grid-template-columns: repeat(auto-fill, 1fr);
        justify-items: center;
        gap: 1em;
        padding: 2em;
        position: relative;
        inset: unset;
    }
    .fullListenHero .fullHero__foreground > *
    {
        position: relative;
        inset: unset;
        max-width: 100%;
    }
    .fullListenHero .fullHero__foreground > .fullHero__playerArea
    {
        width: 100%
    }
    .fullListenHero .fullHero__foreground > .fullHero__info
    {
        max-width: 100%;
        display: grid;
        grid-template-columns: repeat(auto-fill, 1fr);
        justify-items: center;
        
    }
    .fullListenHero .fullHero__foreground > .fullHero__title
    {
        max-width: 100%;
        display: grid;
        grid-template-columns: repeat(auto-fill, 1fr);
        justify-items: center;
        
    }
    /*song page down*/
    .listenEngagement {
        padding: 10px !important;
        width: 100% !important;
    }
    .l-about-rows {
        padding: 0 !important;
    }
    .l-about-row {
        display: flex;
        flex-wrap: wrap;
    }
    .l-about-row>* {
        display: flex;
        justify-content: space-around;
        position: relative !important;
        padding: 0 !important;
        width: 100% !important;
    }
    /*playlist page */
    .l-hero-content {
        padding-right: 0;
    }
    /*VISUAL*/
    .backgroundGradient {
        position: absolute;
        width: 100%;
        z-index: 0;
        top: 0;
        bottom: 0;
    }
    /*PLAY CONTROLS*/
    .playControls {
        visibility: visible;
    }
    .playControls__elements {
        flex-wrap: wrap-reverse;
        min-width: 340px;
        max-width: 550px;
    }
    .playControls__soundBadge > * {
        width:100%;
    }
    .playControls__soundBadge {
        width: 100%;
    }
    .playControls__queue {
        bottom: 100px;
        right: 0;
        max-height: 75vh;
        max-width: 100vw;
    }
    .playControls__queue * {
        max-width: 100%;
        max-height: 100%;
    }
}
}

@-moz-document regexp("http(s)?://(|www.)?soundcloud.com/((?!jobs|settings/connections).*)") {
::-webkit-scrollbar {
    width: 7px;
    height: 7px

}

::-webkit-scrollbar-track {
    background: #222;
}

::-webkit-scrollbar-thumb {
    background: #555;
}

::-webkit-scrollbar-thumb:hover {
    background: #666;
}
}

@-moz-document regexp("http(s)?://(|www.)?soundcloud.com/((?!jobs|settings/connections).*)") {
/* patches */

.soundList .visualSound__wrapper {
    margin: 0 !important;
}

.soundList .visualSound__wrapper .visuals > *
{
    left: 5px;
    bottom: 35px;
}
}
