style.css:
@-moz-document domain("divar.ir") {
body {
 background: #262626;
}
.navbar {
 background: #1a1a1a !important;
 border-bottom: 1px solid #757575 !important;
}
.ui.vertical.menu {
 background-color: #1a1a1a !important;
 border: 1px solid #757575 !important;
}
.ui.menu .item {
 color: #8c8c8c;
}
.browse .filter-box {
 background-color: #1a1a1a !important;
 border: 1px solid #757575 !important;
}
.ui.vertical.menu>.active.item:first-child {
 background: hsla(210, 2%, 36%, 1);
 color: black;
}
.browse .categories-sidebar .ui.vertical.pointing.menu {
 border: auto;
}
.ui.card, .ui.cards>.card {
 background: hsla(0, 0%, 10%, 1);
 padding: 0 !important;
}
.ui[class*="internally celled"].grid>.row:first-child, .ui[class*="internally celled"].grid>.row>.column:first-child {
 color: #dedede;
}
.ui.celled.grid>.row {
 box-shadow: 0 -1px 0 0 #5a5c5e;
}
.ui.celled.grid>.column:not(.row), .ui.celled.grid>.row>.column {
 box-shadow: -1px 0 0 0 transparent;
}
.ui.card>:only-child, .ui.cards>.card>:only-child {
 border-radius: 10px!important;
 padding: 10px;
}
* {
 color: #b3b3b3 !important;
}
.ui.secondary.menu .item button {
 background: #404040 !important;
 color: white !important;
}
.ReactModalPortal .divar-modal {
 background: #333;
}
.browse .filter-box .on-off-field .checkbox label:before {
 background-color: #454545!important;
}
.browse .filter-box .on-off-field .checkbox label:after {
 border: 0px!important;
 background: #1a1a1a;
}
.city-home .cards .ui.card.city .button, .city-modal .cards .ui.card.city .button {
 background-color: transparent!important;
 border: 1px solid #000000!important;
 color: #767676!important;
}
[type=text] {
 background: #454545 !important;
}
.ui.compact.menu {
 background: #454545;
}
.browse .filter-box .fields .field-container .ui.dropdown, .browse .filter-box .fields .field-container .ui.input, .browse .filter-box .fields .field-container .ui.search {
 background: #454545;
}
.ui.selection.active.dropdown, .ui.selection.active.dropdown .menu {
 border-color: #757575;
 background: #262626;
}
.ui.selection.dropdown .menu>.item {
 border-top: 0;
}
.ui.dropdown .menu>.item:hover {
 background: #1a1a1a;
}
.ui.menu .dropdown.item .menu {
 background: #262626;
}
.ui.menu .ui.dropdown .menu>.item:hover, .ui.menu .ui.dropdown .menu>.selected.item {
 background: rgb(26, 26, 26)!important;
}
.filter-option-container>div {
 background: transparent !important;
}
.filter-option-container>div div {
 padding: 0 !important;
}
.ui.icon.button, .ui.icon.buttons .button {
 background: #5a5c5e;
}
i.icon.content:before {
 color: #efefef;
}
button.ui.active.icon.button {
 background: #8d8f91;
}
button.ui.active.icon.button i:before {
 color: #454545;
}
.post-card .no-picture-thumbnail-image {
 height: 100%;
 width: 100%;
 filter: invert(1);
}
.ui.link.menu .item:hover, .ui.menu .dropdown.item:hover, .ui.menu .link.item:hover, .ui.menu a.item:hover {
 background: #ffffff14;
}
.ui.vertical.menu .active.item, .ui.vertical.menu .active.item:hover {
 background: #7676767a;
}
.post-page .right {
 background: #1a1a1a;
}
.report-post {
 color: #b3b3b3 !important;
}
.post-page .breadcrumb-container {
 background-color: #1a1a1a!important;
 border: 1px solid #989898!important;
 color: #b3b3b3 !important;
 font-size: 14px;
 height: 44px!important;
 margin: 0!important;
}
.post-page .breadcrumb-container .content a {
 color: #b3b3b3 !important;
}
.post-page .breadcrumb-container .content button.back-button:hover {
 background-color: #4e4e4e!important;
}
.post-page .post-actions button.start-chat, .post-page .post-actions button.bookmark {
 background-color: #3d3d3d!important;
}
.post-page .post-fields .item {
 border-bottom: 1px solid #3d3d3d;
}
.post-page .post-guidelines-container .post-guidelines .report-post {
 color: #989898!important;
}
.ui.attached:not(.message)+.ui.attached.segment:not(.top) {
 background: transparent;
}
.category-dropdown__menu {
 background-color: #3d3d3d;
 border: 1px solid #616161;
}
.css-yk16xz-control {
 background-color: hsl(0, 0%, 24%);
 border-color: hsl(0, 0%, 38%);
}
.search-bar .search-bar-box {
 border: 1px solid #616161;
}
[type=text] {
 background: #3d3d3d !important;
}
.ui.button {
 background: #404040 none;
}
.ui.button:hover {
 background-color: #1a1a1a;
}
.ui.teal.button, .ui.teal.buttons .button, .ui.teal.button:hover, .ui.teal.buttons .button:hover {
 background-color: #c10c1a;
}
.post-card__image {
 background-image: url("data:image/jpg;base64,");
}
.post-card {
 border: 1px solid #262626;
 background: #1a1a1a;
}
.chip {
 background-color: #1a1a1a;
 border: 1px solid #c10c1a;
}
.image-slider img {
 border: 2px solid #262626;
}
.ui.basic.button, .ui.basic.buttons .button {
 background: #1a1a1a !important;
 color: #949494 !important;
}
.ui.basic.secondary.button, .ui.basic.secondary.buttons .button {
 color: inherit !important;
}
.ui.basic.secondary.button:hover, .ui.basic.secondary.buttons .button:hover {
 background: #404040 !important;
}
.map-container {
 filter: invert(.8) contrast(1.5) hue-rotate(120deg);
}
.breadcrumb-card {
 background-color: #1a1a1a!important;
 border: 1px solid #404040!important;
}
.breadcrumb-card a {
 color: #b3b3b3!important;
}
.breadcrumb-card button.back-button {
 border-left: 1px solid #404040!important;
}
.breadcrumb-card button.back-button:hover {
 background-color: #404040!important;
}
.browse__main {
 background-color: #262626;
}
.kt-post-card--outlined {
 background: #1a1a1a;
}
.kt-post-card__image-wrapper {
 background: #1a1a1a;
}
  .kt-post-card--outlined:after {
    box-shadow: inset 0 0 0 1px rgba(0,0,0,.08), inset 0 0 0 1px #040404;
}
  
}
