style.css:
@-moz-document url-prefix("https://www.youtube.com") {
:root{
	--bgr-theme-color: #1a1a1a;
	--logo-color:#878787;
	--narrow-color:#15b700;
	--subscript-color:#444444;
	--subscript-background-color:#232323;
	--text-color:#808080;
	--color-text-inside-button:#d1d1d1;
	--bgr-theme-color-lighter: #333333;
        --author-comment-badge-name-color: #383838;
        --icon-like: #254977;
        --time-color: #e0e0e0;
        --scroll-color: #3a3a3a;
        --scroll-track-color: #262626;
        --play-progress: #821d1d;


        --yt-menu-hover-backgound-color: #282828 !important;
        --yt-guide-entry-hover-background-color: #141414 !important;

}
ytd-app, #content, #content #page-manager, #content #page-manager ytd-watch.style-scope.ytd-page-manager, #content #page-manager  ytd-browse.style-scope.ytd-page-manager, app-toolbar #tabs-container #tabs-inner-container, #channel-container #channel-header, ytd-multi-page-menu-renderer, #page-manager ytd-browse.style-scope.ytd-page-manager ytd-playlist-sidebar-renderer.style-scope.ytd-browse, #page-manager ytd-section-list-renderer#primary #contents ytd-item-section-renderer.style-scope.ytd-section-list-renderer #contents, #page-manager ytd-browse #alerts, #page-manager ytd-section-list-renderer#primary #contents, ytd-watch-flexy, ytd-section-list-renderer#primary, ytd-playlist-video-renderer, div#secondary, ytd-video-renderer.ytd-backstage-post-renderer, h3.ytd-miniplayer, #card.style-scope.ytd-miniplayer{
	background:var(--bgr-theme-color) !important;
	color:var(--text-color) !important;
}
ytd-multi-page-menu-renderer{border:1px solid gray;}
#content #page-manager ytd-watch.style-scope.ytd-page-manager *{
	color:var(--text-color) !important;
}
body *, a:link, a:visited{
	color:var(--text-color) !important;
}
a:hover{
	color:var(--text-color) !important;
}
#masthead-container{
	background-color: transparent !important;
	background:none !important;
}

ytd-masthead#masthead{
border-bottom:1px solid #262525;
background:linear-gradient(var(--bgr-theme-color), var(--bgr-theme-color));
}
ytd-searchbox#search *{
	background:transparent !important;
}
ytd-searchbox#search input{
	color:white !important;
}
#guide #guide-content, #columns, ytd-browse{
	background:var(--bgr-theme-color) !important;
	color:var(--text-color) !important;
;
}
#guide #guide-content *{
	color:var(--text-color) !important;
}
#guide #guide-content{
	margin-right:-18px !important;
}
#right-arrow ytd-button-renderer, #left-arrow ytd-button-renderer{background:var(--narrow-color) !important;}
#right-arrow ytd-button-renderer svg, #left-arrow ytd-button-renderer svg{fill:var(--color-text-inside-button) !important;}
paper-button{background:var(--subscript-color) !important;}
ytd-comments#comments paper-button, #meta paper-button{background:none !important;}
ytd-comments#comments paper-button *, #meta paper-button *{color:var(--color-text-inside-button) !important;}
#guide-button #button{filter:contrast(10%);}
#contents.ytd-section-list-renderer > .ytd-section-list-renderer:not(:last-child):not(ytd-page-introduction-renderer), #sections.ytd-guide-renderer > .ytd-guide-renderer:not(:last-child){
	border-bottom: 1px solid rgba(255,255,255,.1) !important;
}
#playlist #container .header.style-scope.ytd-playlist-panel-renderer{background: var(--bgr-theme-color) !important;}
#playlist #container #items{background:var(--bgr-theme-color) !important;}
#content #page-manager ytd-watch.style-scope.ytd-page-manager *{color:var(--text-color) !important;}
ytd-video-secondary-info-renderer, ytd-video-primary-info-renderer, #placeholder-area.ytd-comment-simplebox-renderer {border-bottom:rgba(255,255,255,.1) !important;}
#logo svg g g path{fill:var(--logo-color) !important;}
ytd-simple-menu-header-renderer, ytd-active-account-header-renderer{background-color:#343434 !important;}
ytd-multi-page-menu-renderer{overflow-x:hidden !important;}
ytd-multi-page-menu-renderer ytd-account-settings{background:var(--bgr-theme-color) !important;}
ytd-multi-page-menu-renderer ytd-account-settings .ytd-account-settings::-webkit-scrollbar{width:4px; background-color:#000c00;}
paper-listbox#items{background:var(--bgr-theme-color) !important;border:solid gray 1px !important;}
paper-listbox#items ytd-menu-service-item-renderer:hover{background:var(--bgr-theme-color-lighter) !important;}
form#search-form div#container.style-scope.ytd-searchbox{box-shadow:none !important;}
div.gstl_50.sbdd_a{filter:invert(85%);}
div.gstl_50.sbdd_a *{color:black !important;}







paper-button yt-formatted-string, paper-button yt-formatted-string *{color: var(--color-text-inside-button) !important;}
ytd-author-comment-badge-renderer *{color: var(--author-comment-badge-name-color) !important;}
ytd-toggle-button-renderer.style-default-active *{color: var(--icon-like) !important;}
#toggleButton, .yt-alert-renderer, .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox,
#checkbox.checked {
    background-color: var(--icon-like) !important;
}
#checkbox.checked {border-color: var(--icon-like) !important;}
ytd-subscribe-button-renderer *,#sponsor-button *,#subscribe-button *, .yt-next-continuation, .ytd-notification-multi-action-renderer *
,paper-button#button, paper-button.ytd-button-renderer *{
    background-color: var(--subscript-background-color) !important; border-radius: 5px !important;
}
paper-button#toggle.ytd-grid-renderer,#play-all *,#play-button *, paper-button#more *, yt-dropdown-menu *, .ytd-playlist-sidebar-secondary-info-renderer *, .ytd-rich-metadata-row-renderer, .ytd-popup-container, .yt-horizontal-list-renderer *, #items-stamp{
    background-color: var(--bgr-theme-color) !important;
}
ytd-button-renderer.style-suggestive {border: none !important;}
yt-img-shadow *{border-radius: 10px !important;}
.ytp-time-display *{color: var(--time-color) !important;}
#selectionBar {border-color: var(--text-color) !important;}
::-webkit-scrollbar {width: 0.3em !important;}
::-webkit-scrollbar-thumb {background: var(--scroll-color) !important; border-radius: 10px !important;}
::-webkit-scrollbar-track {background-color: var(--scroll-track-color) !important;}
.ytp-swatch-background-color, #progress.ytd-thumbnail-overlay-resume-playback-renderer {
    background-color: var(--play-progress) !important;
}
ytd-compact-autoplay-renderer {border-bottom: 1px solid var(--subscript-color) !important;}
.badge-style-type-live-now {
    background-color: var(--subscript-background-color) !important; 
    border-color: var(--play-progress) !important;
}
.badge-style-type-simple{background-color: var(--subscript-color) !important;}
.ytp-settings-button.ytp-hd-quality-badge:after, .ytp-settings-button.ytp-4k-quality-badge:after, .ytp-settings-button.ytp-5k-quality-badge:after, .ytp-settings-button.ytp-8k-quality-badge:after {
  background-color:  var(--play-progress) !important;
}
}
