style.css:
@-moz-document domain(motamem.org) {
body {
    background: #333;
    color: #e8e8e8;
}
p {
    color: #e8e8e8;
}
input {
    padding: 6px;
    border-radius: 3px;
    background: #222;
    border: 1px solid #333;
    margin-right: 0 !important;
}
#wrapper {
    background: unset;
}
#container, #footer, #footer-widgets .clearfix, #header, #main, #main-fullwidth, #main-index {
    background: #333;
}
.box-index {
    background-color: #2d2d2d;
    margin-left: 0;
}
.box-index .sue-panel {
    background-color: #2d2d2d;
    box-shadow: unset;
    border-color: #3d3d3d;
    color: #fff;
}
.post-wrap {
    margin: 8px 0;
    background: #2d2d2d;
    border-radius: 4px;
}
.post-wrap span {
    color: #babb48 !important;
}
.entry-title a {
    color: #00c341;
}
.image-container {
    border-color: #3d3d3d;
}
#sidebar-primary {
    width: 320px;
    padding: 5px 15px;
    background: #2d2d2d;
    border-radius: 4px;
}
.widgettitle {
    color: #e8e8e8 !important;
    background: #2d2d2d !important;
    box-shadow: unset;
    border-top: 2px solid #3d3d3d;
    border-bottom: 2px solid #3d3d3d;
    padding: 9px 0 9px 0;
}
.widget-container a, .widget-container a:hover {
    color: #e8e8e8;
}
.textwidget a {
    background-color: #333 !important;
    border-color: #333 !important;
    border-radius: 4px !important;
}
.textwidget label {
    color: #ccc;
}
.textwidget a span {
    border-radius: 4px !important;
    color: #093 !important;
    border-color: #3d3d3d !important;
}
.nd_logged_in ul.links li {
    float: right !important;
}
.nd_logged_in ul.links li a {
    background: #009933 !important;
    color: #fff !important;
}
#black-studio-tinymce-7 span {
    color: #818d92;
}
#black-studio-tinymce-7 span a {
	color: #093!important;
}
.su-button-style-soft span {
    background: unset !important;
}
.su-button-style-soft:hover span {
    background: unset !important;
}
.widget ul li {
    color: #999;
}
.ubermenu-main {
    border: 1px solid #333;
    background: #2d2d2d;
}
.ubermenu-skin-black-white-2 .ubermenu-submenu.ubermenu-submenu-drop {
    background: #272727;
    border-color: #3d3d3d;
}
.ubermenu-main .ubermenu-item-normal > .ubermenu-target {
	color: #ccc;
}
/***********************
* pages
***********************/
.single a {
	color: #009933;
}
.single a:focus, .single a:hover {
    color: #05b13e;
}
.post h2,
.post h3 {
    color: #e8e8e8;
}
.post li{
    color: #093;
}
.featured_image {
    border: unset;
    background: #3d3d3d;
}
.wp-caption {
    background: #2d2d2d;
    border: 1px solid #2d2d2d;
}
.su-note {
    border: unset !important;
    border-radius: 4px !important;
}
.su-note-inner {
    background-color: #2d2d2d !important;
    border: none !important;
}
.sue-panel {
    background: #2d2d2d !important;
    box-shadow: unset !important;
    border: unset !important;
    border-radius: 4px !important;
    color: #fff !important;
}
.seriesbox {
    background-color: #2d2d2d !important;
    border: none !important;
    border-radius: 4px !important; 
}
.seriesbox li.serieslist-li:before {
	color: #c5c5c5;
    background: #4c4c4c;
}
.seriesbox li.serieslist-li:after {
	background-color: #4c4c4c;
}
.su-box {
	border: none !important;
}
.su-box-title {
    background-color: #ff0022 !important;
    color: #e8e8e8 !important;
}
.su-box-content {
    background-color: #2d2d2d !important;
}
.seriescomment {
	background: #2d2d2d;
    border: 1px solid #2d2d2d;
}
.commentlist li.comment {
	background: #2d2d2d;
    border-radius: 4px;
    overflow: hidden;
}
.comment-author {
    border-radius: 0;
    background: #2d2d2d;
    border: unset;
    border-bottom: 1px solid #3d3d3d;
    padding: 5px 15px;
}
.comment-meta {
	padding: 0 15px;
}
#comments p {
	padding: 0 15px;
}
#wp-comment-editor-container {
	border-color: #272727!important;
}
div.mce-panel {
    background: #272727!important;
}
div.mce-toolbar-grp {
	border-bottom: 1px solid #272727!important;
    background: #272727!important;
}
div.mce-statusbar {
	border-color: #272727 !important;
}
.fn, .fn a:hover, .fn a:link, .fn a:visited {
	color: #009933!important;
}
/******************
* wpcm
******************/
a.wpcm-wrapper-link {
	background-color: #093;
    color: #fff !important;
}
.wpcm-overlay-wrapper {
	background: #333;
}
.wpcm-overlay-wrapper p {
	color: #e8e8e8;
}
.wpcm-overlay-wrapper h2 {
	color: #e8e8e8;
}
.wpcm-overlay-wrapper .content00 li {
	color: #093;
}
.wpcm-overlay-wrapper .wpcm_post_thumnail img {
	background: #3d3d3d;
    border-color: #3d3d3d;
}
}
