style.css:
@-moz-document url-prefix("https://finobe.com") {
.nav-scroller.navbar-light.bg-faded {
    background: #7474bf;
    background: linear-gradient(90deg, #4c5054, #4c5054);
    box-shadow: 0 0 0 1px black;
    height: 45px;
    width: 1111px;
    left: 400px;
}

.navbar-light.bg-faded {
    background: #7474bf;
    background: linear-gradient(90deg, #000000, #000000);
    border-bottom: 0;
    width: 1111px;
    left: 400px;
    box-shadow: 0 0 0 1px black;
}

body {
    background-repeat: round;
    -webkit-font-smoothing: antialiased;
    background-image: url(https://cdn.discordapp.com/attachments/559417404522758178/589870342026035226/1cc0cddc2eadf3b8dfa1f960372669e8.png);
    background-color: #585858;
    font-family: Comic Sans Ms;
    background-size: cover;
    background-repeat: space;
}

.mb-4,
.my-4 {
    margin-bottom: 1.5rem!important;
    font-family: Comic Sans Ms;
}

.catalog-card > .card {
    border-radius: .2rem;
    box-shadow: none;
    transition: all .3s cubic-bezier(.25, .8, .25, 1);
    box-shadow: 0 0 0px 1px black;
}

.footer > .container {
    border: 1px solid #444;
    border-radius: 0;
    padding: 20px 0 20px 20px;
    background: #303030;
    border: 1px solid #e6e6e6;
    background: #fff;
    box-shadow: 0 0 0 1px black;
}

.badge {
    font-size: 80%;
    box-shadow: 0 0 0 1px black;
    border-radius: inherit;
}

.nav-pills .nav-link {
    border-radius: .0rem;
    background-color: white;
    color: black;
}

.card-img-top {
    /* width: 100%; */
}

.game-card > .data {
    display: inline-block;
    padding: .65rem;
    width: 100%;
    box-shadow: 0 0 0 1.2px black;
}

.nav-scroller-inner > a.nav-link,
.navbar-light .navbar-nav .active > .nav-link,
.navbar-light .navbar-nav .nav-link,
.navbar-light .navbar-nav .nav-link.active,
.navbar-light .navbar-nav .nav-link.show,
.navbar-light .navbar-nav .show > .nav-link {
    color: hsl(0, 0%, 100%);
}

.game-card > .data {
    display: inline-block;
    padding: .65rem;
    width: 100%;
    box-shadow: 0 0 0 1.2px white;
    background-color: black;
    color: white;
}

.card-body {
    flex: 1 1 auto;
    padding: 1.25rem;
    background-color: black;
    color: white;
}

.catalog-card-name {
    color: #fff;
    font-size: 16px;
}

.card-header:first-child {
    border-radius: calc(.25rem - 1px) calc(.25rem - 1px) 0 0;
    background-color: #3f4042;
    color: white;
}

.text-center {
    text-align: center!important;
    background-color: black;
}

nav.finobe-basic-nav,
nav.settings-nav {
    width: 100%;
    height: auto;
    box-shadow: 0 0 0 3px black;
    background-color: white;
}

.table {
    width: 100%;
    margin-bottom: 1rem;
    color: #fefeff;
    background-color: transparent;
}

.mb-2, .my-2 {
    margin-bottom: .5rem!important;
    color: white;
}

.font-light {
    font-weight: 300;
    color: white;
}

.row.flex-override .vertical-align-center .valign-center {
    align-self: center;
    color: white;
}

}
