style.css:
    /*SNOWCONE THEME by tartmart*/

    @-moz-document domain("animaljam.com") {
        /*[[bg]]*/
        /*links color, copyright color*/
        .gamepage .footer ul.footer-links a:link,
        .gamepage .footer ul.footer-links a:visited,
        .gamepage .footer p.copyright {
            color: rgba(255, 255, 255, 0.7);
        }
        /*line break/footer line*/
        .gamepage .footer ul.footer-links {
            color: #ffffff;
            border-bottom: 1px solid #ffffff;
        }
        /*link spacers*/
        ul.footer-links > li.spacer {
            color: #ffffff;
        }
        /*[[color]]*/
    }
    /*code by shiibeh/tartmart*/

