/* ==UserStyle==
@name         Money Dashboard Dark
@namespace    github.com/DaemonExMachina/userstyles
@description  Staying on top of your money is easy... *on the eyes*
@version      1.2.1
@author       DaemonExMachina <dev@daemonexmachina.com>
@license      CC-BY-SA-4.0
==/UserStyle== */

@-moz-document domain("www.moneydashboard.com") {
  /* Hero screen */

  .hero,
  .main-section,
  .main-section.grey {
    background-color : var(--bg-1);
  }

  .header-container,
  .signup-reminder {
    background-color : var(--bg-2);
  }

  .media-carousel,
  .perk, .value,
  .featured-testimonial-col.featured-content,
  .featured-testimonial-col.featured-testimonial-content {
    background-color : var(--bg-3);
  }

  .hero,
  .title-2,
  .title-4,
  .section-title,
  .backup-title,
  .footer-link:hover,
  .footer-title,
  .copyright-link {
    color : var(--text-1);
  }

  .hero-title-second,
  .app-download-text,
  .body, .navbar-link,
  .media-carousel-title,
  .section-subtitle.text-center,
  .backup-subtitle,
  .footer-link,
  .body-small {
    color : var(--text-2);
  }

  img[src='https://assets.website-files.com/5b4c81d9d95543fc4f1cc63a/5b4cb910d95543bc4a1d0027_mdb_horizontal_dark.svg'],
  img[src='https://assets.website-files.com/5b4c81d9d95543fc4f1cc63a/5b4cc4e668e07c34265e8596_mdb_icon_dark.svg'],
  .media-link img {
    filter : invert(1);
  }

  .value img {
    filter : invert(.75) hue-rotate(170deg) saturate(2);
  }

  .goal-button.goal-1 {
    color            : #e1f5fe;
    background-color : #1f688a;
  }

  .goal-button.goal-2 {
    color            : #e0f2f1;
    background-color : #1d5a57;
  }

  .goal-button.goal-3 {
    color            : #f3e5f5;
    background-color : #66336d;
  }

  .goal-button.goal-4 {
    color            : #fff3e0;
    background-color : #745e3b;
  }

  .goal-button.goal-5 {
    color            : #ffd8d7;
    background-color : #753533;
  }

  .goal-button.goal-1:hover,
  .goal-button.goal-2:hover,
  .goal-button.goal-3:hover,
  .goal-button.goal-4:hover,
  .goal-button.goal-5:hover {
    background-image      : linear-gradient(rgba(1, 1, 1, .5), rgba(1, 1, 1, .5));
    background-blend-mode : multiply;
  }

  .text-field,
  .text-field:focus {
    color            : var(--text-1);
    background-color : var(--bg-1);
    border-color     : var(--bg-3);
  }

  .text-field::placeholder {
    color : var(--text-3);
  }
}

@-moz-document domain("my.moneydashboard.com") {
  :root {
    --bg-1       : #222;
    --bg-2       : #333;
    --bg-3       : #444;
    --text-1     : #fff;
    --text-2     : #ccc;
    --text-3     : #999;
    --text-red   : #ef5350;
    --text-green : #4caf50;
  }

  html, body,
  *:not(form) > md-content.md-default-theme,
  *:not(form) > md-content {
    color            : var(--text-1);
    background-color : var(--bg-1);
  }

  md-content.md-default-theme,
  md-content,
  .page-header > .content,
  .page-header h1 {
    color : var(--text-2);
  }

  .card,
  .cardContainer {
    background-color : var(--bg-2);
  }

  md-icon,
  .button md-icon,
  .button md-icon > svg,
  .button.icon md-icon > svg,
  md-icon.button.icon,
  .page-header > .content .help-icon,
  .button img {
    fill : var(--text-3);
  }

  /****************************************************************************************/

  /* Login screen */

  .login-page__sidebar {
    color            : var(--text-1);
    background-color : var(--bg-1);
  }

  .login-page__content .subtitle {
    color : var(--text-1);
  }

  .login-page__content .inputs md-checkbox,
  .login-page__content .inputs .passwordHint,
  .regulationStatement,
  .password-creation-hints,
  .password-creation-hints__description {
    color : var(--text-3);
  }

  .login-page__content .inputs md-input-container .eye {
    filter : invert(1);
  }

  .password-creation-hints__criteria md-icon {
    fill : var(--text-3);
  }

  md-checkbox.md-default-theme:not(.md-checked) .md-icon,
  md-checkbox:not(.md-checked) .md-icon,
  .password-creation-hints {
    border-color : var(--text-3);
  }

  .cookie-control-dialog .title,
  .cookie-control-dialog .body-text,
  .cookie-control-dialog .switch-text,
  .cookie-control-dialog .section-header {
    color : var(--text-1);
  }

  /****************************************************************************************/

  /* Overview (sidebar) */

  #overview,
  #overview .category-container {
    background-color : var(--bg-1);
  }

  #overview .summary-container .net-balance .title,
  #overview .category-container .category-summary .category-title,
  #overview .category-container .account-container .account-content .account-name {
    color : var(--text-1);
  }

  .companyFooter,
  #overview .summary-container .net-balance .subtitle,
  #overview .category-container .category-summary .category-totals,
  #overview .category-container .account-container .account-content .institution-container .institution-name,
  #overview .category-container .account-container .account-content .last-refreshed {
    color : var(--text-3);
  }

  #overview .summary-container .net-balance,
  #overview .category-container .account-card {
    background-color : var(--bg-2);
    border           : none;
  }

  #overview .summary-container .row .card.negative-balance {
    background-color : var(--text-red);
  }

  #overview .category-container .account-container .account-content .balance {
    color : var(--text-red);
  }

  #overview img.hide-category,
  #overview img.refresh-button.visible,
  #overview img.include-in-calculation,
  #overview img.quick-menu {
    filter : invert(1);
  }

  /****************************************************************************************/

  /* Navbar */

  body .nav,
  #navbar {
    color            : var(--text-1);
    background-color : var(--bg-2);
  }

  body .nav .logo,
  #navbar .logo img,
  #navbar .item .submenu md-icon:not([md-svg-src='https://media.moneydashboard.com/material-icons/ic_android_24px.svg']):not([md-svg-src='https://media.moneydashboard.com/material-icons/ic_apple_24px.svg']) {
    filter : invert(1);
  }

  #navbar .logo:after {
    background-color : var(--text-3);
  }

  md-icon.md-default-theme, md-icon {
    color : var(--text-1);
  }

  #navbar .item .submenu {
    color            : var(--text-1);
    background-color : var(--bg-3);
  }

  account-submenu .profile,
  #navbar .item .submenu .subitem.divider {
    border-color : var(--text-3);
  }

  /****************************************************************************************/

  /* Dashboard */

  .grid > .item .item-content .widget-title {
    color : var(--text-2);
  }

  .widget {
    background-color : var(--bg-2);
  }

  .widget__empty {
    background : var(--bg-2);
  }

  .widget .widget-header {
    color            : var(--text-2);
    background-color : var(--bg-3);
    border-color     : var(--bg-3);
  }

  .widget-header__grab-icon {
    fill : var(--text-3);
  }

  .plannerWidget transaction-list .transaction,
  .transactionWidget transaction-list .transaction,
  .transaction-profiles-widget transaction-list .transaction,
  .outgoingsWidget transaction-list .transaction,
  .plannerWidget .transaction-list .transaction,
  .transactionWidget .transaction-list .transaction,
  .transaction-profiles-widget .transaction-list .transaction,
  .outgoingsWidget .transaction-list .transaction,
  .outgoingsByGroup .chartContainer .totals h1,
  .outgoingsByGroup .legend .item .textContainer h3,
  .transaction-profiles-widget .emptyState {
    color : var(--text-1);
  }

  .transaction-profiles-widget,
  .plannerWidget transaction-list .transaction .description-container .tag,
  .transactionWidget transaction-list .transaction .description-container .tag,
  .transaction-profiles-widget transaction-list .transaction .description-container .tag,
  .outgoingsWidget transaction-list .transaction .description-container .tag,
  .plannerWidget .transaction-list .transaction .description-container .tag,
  .transactionWidget .transaction-list .transaction .description-container .tag,
  .transaction-profiles-widget .transaction-list .transaction .description-container .tag,
  .outgoingsWidget .transaction-list .transaction .description-container .tag,
  .plannerWidget transaction-list .transaction .amount-container .date,
  .transactionWidget transaction-list .transaction .amount-container .date,
  .transaction-profiles-widget transaction-list .transaction .amount-container .date,
  .outgoingsWidget transaction-list .transaction .amount-container .date,
  .plannerWidget .transaction-list .transaction .amount-container .date,
  .transactionWidget .transaction-list .transaction .amount-container .date,
  .transaction-profiles-widget .transaction-list .transaction .amount-container .date,
  .outgoingsWidget .transaction-list .transaction .amount-container .date,
  budget-tracker-widget .listOfCharts .budgetChart .budgetDetails .secondaryText,
  .widget-footer__link {
    color : var(--text-3);
  }

  .progressBarContainer .background {
    background-color : var(--bg-1);
  }

  canvas#donutChart {
    filter : invert(1) hue-rotate(180deg) saturate(2);
  }

  canvas#outgoingsByTagChart,
  canvas#balanceTrendsGraph {
    filter : invert(1) hue-rotate(160deg) brightness(.8) saturate(2);
  }

  .balanceTrends .chartContainer .chartTooltip,
  .outgoingsByTag .chartContainer .tooltip {
    color            : var(--text-2);
    background-color : var(--bg-1);
  }

  .outgoingsByTag .chartContainer .tooltip h3 {
    filter : brightness(2);
  }

  .balanceTrends .chartContainer .chartTooltip > .item,
  .balanceTrends .chartContainer .chartTooltip > .transactions,
  .balanceTrends .chartContainer .chartTooltip > .transactions p .amount {
    color : var(--text-1);
  }

  .outgoingsByTag .chartContainer .tooltip p {
    color : var(--text-3);
  }

  account-setup-progress .checklist .item > div {
    color : var(--text-1);
  }

  top-merchants .merchant-container .row {
    color : var(--text-1);
  }

  top-merchants .merchant-container .row .description .count {
    color : var(--text-3);
  }

  img[src='https://media.moneydashboard.com/img/default_merchant.svg'] {
    filter : invert(1);
  }

  whatisnew .container {
    color : var(--text-1);
  }

  img.closeButton {
    filter : invert(1);
  }

  /****************************************************************************************/

  /* Accounts */

  #fluidContent .cardContainer .institutionContainer .title h2,
  #fluidContent .cardContainer .institutionContainer .totalAmountContainer h2 {
    color : var(--text-1);
  }

  .totalAmountContainer .totalAmount p.negativeBalance,
  .accountCard p.negativeBalance {
    color : var(--text-red);
  }

  #fluidContent .cardContainer .institutionContainer .dragIcon {
    fill : var(--text-3);
  }

  .institutionContainer .expandIcon,
  .institutionContainer .expandIcon.is-expanded,
  .accountCard .md-button.md-icon-button img {
    filter : invert(1);
  }

  #fluidContent .accountCard {
    background-color : var(--bg-1);
  }

  #fluidContent .accountCard h2 {
    color : var(--text-1);
  }

  .accountCard,
  .cardContainer .left p,
  .open-banking-badge {
    color : var(--text-3);
  }

  .editAccountDialog .dialogContent {
    background-color : var(--bg-3);
  }

  .editAccountDialog .dialogContent .inlcudeInBalances p {
    color : var(--text-2);
  }

  .deleteAccountDialog .dialogContent .radioGroup p.title {
    color : var(--text-1);
  }

  .deleteAccountDialog .dialogContent p.datePickerText {
    color : var(--text-2);
  }

  /****************************************************************************************/

  /* Transactions */

  filter-controls > .card .action-bar,
  filter-controls > .card .filter-profile {
    border-color : var(--bg-3);
  }

  .transactions-filter-container {
    background-color : var(--bg-2);
  }

  .order-action-bar .order-by,
  .transactions-container .group .header > div {
    color : var(--text-1);
  }

  .transactions-container .group .header .totals div {
    color : var(--text-red);
  }

  .transactions-container .group .header .totals .totals-divider {
    background-color : var(--text-3);
  }

  .order-action-bar .order-by md-icon {
    fill : var(--text-3);
  }

  filter-controls > .card .filter-container .row .button {
    background-color : var(--bg-2);
  }

  filter-controls > .card .filter-container .row .button:hover {
    background-color : var(--bg-1);
  }

  .filterByAmount .dialogContent p {
    color : var(--text-2);
  }

  .transactions-container .group .card .transaction {
    color : var(--text-2);
  }

  .transactions-container .group .card .transaction.positive {
    color : var(--text-green);
  }

  md-dialog.fullscreen-dialog .formatted-content {
    color            : var(--text-1);
    background-color : var(--bg-2);
  }

  md-dialog.fullscreen-dialog .formatted-content h3 {
    color : var(--text-3);
  }

  md-dialog.fullscreen-dialog .formatted-content .transaction {
    color : var(--text-1);
  }

  .side-sheet__sheet {
    background-color : var(--bg-1);
  }

  transaction-details .navbar {
    background-color : var(--bg-2);
  }

  transaction-details .badge {
    color        : var(--text-1);
    border-color : var(--text-3);
  }

  transaction-details .badge .icon,
  transaction-details .ghost-icon-button {
    filter : invert(1);
  }

  transaction-details .transaction-summary .header .amount {
    color : var(--text-1);
  }

  transaction-details .transaction-details .container label,
  transaction-details .secondary-text {
    color : var(--text-3);
  }

  #editDialog,
  #editDialog .text,
  #editTransactionConfirmationDialog .md-dialog-content .text {
    color : var(--text-2);
  }

  /****************************************************************************************/

  /* Transactions - Tag selector */

  md-dialog.tagSelector .tagsList,
  md-dialog.tagSelector .tag-similar {
    background-color : var(--bg-2);
  }

  md-dialog.tagSelector .tag-similar .checkbox {
    color : var(--text-1);
  }

  md-dialog.tagSelector .tagsList .tag .addTag {
    background-color : var(--bg-1);
  }

  md-dialog.tagSelector .tagsList .tag .addTag:hover {
    background-color : var(--bg-3);
  }

  md-dialog.tagSelector .tagsList .sectionHeader,
  md-dialog.tagSelector .tagsList .tag .addTag p {
    color : var(--text-1);
  }

  md-dialog.tagSelector .moreButton {
    background-color : var(--bg-1);
  }

  md-dialog.tagSelector .moreButton md-icon.moreIcon {
    fill : var(--text-1);
  }

  md-dialog.tagSelector .moreButton:hover {
    background-color : var(--bg-3);
  }

  md-dialog.tagSelector .moreButton:hover md-icon.moreIcon {
    fill : var(--text-1);
  }

  md-dialog.tagSelector .custom-tag-container {
    background-color : var(--bg-2);
  }

  md-dialog.tagSelector .custom-tag-container .divider {
    background-color : var(--bg-3);
  }

  md-dialog.tagSelector .tagsList .tag .mainText {
    color : var(--text-1);
  }

  md-dialog.tagSelector .tagsList .tag .secondaryText {
    color : var(--text-3);
  }

  md-dialog.tagSelector .noTags .mainText,
  md-dialog.tagSelector .noTags .secondaryText {
    color : var(--text-3);
  }

  /****************************************************************************************/

  /* Budgets */

  .daysChart .budgetFrequencyTitle {
    color : var(--text-1);
  }

  .chartInfoContainer .budgetStatus,
  .cardContainer .budgetTags {
    color : var(--text-2);
  }

  .budgetRemaining {
    color : var(--text-3);
  }

  .cardContainer .budgetHistory {
    background-color : var(--bg-3)
  }

  .historyChart .primaryText {
    color : var(--text-1);
  }

  .historyChart .secondaryText {
    color : var(--text-3);
  }

  .historyChart canvas {
    filter : contrast(5);
  }

  .editBudgetContainer .subtitle {
    color : var(--text-3);
  }

  md-chips.md-default-theme md-chip, md-chips md-chip {
    color            : var(--text-1);
    font-weight      : normal;
    background-color : var(--bg-3);
  }

  /****************************************************************************************/

  /* Planner */

  #plannerGraph .header {
    background-color : var(--bg-3);
  }

  #plannerGraph canvas {
    filter : saturate(2);
  }

  #plannerGraph planner-chart .planner-chart__container {
    background-color : var(--bg-2);
  }

  #plannerGraph planner-chart .container .chart-container {
    background-color : var(--bg-2);
  }

  #plannerGraph planner-chart .chart-tooltip .block.in .row {
    background-color : rgba(76, 175, 80, .16);
  }

  #plannerGraph planner-chart .chart-tooltip .block .row {
    background-color : var(--bg-3);
  }

  #plannerGraph .controls md-menu.settings-menu button {
    color : var(--text-2);
  }

  #upcomingEvents .header,
  #upcomingEvents .md-subheader {
    background : var(--bg-3);
  }

  #upcomingEvents .md-subheader.md-default-theme,
  #upcomingEvents .md-subheader {
    color : var(--text-1);
  }

  #upcomingEvents .list .item,
  #accountsSummary .accounts p,
  #accountsSummary .summaryContainer p {
    color : var(--text-2);
  }

  #upcomingEvents .list .item.isPositive p.amount {
    color : var(--text-green);
  }

  #upcomingEvents .list .item p.amount,
  #accountsSummary .accounts p.amount,
  #accountsSummary .summaryContainer p.amount {
    color : var(--text-red);
  }

  #upcomingEvents .dailyBalance.negative {
    background-color : var(--text-red);
  }

  .add-event-dialog p,
  .cashflow:not(.managePredictions) md-dialog-content p {
    color : var(--text-2);
  }

  #existing-transactions .sorting p {
    color : var(--text-3);
  }

  #existing-transactions .sorting p md-icon {
    fill : var(--text-3);
  }

  #existing-transactions .transaction-list .transaction {
    color : var(--text-1);
  }

  /****************************************************************************************/

  /* Balance History */

  .balance-history canvas {
    filter : invert(1) hue-rotate(180deg) saturate(2);
  }

  .balance-history .chart-container .chart-tooltip {
    background-color : var(--bg-3);
  }

  .balance-history .chart-container .chart-tooltip > .item div h3 {
    color : var(--text-3);
  }

  .balance-history .chart-container .chart-tooltip > .item div p {
    color : var(--text-1);
  }

  /****************************************************************************************/

  /* In/Out */

  .inOutChart .mdbamchart {
    background-color : var(--bg-3);
  }

  .inOutChart .mdbamchart text {
    fill : var(--text-2);
  }

  #toggleTagsContainer .titleBox {
    color : var(--text-1);
  }

  #toggleTagsContainer .tagRow {
    color : var(--text-2);
  }

  #toggleTagsContainer .tagRow.secondLevelTagRow {
    color : var(--text-3);
  }

  /****************************************************************************************/

  /* Spend Report */

  spend-report-navbar .navbar .title {
    color : var(--text-1);
  }

  img.ghost-icon-button[src='https://media.moneydashboard.com/material-icons/ic_arrow_back_24px.svg'],
  img[src='https://media.moneydashboard.com/material-icons/ic_arrow_forward_24px.svg'] {
    filter : invert(1);
  }

  .spend-report p {
    color : var(--text-2);
  }

  .in-out .progress-container .progress-bar {
    background-color : var(--text-red);
  }

  .in-out .card .negative {
    color : var(--text-red);
  }

  category-breakdown .card .chart-container .center-information .main-text,
  .spend-report merchants-by-spend .header .main,
  .spend-report merchants-by-frequency .header .main,
  .spend-report .card.what-next .title {
    color : var(--text-1);
  }

  category-breakdown .card .chart-container .center-information .secondary-text,
  .spend-report merchants-by-spend .header .secondary,
  .spend-report merchants-by-frequency .header .secondary {
    color : var(--text-2);
  }

  /****************************************************************************************/

  /* Add Account */

  .addAccountContainerContent .institutionContainer,
  .addAccountContainerContent .institutionContainer .header,
  #fluidContent .contentContainer .addAccountContainer,
  .addAccountContainerContent .institutionContainer .institutionContent {
    color            : var(--text-2);
    background-color : var(--bg-1);
  }

  .rightSidebar,
  .rightSidebar .itemsContainer.progress,
  selected-institution .itemsContainer.progress {
    background-color : var(--bg-3);
  }

  h2.title,
  .addAccountContainerContent .institutionContainer .header .headerText,
  .addAccountContainerContent .institutionContainer .institutionContent .realmHeader .key {
    color : var(--text-1);
  }

  img.searchIcon[src='https://media.moneydashboard.com/svg/ic_search.svg'],
  img[src='https://media.moneydashboard.com/svg/ic_close.svg'] {
    filter : invert(1);
  }

  .switching-to-open-banking p,
  .addAccountContainerContent .institutionContainer .header .searchBox input {
    color : var(--text-2);
  }

  .addAccountContainerContent .institutionContainer .header .searchBox input::placeholder {
    color : var(--text-3);
  }

  selected-institution form {
    background-color : var(--bg-3);
    border-radius    : 10px;
  }

  /****************************************************************************************/

  /* Manage Tags */

  .searchBarContainer .searchBar,
  section.contentBox.manageTags,
  .searchBarContainer {
    background-color : var(--bg-2);
  }

  .searchBarContainer .searchBar input {
    color : var(--text-1);
  }

  .input-container .help-text,
  .searchBarContainer .searchBar input::placeholder {
    color : var(--text-3);
  }

  .levelsContainer .levelColumn .levelHeader {
    color : var(--text-1);
  }

  .levelsContainer .levelColumn .add-tag,
  .levelsContainer .levelColumn .tagLine {
    background-color : var(--bg-3);
  }

  .levelsContainer .levelColumn .tagLine.selected {
    background-color : rgba(0, 0, 0, 0.12) !important;
  }

  .levelsContainer .levelColumn .tagLine .tagName p,
  .levelsContainer .levelColumn .add-tag p {
    color : var(--text-1);
  }

  .tagSettings h2.title {
    color : var(--text-1);
  }

  .tagSettings p {
    color : var(--text-3);
  }

  .editTag .close-button {
    filter: invert(1);
    fill: var(--text-2);
  }

  /****************************************************************************************/

  /* Tagging Rules */

  .descriptionContainer li {
    color : var(--text-3)
  }

  .customRulesContainer .customRulesTable th {
    color : var(--text-2);
  }

  .customRulesContainer .customRulesTable tbody tr {
    border-bottom-color : var(--bg-3);
  }

  /****************************************************************************************/

  /* Settings */

  md-content.settings-tab {
    background-color : var(--bg-2);
  }

  .contentBox .settings-tab .text.label,
  .contentBox .settings-tab p.title,
