style.css:
@-moz-document domain("www.cbc.ca") {
/**
* CBC Dark Night Mode
*
* A dark style for CBC websites. Applies to News, Sports, Radio, Music, Listen sections.
* 
* By pkiff
* Version 1.4
* Last update: 2019-12-14
* Initial launch: 2019-06-20
*
* Notes from latest update:
* - fixed new Recommended for you list
* - tweaked primary heading style
* - tweaked Load More buttons
*
* 2019-09-26: Version 1.3
* - fixed new primary heading style
* - tweaked some underlines
*
* 2019-09-06: Version 1.2
* - fixed dark text and arrow overlay on teaser cards broken by CBC changes
* 
* 2019-07-05: Version 1.1
* - added styles for Music and Listen sections
* - added styles for Comments area
* 
* 2019-06-20: Version 1.0
* - Initial launch
* - Includes News, Radio and Sports sections
*
*/

/* Colors 
*
* black: #191919
* red: #e60505
* white: #ffffff
* off-white: #dddddd
* grey-mid: #666666
* grey-dark: #383838
* blue-mid (radio): #00498f
* blue-mid-alt (listen): #0a7f9d;
* blue-light (links): #3ea6ff
*
*/

/* Global Elements */

html {
  color: #dddddd;
}

a {
  color: #3ea6ff;
  text-decoration: underline;
}

a:visited {
  color: #00498f;
}

a:hover,
a:focus {
  text-decoration: none;
  color: #ffffff;
}


/* Global Blocks and Backgrounds */

.feed .appContainer {
  background-color: #191919;
}

.feed .appContainer.cookiesTheme, 
.feed .appContainer.detail, 
.feed .appContainer.metaTheme {
  background-color: #191919;
}

.feed .appContainer > .content {
  background-color: #191919;
}

.feed .show {
  background-color: #191919;
}

.feed .sponsorInfo .sponsorBoxHeading {
  background-color: #191919;
}


/* Global Classes */

.authorprofile-name-container a {
  color: #e60505;
}
  
.content {
  color: #dddddd;
}

.headingText {
  color: #dddddd;
  border-bottom: 1px solid #dddddd;
}

.headingText a, 
.headingText a:visited {
  color: #dddddd;
}

.headingText a:focus, 
.headingText a:hover {
  text-decoration: underline;
  color: #e60505;
}

.icon .closeIcon {
  fill: #000000;
}

.icon .searchIcon.headerSearch {
  fill: #dddddd;
}

.icon .chevronIcon {
  background-color: #191919;
  fill: #dddddd;
}

.icon .chevronIcon.horizontal {
  fill: #dddddd;
}

.icon .logoIcon.logoArts .cls-1, 
.icon .logoIcon.logoComedy .cls-1, 
.icon .logoIcon.logoDocumentaries .cls-1, 
.icon .logoIcon.logoLife .cls-1, 
.icon .logoIcon.logoMeta .cls-1, 
.icon .logoIcon.logoTV .cls-1 {
  fill: #dddddd;
}

.label.flag {
/*  background-color: #191919; */
  background-color: transparent;
  color: #dddddd;
}

.label.flag .labelText {
  color: #dddddd !important;
}


.widgets {
  color: #dddddd;
}


/* Global Header */

.feed .globalHeader {
  background-color: #191919;
}

.feed .globalHeader.open {
  background-color: #383838;
}

.feed .globalHeader .logo {
  color: #dddddd;
}

.feed .globalHeader .menuButton {
  border-left: 1px solid #dddddd;
  color: #dddddd;
}

.feed .globalHeader .menuButton:focus, 
.feed .globalHeader .menuButton:hover {
  color: #e60505;
}

.feed .globalHeader .landingNav {
  border-bottom: 1px solid #666666;
  border-top: 1px solid #666666;
  background-color: #191919;
}

.feed .globalHeader .landingNav .selectedNav {
  display: none;
}


.feed .globalHeader.isSticky.scrollUp .selectedNav {
  display: none; /* check on mobile */
}


/* Global Header - Modal Menu */

.feed .globalHeader .menuNav .menuList {
  background-color: #191919;
}

.feed .globalHeader .menuNav .mainMenuItem.hasSubsection {
    border-bottom: 1px solid #191919;
}

.feed .globalHeader .navSection
.feed .globalHeader .collapsible .navSection, 
.feed .globalHeader .collapsible .navSection:active, 
.feed .globalHeader .collapsible .navSection:link, 
.feed .globalHeader .collapsible .navSection:visited, 
.feed .globalHeader .navLink, 
.feed .globalHeader .navLink:active, 
.feed .globalHeader .navLink:link, 
.feed .globalHeader .navLink:visited {
  color: #dddddd;
}

.feed .globalHeader .collapsible .navSection:focus, 
.feed .globalHeader .collapsible .navSection:hover, 
.feed .globalHeader .navLink:focus, 
.feed .globalHeader .navLink:hover {
  color: #e60505;
}

.feed .globalHeader .navSection {
  color: #666666;
}

.feed .globalHeader .menuNavWrapper {
  background-color: #191919;
}

.feed .globalHeader .subNavList .subNavListItem .subNavLink {
  color: #dddddd;
}

.feed .globalHeader .menuNav .menuSidebar .menuSidebarHeading {
  background-color: #666666;
  color: #383838;
}

.feed .globalHeader .menuNav .menuSidebar {
  background: #000000;
}

.feed .globalHeader .menuNav .mainMenuItem.nav-more {
  background-color: #000000;
}

.feed .globalHeader .menuNav .mainMenuItem.nav-more .navLink {
    color: #666666;
}

.feed .globalHeader .menuNav .mainMenuItem.nav-more .navLink:hover,
.feed .globalHeader .menuNav .mainMenuItem.nav-more .navLink:focus,
.feed .globalHeader .menuNav .mainMenuItem.nav-more .navLink:active {
    color: #e60505;
}


.feed .globalHeader .menuNav .menuSidebar .subNavList .subNavListItem .subNavLink {
  color: #e60505;
}

.feed .globalHeader .menuNav .menuSidebar .subNavList .subNavListItem .subNavLink.selected, 
.feed .globalHeader .menuNav .menuSidebar .subNavList .subNavListItem .subNavLink:focus, 
.feed .globalHeader .menuNav .menuSidebar .subNavList .subNavListItem .subNavLink:hover {
  background-color: #e60505;
  color: #000000;
}

.feed .globalHeader .regionsListWrapper {
  background-color: #191919;
}

.feed .globalHeader .regionsListWrapper .regionLink {
  color: #666666;
  font-weight: bold;
}

.feed .globalHeader .regionsListWrapper .regionButton {
  background-color: #e60505;
  color: #000000;
  font-weight: bold;
}

.feed .globalHeader .regionsListWrapper .regionButton:hover {
  background: #191919 !important;
}


/* Global Footer */

.feed .globalFooter {
  background-color: #191919;
}

.feed .globalFooter .extendedFooter {
  background-color: #191919;
  color: #666666;
}

.feed .globalFooter .footerHeading {
  border-bottom: 1px solid #666666;
}

.feed .globalFooter .footerLink {
  color: #dddddd;
}

.feed .globalFooter svg.emailIcon g {
  fill: #dddddd;
}

.feed .globalFooter .cbcWatch:focus, 
.feed .globalFooter .cbcWatch:hover {
  background-color: #e60505;
  color: #ffffff;
  text-decoration: none;
  border-bottom-color: #e60505;
}


/* Banner Notice */

.feed .banner.notice {
  background-color: #383838;
  border: 3px solid #666666;
}

.feed .banner a:active, 
.feed .banner a:link, 
.feed .banner a:visited {
  color: #dddddd;
}

.feed .banner a:hover, 
.feed .banner a:focus {
  color: #000000;
  border-bottom: 1px solid #000000;
}

.feed .banner a:active:focus, 
.feed .banner a:active:hover, 
.feed .banner a:hover:focus, 
.feed .banner a:hover:hover, 
.feed .banner a:link:focus, 
.feed .banner a:link:hover, 
.feed .banner a:visited:focus, 
.feed .banner a:visited:hover {
  background-color: #e60505;
}


/* Home Page Card? */

.feed .homepageTheme .card {
  border: 1px solid #666666;
}


/* Card */

.feed .viewportLarge .featuredArea .featuredNews .featuredTopStories .primaryTopStories .contentList .contentListCards .card, 
.viewportMedium .featuredArea .featuredNews .featuredTopStories .primaryTopStories .contentList .contentListCards .card {
  border-color: #666666;
}

.feed .viewportLarge .featuredArea .card.cardFullBleed:not(.sponsored), 
.feed .viewportMedium .featuredArea .card.cardFullBleed:not(.sponsored), 
.feed .viewportXLarge .featuredArea .card.cardFullBleed:not(.sponsored) {
  background-color: #191919;
}

.feed .card {
  color: #dddddd;
  background-color: #191919;
  border-color: #666666;
}

.feed .card:focus,
.feed .card:hover {
  background-color: #000000 !important;
  box-shadow: 0 0 0 3px #e60505;
}

.feed .card:focus .card-content,
.feed .card:hover .card-content {
  background-color: transparent;
}

.feed .card.cardContentPackage .primaryHeadline .primaryHeadlineLink .headline, 
.feed .card.cardContentPackage .primaryContent .primaryHeadline .primaryHeadlineLink .headline {
  color: #dddddd;
}

.feed .card.cardContentPackage .primaryHeadline .primaryHeadlineLink:focus, 
.feed .card.cardContentPackage .primaryHeadline .primaryHeadlineLink:hover,
.feed .card.cardContentPackage .primaryContent .primaryHeadline .primaryHeadlineLink:focus, 
.feed .card.cardContentPackage .primaryContent .primaryHeadline .primaryHeadlineLink:hover {
  text-decoration-color: #dddddd;
}

.feed .card.cardListing {
  background-color: #191919;
}

.feed .card.cardFullBleed .contentWrapper .card-content::after {
  border-bottom-color: #191919;
}

.feed .card.cardFullBleed:hover .contentWrapper .card-content::after, 
.feed .card.cardFullBleed:focus .contentWrapper .card-content::after {
  border-bottom-color: #000000;
}

.feed .card.cardMultiLink .card-top-section {
  background-color: #191919;
}

.feed .card.cardMultiLink .card-top-section a .headline {
  color: #dddddd;
}

.feed .card.cardMultiLink .card-bottom-section .multiLower {
  background-color: #191919;
  border-color: #666666;
}

.feed .card.cardMultiLink .card-top-section a:hover .callToAction, 
.feed .card.cardMultiLink .card-top-section a:focus .callToAction, 
.feed .card.cardMultiLink .card-top-section a:active .callToAction {
  color: #dddddd;
  text-decoration: underline;
}

.feed .card.cardMultiLink .card-bottom-section .multiLower:hover .callToAction,
.feed .card.cardMultiLink .card-bottom-section .multiLower:focus .callToAction,
.feed .card.cardMultiLink .card-bottom-section .multiLower:active .callToAction {
  color: #dddddd;
  text-decoration: underline;
}

.feed .card.cardRegular {
  background-color: #191919;
}

.feed .card.cardRegular .contentWrapper::after {
  border-bottom-color: #191919;
}

.feed .card.cardRegular:hover .contentWrapper::after, 
.feed .card.cardRegular:focus .contentWrapper::after {
  border-bottom-color: #000000;
}

.feed .card.cardShow:hover .callToAction {
  color: #dddddd;
  text-decoration: underline;
}
  
.feed .card.cardShow .contentWrapper::after {
  border-bottom-color: #191919;
}

.feed .card .callToAction {
  color: #dddddd;
}

.feed .card .callToAction .chevronIcon {
  fill: #e60505;
}

.feed .card .card-content {
  color: #dddddd;
  background-color: #191919;
}

.feed .card .card-content .card-content-top .label.flag {
  padding-top: .5em;
}

.feed .card .card-content .card-content-top .label.flag .labelText {
  color: #666666 !important;
}

.feed .card .metadata {
  color: #666666;
}

.feed .card .metadata .commentCount-icon {
  fill: #dddddd;
}

.feed .card > .imageMedia {
  background-color: #191919; /* maybe not necessary */
}

.feed .card.cardShow {
  background-color: #191919;
}

.feed .card.cardShow .contentWrapper .card-content {
  background-color: #191919;
  color: #dddddd;
}

.feed .card.cardShow:hover .contentWrapper .card-content {
  background-color: #000000;
}

.feed .card.cardDiscovery {
  background-color: #191919;
}

.feed .card.cardDiscovery .headlineLink {
  color: #dddddd;
}

.feed .card.cardDiscovery .rankIndicator {
  color: #dddddd;
}

.feed .card.cardDiscovery .headlineLink:hover,
.feed .card.cardDiscovery .headlineLink:focus {
  background-color: #000000;
}

.feed .recommendations .contentListCards .card .card-content:hover, 
.feed .recommendations .contentListCards .card .card-content:focus, 
.feed .recommendations .contentListCards .card:first-child .card-content:hover, 
.feed .recommendations .contentListCards .card:first-child .card-content:focus {
  background-color: #000000;
}


.feed .verticalCardList.cardList .titleBar {
  background-color: #e60505;
  color: #dddddd;
  margin-bottom: 0;
}

.feed .verticalCardList.cardList .titleBar .title {
  border-bottom: 1px solid #e60505;
}

.feed .verticalCardList.cardList .titleBar a:hover {
  color: #e60505;
}


/* Story */


.feed .story {
  color: #dddddd;
}

.feed .story a {
  color: #e60505;
  border-bottom: 1px solid #e60505;
}

.feed .story a u {
  text-decoration: none;
}

.feed .story a.similarLink {
  border-bottom: none;
}

.feed .story a:hover,
.feed .story a:focus, 
.feed .story a:active {
  background-color: #e60505;
  color: #dddddd;
  border-bottom: 1px solid #e60505;
}

.feed .story .callout, 
.feed .story table {
  background-color: #000000;
  border-top: 1px solid #666666;
  border-bottom: 1px solid #666666;
}

.feed .story .pullquote .pullquote-source {
  color: #666666;
}

.pullquote-quotation-open path,
.pullquote-quotation-close path {
  fill: #dddddd; 
}



/* Detail */

.feed .globalHeader .detailNav .gemSectionLink .navLink {
  color: #dddddd;
}

.feed .globalHeader .detailNav {
  background-color: #191919;
  border-top: 1px solid #666666;
  border-bottom: 1px solid #666666;
  color: #dddddd;
}

.feed .detail .detailHeadline {
  color: #dddddd;
}

.feed .detail .deck {
  color: #dddddd;
}

.feed .detail .byline {
  color: #666666;
}

.feed .detail .byline a {
  color: #e60505;
  border-bottom: 1px solid #e60505;
}

.feed .detail .byline a:hover,
.feed .detail .byline a:focus,
.feed .detail .byline a:active
{
  background-color: #191919;
  color: #dddddd;
  border-bottom: 1px solid #191919;
}

.feed .detail .moreStories .contentList .contentListCards .card .contentWrapper .card-content .card-content-bottom .authorInfo .authorName {
  color: #dddddd;
}

.feed .detail .detail-link-label {
  color: #666666;
}

.feed .detail .detail-link-label a {
  color: #666666;
  text-decoration: underline;
}

.feed .detail .detail-link-label a:hover, 
.feed .detail .detail-link-label a:focus, 
.feed .detail .detail-link-label a:active {
  color: #dddddd;
  text-decoration: none;
}

.feed .detail .highlights .highlightListItem span {
  color: #dddddd;
}


.feed .detail .imageMedia .image-caption {
  color: #666666;
}

.feed .detail .moreStories .contentList .contentListCards .card .contentWrapper .card-content .card-content-top .headline:hover,
.feed .detail .moreStories .contentList .contentListCards .card .contentWrapper .card-content .card-content-top .headline:focus,
.feed .detail .moreStories .contentList .contentListCards .card .contentWrapper .card-content .card-content-top .headline:active{
  color: #e60505;
}

.feed .detail .contentFeedback .feedback, 
.feed .detail .contentFeedback .journalistLink, 
.feed .detail .contentFeedback .reportIssue, 
.feed .detail .contentFeedback .trustProjectLink {
  color: #e60505
}


/* Recommendations */ 

.feed .recommendations .contentListCards .card .card-content, 
.feed .recommendations .contentListCards .card:first-child .card-content {
  background-color: #191919;
  color: #ffffff;
}

.feed .recommendations .contentList .contentListCards, 
.feed .recommendations .verticalCardList.cardList .contentList .contentListCards {
  background-color: #191919;
}

.feed .detail .recommendations .contentList .contentListCards .card .contentWrapper .card-content .card-content-top .headline:hover,
.feed .detail .recommendations .contentList .contentListCards .card .contentWrapper .card-content .card-content-top .headline:focus,
.feed .detail .recommendations .contentList .contentListCards .card .contentWrapper .card-content .card-content-top .headline:active{
  color: #e60505;
}

.disclaimer-container button {
  background-color: #e60505;
  color: #000000;
  font-weight: bold;
}

.disclaimer-container button:hover,
.disclaimer-container button:focus,
.disclaimer-container button:active {
  background-color: #191919;
  color: #ffffff;
}

.disclaimer-container .question-mark {
  font-weight: bold;
  font-size: 1rem;
}

.disclaimer-container .disclaimer {
  background-color: #000000;
  color: #666666;
  border-color: #666666;
}

.disclaimer-container .disclaimer::after {
 border-color: hsla(0,0%,100%,0) hsla(0,0%,100%,0) hsla(0,0%,100%,0) #666666;
}

/* Swim Lane */ 

.feed .swimlane.newsTheme {
  background-color: #191919;
}

.feed .swimlane .moreStoriesList {
  background-color: #191919;
}

.feed .swimlane .regionsListWrapper {
  background-color: #191919;
  color: #dddddd;
}

.feed .swimlane .moreStoriesSubHeadline .regionsListWrapper .regionLink {
  color: #666666;
}

.feed .swimlane .moreStoriesSubHeadline .regionsListWrapper .regionLink {
  color: #666666;
}
.feed .swimlane .regionsListWrapper .regionLink:focus, 
.feed .swimlane .regionsListWrapper .regionLink:hover {
  color: #e60505;
}

.feed .swimlane .regionsListWrapper .regionButton {
  background-color: #e60505;
  color: #000000;
  font-weight: bold;
}

.feed .swimlane .regionsListWrapper .regionButton:hover {
  background: #191919 !important;
}

.feed .swimlane .contentListCards .card, 
.feed .swimlane .contentListCards .cardRegular {
  border-color: #666666;
}


/* WIDGETS AND SIDEBARS */


/* Ads */ 

.adCardWrapper {
  display: none;
}


/* Region List */

.regionsListWrapper .icon .chevronIcon.horizontal {
  fill: #666666;
}

.feed .regionInfo .regionsListWrapper.standAlone .currentRegion {
  color: #666666;
}

.feed .regionInfo .regionsListWrapper.standAlone .regionButton {
  background-color: #e60505;
}

.feed .regionInfo .contentList .contentListCards .card.cardFullBleed .card-content:focus, 
.feed .regionInfo .contentList .contentListCards .card.cardFullBleed .card-content:hover, 
.feed .regionInfo .contentList .contentListCards .card.cardText .card-content:focus, 
.feed .regionInfo .contentList .contentListCards .card.cardText .card-content:hover {
  background-color: #191919;
}

.feed .regionInfo .contentList .contentListCards .card.cardFullBleed:focus, 
.feed .regionInfo .contentList .contentListCards .card.cardFullBleed:hover, 
.feed .regionInfo .contentList .contentListCards .card.cardText:focus, 
.feed .regionInfo .contentList .contentListCards .card.cardText:hover {
  background-color: #191919;
}

.feed .regionInfo .contentList .contentListCards .card.cardFullBleed .card-content::after, 
.feed .regionInfo .contentList .contentListCards .card.cardText .card-content::after {
  border-bottom-color: #191919;
}

.feed .regionInfo .contentList .contentListCards .card.cardFullBleed .card-content:focus::after, 
.feed .regionInfo .contentList .contentListCards .card.cardFullBleed .card-content:hover::after, 
.feed .regionInfo .contentList .contentListCards .card.cardText .card-content:focus::after, 
.feed .regionInfo .contentList .contentListCards .card.cardText .card-content:hover::after {
  border-bottom-color: #000000;
}


.feed .newsTheme .regionInfo .regionsListWrapper.standAlone .regionButton {
  font-family: Open Sans, sans-serif;
  font-weight: bold;
}

.feed .newsTheme .regionInfo .regionsListWrapper.standAlone .regionButton:hover {
  background: #191919;
  color: #dddddd;  
}



/* Related Stories */ 

.feed .detail .relatedlinks .relatedLink {
  color: #e60505;
  border-bottom: 1px solid #191919;
}

.feed .detail .relatedlinks .relatedLink:hover,
.feed .detail .relatedlinks .relatedLink:focus, 
.feed .detail .relatedlinks .relatedLink:active {
  background-color: #e60505;
  color: #dddddd;
  border-bottom: 1px solid #e60505;
} 

.relatedList {
  color: #dddddd;
}

/* Trending List */

.feed .trendingList {
  background-color: #191919;
}

.feed .trendingList-sidebar .trendingList .trendingListItem .itemText {
  border-color: #666666;
}

.trendingList-news {
  color: #dddddd;
}

.feed .trendingList-detail h2 {
  color: #dddddd
}

.feed .trendingList-detail h2 span {
  color: #666666;  
}

.feed .trendingList .trendingListItem .readerCount {
  color: #666666;
}

.feed .trendingList .trendingListItem .rankIndicator {
  color: #dddddd
}

.feed .trendingList .trendingListItem .itemTitle {
  color: #dddddd;
}


/* Follow Us */

.feed .followUs {
  background-color: #191919;
  border: 1px solid #666666;
}

.feed .followUs ul li:hover,
.feed .followUs ul li:focus {
  border-color: #e60505;
}

/* Load More Button in Content List */

.feed .contentList button.loadMore {
  background-color: #e60505;
  color: #000000;
  font-weight: bold;
  border: 3px solid #e60505;
}

.feed .contentList button.loadMore .chevronIcon {
  fill: #000000;
}

.feed .contentList button.loadMore:hover {
  background: #191919;
  color: #dddddd;  
}
.feed .contentList button.loadMore:hover:focus {
  background: #dddddd;
  color: #191919;  
  border-color: #000000;
}

/* Schedule */

.feed .schedule .scheduleList {
  background-color: #191919;
}

.feed .schedule .disclaimer {
  background-color: #191919;
}

.feed .schedule .scheduleListItem .scheduleMedia {
  background-color: #666666;
  color: #000000;
}

.feed .schedule .scheduleLink {
  color: #ffffff;
}

/* Viafoura - Share and Comments */

.feed .appContainer .share .viafoura, 
.feed .share .viafoura {
  background-color: #191919;
}

.viafoura .vf-share-button .vf-share-button-icon::before {
  color: #3ea6ff;
}

.viafoura .vf-share-button .vf-share-button-icon:focus::before, 
.viafoura .vf-share-button .vf-share-button-icon:hover::before{
  color: #ffffff;
}

