style.css:
@-moz-document url-prefix("https://optifine.net") {
/* http://meyerweb.com/eric/tools/css/reset/ 
   v2.0 | 20110126
   License: none (public domain)
*/

html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed, 
figure, figcaption, footer, header, hgroup, 
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
	margin: 0;
	padding: 0;
}
body {
	line-height: 1;
}

table {
	border-collapse: collapse;
	border-spacing: 0;
}


/** CSS Start **/

body {background: rgb(20,24,26) url('https://i.imgur.com/XpzNYn2.png');}


.tableRoot {margin: 0;width: 100%;background: transparent;border: none;}
.tableRoot > tbody {display: grid;grid-template: 50px auto 40px/ auto;min-height: 100vh;}
.tableRoot > tbody > tr:nth-of-type(1) {padding: 0 10px;background: rgb(40,44,46);border-bottom: 2px solid rgb(50,100,150);height: 48px;position: fixed;top: 0;left: 0;width: 150px;z-index: 2;}
.tableRoot > tbody > tr:nth-of-type(1) > .title {padding: 4px 10px;background: transparent;font-size: 200%;display: block;}
.tableRoot > tbody > tr:nth-of-type(1) > .title > a {color: rgb(50,100,200);display: block;line-height: 40px;text-align: end;}

.tableRoot > tbody > tr:nth-of-type(2) {position: fixed;top: 0;left: 170px;width: 100%;z-index: 1;}
.tableNav {text-align: unset;background: rgb(40,44,46);border: none;display: block;height: 48px;border-bottom: 2px solid rgb(50,100,150);box-shadow: 0 1px rgba(0,0,0,.1), 0 2px rgba(0,0,0,.1), 0 3px rgba(0,0,0,.1);}
.tableNav > .tableNav {text-align: unset;background: transparent;border: none;display: block;}
.tableNav > .tableNav > tbody {display: block;}
.tableNav > .tableNav > tbody > tr {display: flex;flex-direction: row;flex-wrap: nowrap;height: 48px;}
.tableNav > .tableNav > tbody > tr > td {padding: 0;display: block;}
.tableNav > .tableNav > tbody > tr > td > a {color: rgb(200,200,200);height: 40px;width: auto;display: block;font-size: 16px;text-align: center;line-height: 40px;margin: 8px 10px 0 10px;padding: 0 10px;border-radius: 4px 4px 0 0;}
.tableNav > .tableNav > tbody > tr > td > a:hover {background: rgb(50,100,150);color: white;}


.tableRoot > tbody > tr:nth-of-type(3) {grid-area: 2/1/3/2;}
.content {padding: 20px;margin: 30px 40px 20px 40px;background: rgb(30,34,36);display: flex;flex-direction: column;flex-wrap: nowrap;border-radius: 4px;color:rgb(200,200,200);border: 2px solid rgb(40,44,46);position: relative;}



.tableRoot > tbody > tr:nth-of-type(4) {grid-area: 3/1/4/2;}
.tableRoot > tbody > tr:nth-of-type(4) > .footer {display: block;background: rgb(30,34,36);padding: 0;text-align: center;font-size: 18px;height: 40px;line-height: 40px;box-shadow: 0 -2px 2px rgba(0,0,0,.2);}
}

@-moz-document url("https://optifine.net/home") {
/** CSS Start **/



.content > h3 {text-align: center;font-size: 42px;text-decoration: underline rgb(100,100,100);padding: 20px 0;}
.content > p {text-align: center;}
.content > p:nth-of-type(1) {font-size: 42px;text-decoration: underline rgb(100,100,100);padding-bottom: 20px;text-align: center;}
.content > p:nth-of-type(4) {font-size:0;position: fixed;top: 0;right: 0;display: flex;flex-direction: row;flex-wrap: nowrap;z-index: 2;height: 50px;width: 120px;}
.content > p:nth-of-type(4) > a {color: white;background: rgb(50,100,150);border-radius: 4px;line-height: 30px;font-size:0;position: relative;height: 30px;width: 30px;display: block;margin: 10px auto;}

.content > p:nth-of-type(4) > a:nth-of-type(1) {-webkit-mask: url("https://i.imgur.com/iuke5LY.png") center/cover no-repeat}
.content > p:nth-of-type(4) > a:nth-of-type(2) {-webkit-mask: url("https://i.imgur.com/2pf2tqd.png") center/cover no-repeat}
