@-moz-document url-prefix("https://optifine.net/changelog") {
body {color: rgb(180,180,180);font-weight:800;letter-spacing:1px;line-height:14px;}
body pre {margin:20px;padding:10px;background:rgba(30,34,36,.6);border:2px solid rgb(60,64,66);border-radius:4px}


}
