style.css:
@-moz-document domain("al-monitor.com") {
h1, h2, h2.title, .title3, .title2, .title1, h3, h4, h5 {
	color: #8ba446 !important;
}
body, .bc-large, .artimg-rcol, .grad1, .s4tograd1, .col-md-4, #article, .recomArticle, #next_article, .header-top, .home-toparticle, .articletitle-inset, .touchup, #side-articles > div {
	color: #b9a993 !important;
    background-color: #333 !important;
    background: #333 !important;
    border-color: #222 !important;
    -webkit-text-stroke-width: 0px;

}
a, #small,.byline-name, a .byline-name, .timeago, .greenSmallUC {
    color: #559c7a !important;
}

div.col-md-2, #footer {
    display:none;
}
.col-md-6 {
	width: 66%;
}
.col-md-6 {
    padding: 20px;
    margin: 0px;
}
.articleLead, .col-md-6, .artimg-rcol, .essay, .s6, .art-span-4, .current-slide, .blackbg-fullw, .panel-body {
    background-color: #333 !important;
    background: #333 !important;
	color: #b9a993 !important;
}
.col-md-4 {
    margin: 0;
    padding: 0;
}

.topicsTitles {
    width:auto;
    height: 49px;
    padding: 0;
    padding-right: 20px;
    margin: 0;
}
.artimg-rcol {
	padding: 30px;
}
#article {
	margin-top: 0px;
}
.fullpageArticleAuthors {
	margin-right: 50px;
}
header .blogsMenu ul li, .bottom-margin-minusone {
	border-color: #333 !important;
}

.recomArticle, #next_article {
	margin-top: 0px;
	margin-left: auto;
	margin-right: 0px;
	padding-right: 92px;
	margin-bottom: 0px;
	padding-bottom: 5px;
   	padding-left: 30px;
	padding-top: 30px;
}
.essay {
    font-size: large !important;
}
div.hidden-xs:nth-child(4), div.nomarg-nopad:nth-child(1) {
    display:none;
}
.panel-group .panel-heading + .panel-collapse > .panel-body, .panel-group .panel-heading + .panel-collapse > .list-group {
	border-top: 1px solid black;
}
.border-container::before {
	border-top: 4px solid #252525;
	border-bottom: 4px solid #252525;
	background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#333), to(#252525));
	background-image: -webkit-linear-gradient(#333, #252525);
	background-image: -moz-linear-gradient(#333, #252525), -moz-linear-gradient(#333, #252525);
	background-image: -o-linear-gradient(#333, #252525), -o-linear-gradient(#333, #252525);
	background-image: linear-gradient(#333, #252525), linear-gradient(#333, #252525);
}
.ghost-button, #recurlyModal .btn {
	border: 0px solid #2b2b2b;
    box-shadow: 3px 3px 0 black;
	background-color: #3c3c3c;
    color: #559c7a !important;
}
.ghost-button:hover, #recurlyModal .btn:hover {
	border: 0px solid #2b2b2b;
    box-shadow: 3px 3px 3px black;
	background-color: #444;
    color: #559c7a !important;
}

.pulse-feat-topics {
	border: solid #242424 4px;
}

.gradBak {
	background: #222;
	background: -moz-linear-gradient(10deg, #444 0%,#444 27%,#444 27%,#222 100%);
	background: -webkit-linear-gradient(10deg, #444 0%,#444 27%,#444 27%,#222 100%);
	background: linear-gradient(10deg, #444 0%,#444 27%,#444 27%,#222 100%);
}
.col-md-5 {
	width: 25%;
}
.col-md-7 {
	width: 75%;
}
#recurlyModal {
	background: #333;
	background: -webkit-linear-gradient(to bottom, #444, #333);
	background: -o-linear-gradient(to bottom, #444, #333);
	background: -moz-linear-gradient(to bottom, #444, #333);
	background: linear-gradient(to bottom, #444, #333);
	border: 0px;
}
#recurlyModal.aboveRecurlyModal {
	background: none;
	background: -webkit-linear-gradient(to bottom, #333, #444) !important;
	background: -o-linear-gradient(to bottom, #333, #444) !important;
	background: -moz-linear-gradient(to bottom, #333, #444) !important;
	background: linear-gradient(to bottom, #333, #444) !important;
	filter: none;
}
}
