@-moz-document domain("kaas.am") {
/*V4.1.1_230216*/
/*[[th]]*/
:root {
    --minr: /*[[min]]*/;
    --scnr: /*[[scn]]*/;
    --acnr: /*[[acn]]*/;
    --txnr: /*[[txn]]*/;
    --acnbcg: /*[[acn]]*/21;
    --minbcg: /*[[min]]*/21;
}
/*[[vvh]]*/
:root .v-theme--dark{
    --v-theme-background: var(--minr);
    --v-theme-background-overlay-multiplier: 1;
    --v-theme-surface: var(--minr);
    --v-theme-surface-overlay-multiplier: 1;
    --v-theme-surface-variant: 189,189,189;
    --v-theme-surface-variant-overlay-multiplier: 2;
    --v-theme-on-surface-variant: 66,66,66;
    --v-theme-primary: var(--acnr);
    --v-theme-primary-overlay-multiplier: 2;
    --v-theme-primary-darken-1: 55,0,179;
    --v-theme-primary-darken-1-overlay-multiplier: 1;
    --v-theme-secondary: var(--acnr);
    --v-theme-secondary-overlay-multiplier: 2;
    --v-theme-secondary-darken-1: 3,218,197;
    --v-theme-secondary-darken-1-overlay-multiplier: 2;
    --v-theme-error: 207,102,121;
    --v-theme-error-overlay-multiplier: 2;
    --v-theme-info: 33,150,243;
    --v-theme-info-overlay-multiplier: 2;
    --v-theme-success: 76,175,80;
    --v-theme-success-overlay-multiplier: 2;
    --v-theme-warning: 251,140,0;
    --v-theme-warning-overlay-multiplier: 2;
    --v-theme-primary-darken: var(--acnr);
    --v-theme-primary-darken-overlay-multiplier: 2;
    --v-theme-on-background: var(--txnr);
    --v-theme-on-surface: var(--txnr);
    --v-theme-on-primary: var(--txnr);
    --v-theme-on-primary-darken-1: var(--txnr);
    --v-theme-on-secondary: 0,0,0;
    --v-theme-on-secondary-darken-1: 0,0,0;
    --v-theme-on-error: var(--txnr);
    --v-theme-on-info: var(--txnr);
    --v-theme-on-success: var(--txnr);
    --v-theme-on-warning: var(--txnr);
    --v-theme-on-primary-darken: 0,0,0;
    --v-border-color: var(--txnr);
    --v-border-opacity: 0.12;
    --v-high-emphasis-opacity: 1;
    --v-medium-emphasis-opacity: 1;
    --v-disabled-opacity: 0.38;
    --v-idle-opacity: 0.1;
    --v-hover-opacity: 0.1;
    --v-focus-opacity: 0.12;
    --v-selected-opacity: 0.08;
    --v-activated-opacity: 0.12;
    --v-pressed-opacity: 0.16;
    --v-dragged-opacity: 0.08;
    --v-theme-kbd: 33, 37, 41;
    --v-theme-on-kbd: var(--txnr);
    --v-theme-code: 52, 52, 52;
    --v-theme-on-code: 204, 204, 204;
    --borr: /*[[boh]]*/;
}
/*min*/
.v-application {
    background-color: var(--v-theme-background)
}
/*minimp*/
footer.v-footer.v-theme--dark.d-flex.flex-column.justify-center {
    background-color: var(--minr)!important
}
/*scn*/
.v-menu .v-overlay__content>.v-card, nav.v-navigation-drawer.v-navigation-drawer--top.v-navigation-drawer--temporary.v-navigation-drawer--active.v-theme--dark, .v-menu .v-overlay__content {
    background-color: var(--scnr)
}
/*acntxn*/
/* {
    color: var(--acnr)
}*/
/*acntxnimp*/
.text-primary, .text-primary-darken {
    color: var(--acnr)!important
}
/*acnbcg*/
.bg-amber-darken-1 {
    background-color: var(--acnr)!important
}
/*txn*/
a, .v-list-item--variant-outlined, .v-list-item--variant-plain, .v-list-item--variant-text, .v-list-item--variant-tonal {
    color: var(--txnr)
}
/*txnimp*/
.v-app-bar.v-toolbar, .v-application, .text-grey-lighten-5, .text-black, .bg-amber-darken-1 {
    color: var(--txnr)!important
}
/*br*/
    .v-menu .v-overlay__content {
        border-radius: var(--borr)
    }
/*brimp*/
.v-card, .rounded-pill, nav.v-navigation-drawer.v-navigation-drawer--left.v-navigation-drawer--active.v-theme--dark.border-none.rounded-lg.main-nav-bar {
    border-radius: var(--borr)!important
}
/*rbxs*/
.v-chip--variant-elevated {
    box-shadow: none
}
/*navbar*/
nav.v-navigation-drawer.v-navigation-drawer--left.v-navigation-drawer--active.v-theme--dark.border-none.rounded-lg.main-nav-bar {
    
    left: 1.8ch!important;
    top: 9ch!important;
    width: 25ch!important;
    height: 60ch!important;
    border-radius: 5ch!important;
    background-color: var(--scnr)!important;
    background-image:   linear-gradient(217deg, var(--acnbcg), rgba(0,0,0,0) ), 
                        linear-gradient(127deg, var(--acnbcg), rgba(0,0,0,.4) ),
                        linear-gradient(336deg, var(--acnbcg), rgba(0,0,0,0.2) );
}
/*info*/
.v-card.v-theme--dark.v-card--density-default.rounded-lg.v-card--variant-elevated  {
    background-color: var(--scnr);
    background-image:   linear-gradient(217deg, var(--acnbcg), rgba(0,0,0,.4) ), 
                        linear-gradient(127deg, var(--acnbcg), rgba(0,0,0,.2) ),
                        linear-gradient(336deg, var(--acnbcg), rgba(0,0,0,0) );
}
/*header*/
.v-toolbar__content, .v-toolbar__extension {
    background-color: transparent;
    background-image: linear-gradient(45deg, var(--minr) 25ch, #0f082121,var(--acnbcg) 35%, var(--minr) 90%);
}
/*search result*/
a.v-list-item.v-list-item--link.v-list-item--nav.v-list-item--prepend.v-theme--dark.v-list-item--density-compact.v-list-item--one-line.v-list-item--rounded.v-list-item--variant-text:hover {
    background-image: linear-gradient(45deg, transparent, 66%, #0000ff21);
    background-color: var(--scnr)
}
/*card animation*/
a.v-card.v-card--link.v-theme--dark.v-card--density-default.rounded-lg.v-card--variant-elevated.episode-item:hover {
    transform: translatey(-1ch)
}
a.v-card.v-card--link.v-theme--dark.v-card--density-default.rounded-lg.v-card--variant-elevated.episode-item {
    transition: transform .3s ease
}
/*---*/
/*bold :)*/
.episode-item-footer .episode-title {
    font-weight: bold
}
 span.v-chip.v-theme--dark.bg-primary.v-chip--density-comfortable.rounded-sm.v-chip--size-x-small.v-chip--variant-elevated.font-medium {
    width: 85%!important;
    text-shadow: 1px 1px 5px #000;
    color: #fff!important;
    font-weight: bold!important;
    height: 50%;
    font-size: 2ch
}
}
