style.css:
@-moz-document url-prefix("http://localhost:8888/") {
:root {
      --main-font-family: Consolas, monaco, monospace;
      --main-bg-color: #35362F;

      --atom-one-dark-cyan : #56B6C2;
      --atom-one-dark-blue : #61AFEF;
      --atom-one-dark-bg-1 : #121417;
      --atom-one-dark-mono-2 : #828997;
      --atom-one-dark-red-1 : #E06C75;
      --atom-one-dark-mono-1 : #ABB2BF;
      --atom-one-dark-silver : #AAAAAA;
      --atom-one-dark-purple : #C678DD;
      --atom-one-dark-orange-2 : #E5C07B;
      --atom-one-dark-bg-hl : #2F343D;
      --atom-one-dark-black : #0F1011;
      --atom-one-dark-gutter : #666D7A;
      --atom-one-dark-fg : #ABB2BF;
      --atom-one-dark-green : #98C379;
      --atom-one-dark-red-2 : #BE5046;
      --atom-one-dark-orange-1 : #D19A66;
      --atom-one-dark-mono-3 : #5C6370;
      --atom-one-dark-accent : #AEB9F5;
      --atom-one-dark-bg : #282C34;
      --atom-one-dark-gray : #3E4451;
  }
/* Intro page  */
    body {
        background-color:  var(--main-bg-color);
        color : var(--atom-one-dark-silver);
        color: white;
    }
    
    .list_header {
        background-color:  var(--main-bg-color);
    }

    div#notebook{
        background-color:  var(--main-bg-color);
    }

/* Dropdown menus */

    .dropdown-menu {
        background: rgba(50, 50, 50, 0.95);
        color: #b7b7b7;
    }

    .dropdown-menu>li>a, .dropdown-submenu>a:after, .dropdown-menu>li>a:visited:after {
        color: #b7b7b7;
    }

    .dropdown-menu .divider {
        background-color: #444;
    }

    .dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
        color: #fff;
        background-color: #444;
    }

    .dropdown-menu>li>a:hover:after, .dropdown-menu>li>a:focus:after, .dropdown-menu>li>a:active:after {
        color: #fff;
    }
    
    div#header{
        background-color:  var(--main-bg-color);
    }
    
    div#menus{
        background-color:  var(--main-bg-color); 
    }

    div#maintoolbar{
        background-color:  var(--main-bg-color); 
    }
    
    div#notebook-container{
        background-color:  var(--main-bg-color);
    }
    
    body > #header .header-bar {
        background: #757575;
    }

/* Help panel */
    div#pager {
        background-color: #282c34;
    }
    
    div#pager .ui-resizable-handle {
        top: 0px;
        height: 8px;
        background: #282c34;
        border-top: 1px solid #757575;
        border-bottom: 1px solid #757575;
    }
    div#pager pre {
        color: #9ac2c9;
        background-color: #282c34;
    }
    

    .btn-default {
        color: var(--atom-one-dark-silver);
        background-color: #54565a;
        border-color: #3a3b3d;
        border-radius: 4px;
    }
    
    .notification_widget {
        background: rgba(240, 240, 240, 0.5);
        color: #333;
        background-color: #aaa;
        border-color: #757575;
    }
    
    span#login_widget > .button, #logout {
        color: #333;
        background-color: #aaa;
        border-color: #757575;
    }
    
    .form-control {
        color: #333;
        background-color: #aaa;
        border: 1px solid #757575;
    }
    
    .input_area {
        border: 1px solid #757575 !important;
    }
    
    .notebook_app div.input_prompt {
        color: var(--atom-one-dark-green);
    }
    
    .notebook_app div.output_prompt {
        color: var(--atom-one-dark-red-1);
    }
    
    .notebok_app div.output_area pre {
         color: whitesmoke !important;
    }
    
    .notebook_app div.output_subarea pre {
         color: #c4c4c4 !important;
    }

    .notebook_app div.text_cell_render, .notebook_app div.output_html pre {
        color: #979797;
    }

/* Tooltips */
    .ipython_tooltip {

        background-color:  var(--main-bg-color) !important;
    }
    .ipython_tooltip .tooltiptext pre {
        font-size: 100%;
        color:lightgrey;
        background-color:var(--main-bg-color) !important;
    }

/*Terminal text coloring*/

    span.ansiblack {
        color: #323537;
    }

    span.ansiblue {
        color: #afc4db;
    }

    span.ansigray {
        color: #838184;
    }

    span.ansigreen {
        color: #8f9d6a;
    }

    span.ansipurple {
        color: #9b859d;
    }

    span.ansired {
        color: #cf6a4c;
    }

    span.ansiyellow {
        color: #c2bc15;
    }

/*INPUT CELLS*/

  .CodeMirror {
/*Background color of input cell*/
/*       background: #141414; */
      background-color: #3333;
      color: var(--atom-one-dark-mono-1);
/*       border-radius: .35em; */
      /*box-shadow: inset 10px 10px 16px -4px rgba(0, 0, 0, 0.44);*/
  }

  div.CodeMirror-selected {
      background: rgba(200, 219, 234, .19) !important;
/*       background: beige!important; */
  }

  #texteditor-backdrop #texteditor-container .CodeMirror-gutter, .CodeMirror-gutters {
      background: #35362f;
      color: #777;
  }

  .CodeMirror-linenumber {
      background-color:  var(--main-bg-color);
      color: #7C7C7C;
      font-size: inherit !important;
  }

    .CodeMirror-linenumbers{
      background-color:  var(--main-bg-color);
    }
  .CodeMirror-cursor {
      border-left: 1px solid white !important;
      margin-top: -2px;
      min-height: 18px;
  }

  .cm-s-ipython.CodeMirror {
      background-color: var(--main-bg-color);
      /*background: #282C34;*/
      /*background: #2c303c;*/
      /* The color below is all of the text not spoken for by the code below. */
      color: white;
  }

  .cm-s-ipython div.CodeMirror-selected {
      background: rgba(235, 240, 235, .19) !important;
  }

  .cm-s-ipython .CodeMirror-gutters {
      background: #2b303b;
      border-right: 1px solid #666464;
  }

  .cm-s-ipython .CodeMirror-linenumber {
      color: #7C7C7C;
      font-size: 10px;
  }

  .cm-s-ipython .CodeMirror-cursor {
      border-left: 2px solid white !important;
  }

  .cm-s-ipython span.cm-comment {
      color: #7C7C7C;
  }

  .cm-s-ipython span.cm-atom {
      color: #61afef;
  }

  .cm-s-ipython span.cm-number {
      color: #BB99FF;
  }

  .cm-s-ipython span.cm-property {
      color: white;
  }

  .cm-s-ipython span.cm-attribute {
      color: #ea8080;
  }

  .cm-s-ipython span.cm-keyword {
      color: #F74181;
  }

  .cm-s-ipython span.cm-string {
      color: #ECE087;
  }

  .cm-s-ipython span.cm-meta {
      color: #51b6c3;
  }

  .cm-s-ipython span.cm-operator {
      color: #5cadf1;
  }

  .cm-s-ipython span.cm-builtin {
      color: #61afef;
  }

  .cm-s-ipython span.cm-variable {
      color: white;
  }

  .cm-s-ipython span.cm-variable-2 {
      color: #ea8080;
  }

  .cm-s-ipython span.cm-variable-3 {
      color: #51b6c3;
  }

  .cm-s-ipython span.cm-def {
      color: #B1E037;
  }

  .cm-s-ipython span.cm-error {
      background: #bf616a;
      color: #a7adba;
  }

  .cm-s-ipython span.cm-bracket {
      color: white;
      font-weight: bold;
  }
  
  .cm-s-ipython span.cm-tag {
      color: #61afef;
  }

  .cm-s-ipython span.cm-link {
      color: #e2a271;
  }

  .cm-s-ipython span.cm-storage {
      color: #61afef;
  }

  .cm-s-ipython span.cm-entity {
      color: #61afef;
  }

  .cm-s-ipython span.cm-latex {
      color: #8fb36a;
  }

  .cm-s-ipython .CodeMirror-matchingbracket {
      text-decoration: underline;
      -moz-text-decoration-color: #37d6f2;
      text-decoration-color: #37d6f2;
      color: white !important;
  }


  /*Markdown editing*/

  .CodeMirror.cm-s-default {
      background: #2C2C2C;
      color: #787474;
      border-radius: .35em;
      box-shadow: inset 10px 10px 16px -4px rgba(0, 0, 0, 0.44);
  }

  .cm-s-default span.cm-bracket {
      color: #c0c5ce;
  }

  .cm-s-default span.cm-header {
      color: #CF4A4C;
  }

    .cm-s-ipython span.cm-header {
        color: #7575f6;
    }

  .cm-s-default span.cm-variable-2 {
      color: #7587A6;
  }

  .cm-s-default span.cm-comment {
      color: #7F7A80;
  }
    div.output_stderr {
        background: #000000;
    }

div.output.output_scroll {
    box-shadow: none;
}
/*Scroll bars*/
::-webkit-scrollbar {
    width: 2px;
    height: 5px;
    padding-top: 15px;
    margin-top: 10px;

}

/* Handle */
::-webkit-scrollbar-thumb {
    -webkit-border-radius: 2px;
    border-radius: 2px;
    background: #484848;
    border: 0px solid #eee;

}
::-webkit-scrollbar-thumb:window-inactive {
	background: #484848; 
}
.CodeMirror-code {
    font-family: Consolas, Monaco;
    line-height: 1.5;
    font-family: Menlo;
    font-size: 11pt;
    font-weight: 300;
}
.modal-content{
    color: black;
}
.list_container > div {
    border-bottom: 1px solid #716d6d;
}
.list_container {
    border: 1px solid #7a7474;
    border-radius: 4px;
}
.list_item:hover {
    background-color: #444444;
}
.item_name {
    color: #75bfff;
}
#texteditor-backdrop {
    background-color: #35362f;
}
.completions {
    -webkit-box-shadow: none;
    box-shadow: none;
}
.completions select {
    background: #35362f;
    color: #cfcdcd;
}
#menubar .navbar {
    border: none;
}
.nav-tabs {
    border: none;
}
.nav-tabs >li > a{
    color: #75bfff;
}

.item_icon{
    color: white;
}
.panel > .list-group, .panel > .panel-collapse > .list-group{
    color: black;
}
.nav-pills a {
    color: #75bfff;
}
.ipython_tooltip {
    -webkit-box-shadow: none;
    box-shadow: none;
}
.smalltooltip{
    overflow: scroll;
}
.tooltipbuttons {
    top: 0px;
}
a:link.ui-button, a:visited.ui-button, .ui-button {
    text-decoration: none;
    background: #363834;
    border-radius: 6px;
    border: none;
}
button:hover .ui-icon, .ui-button:focus .ui-icon {
   background-image: url(https://yyrcd-1256568788.cos.na-siliconvalley.myqcloud.com/yyrcd/2019-06-28-ui-icons_888888_256x240.png);
}
.ui-button:hover .ui-icon:hover{
    background-image: url(https://yyrcd-1256568788.cos.na-siliconvalley.myqcloud.com/yyrcd/2019-06-28-ui-icons_888888_256x240.png);
}
a.ui-button:hover{
    background: darkgray;
}
.ui-corner-all{
    border-radius: 6px;
    padding: .3em 1em;
}
a.ui-corner-all:hover{
    background: darkgray;
}
.ui-icon, .ui-widget-content .ui-icon {
    background-image: url(https://yyrcd-1256568788.cos.na-siliconvalley.myqcloud.com/yyrcd/2019-06-28-ui-icons_888888_256x240.png);
}
div.cell.jupyter-soft-selected {
    border-left-color: #5c5a5a;
    border-right-color: #5c5a5a;
    background: #5c5a5a;
}
}
