html * {
    line-height: 1.5;
}
dfn[data-dfn-type]::after {
    content: "#" attr(id) !important;
    font-weight: normal;
    font-size: 14px;
    padding-left: 2rem;
    font-style: normal;
    font-family: "Consolas, sans-serif"
}

dt,dd{
    margin-bottom:.5em;
}
.termref, a.termref:link {
    text-decoration: underline;
}

pre .str, code .str, code.str {
    color: #066206;
}

#navigation li a,
#navigationbottom li a {
    padding: 3px 1em;
    background: #eeeef3;
}
#related > ul {
    list-style-type: none;
}
a[href] {
    color: #0074ca;
}

a[href].silverlight,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/silverlight/"],
a.flash,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/flash/"]{
    color:#777777 !important;
}



a.general::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/general/"]::before,
a.html::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/html/"]::before,
a.pdf::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/pdf/"]::before,
a.script::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/script/"]::before,
a.css::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/css/"]::before,
a.aria::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/aria/"]::before,
a.text::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/text/"]::before,
a.failure::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/failures/"]::before,
a.silverlight::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/silverlight/"]::before,
a.flash::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/flash/"]::before{
    
    font-family:"franklin gothic","Segoe UI", Helvetica, sans-serif;
    font-weight: 400;
    display: inline-block;
    padding: 0 .25em;
    border: 1px solid transparent;
    font-size: .8em;
    margin-right: .5em;
    text-align: center;
    border-radius: .3em;
    min-width: 48px;
    
}

a.general::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/general/"]::before {
    content: "General";
    background-color: #00059e;
    color: #ffffff;
}
a.html::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/html/"]::before {
    content: "HTML";    
    background-color: #6b6c8a;
    color: #ffffff;
}
a.pdf::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/pdf/"]::before {
    content: "PDF";
    background-color: #ffffff;
    color: #9b0000;
    border: 1px solid #9b0000;
}
a.script::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/script/"]::before {
    content: "Script";
    background-color: #686868;
    color: #ffffff;
}

a.css::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/css/"]::before {
    content: "CSS";
    background-color: #3d5959;
    color: #ffffff;
}
a.aria::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/aria/"]::before {
    content: "ARIA";
    background-color: #008274;
    color: #ffffff;
}

a.text::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/text/"]::before {
    content: "Text";
    background-color: #686868;
    color: #ffffff;
}
a.failure::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/failures/"]::before {
    content: "Failure";
    background-color: #b50000;
    color: #ffffff;
}
a.silverlight::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/silverlight/"]::before {
    content: "Silverlight";
    background-color: transparent;
    border: 1px solid #DDDDDD;

}
a.flash::before,
a[href^="https://www.w3.org/WAI/WCAG21/Techniques/flash/"]::before {
    content: "Flash";
    background-color: transparent;
    border: 1px solid #DDDDDD;
}

a[href] {
    color: #0061a8;
    text-decoration: underline;
    border-bottom: none;
    padding: 0 1px 0;
    margin: 0 -1px 0;
}


h1,
h2,
h3 {
    color: inherit;
    font-family: Convection, Verdana, Helvetica, sans-serif;
    font-weight: 600 !important;
}
h1,
h2,
h3,
h4,
h5,
h6,
dt {
    line-height: 1.4;
}

h2 {
    padding-top: 1em;
    border-top: 1px solid #ccc;
}

blockquote.scquote,
blockquote.glquote {
    border: solid #bbb 1px;
    background: #fbf9f9;
}
kbd {
    border: 1px solid #444444;
    border-radius: 3px;
    background: rgba(0,0,0,.03);
    color: #000000;
    font-family: arial, helvetica, san-serif !Important;
    padding: 0 2px;
}


