    margin-bottom: 1em;
}

p,
td {
    line-height: 1.5;
    word-spacing: .1em;
}

main {
    max-width: 700px;
}

.note {
    border-top: 1px solid #1da51d;
    border-right: 1px solid #1da51d;
    border-bottom: 1px solid #1da51d;
    border-left: 7px solid #1da51d;
}
