style.css:
@-moz-document url-prefix("http://you.graylog.com/") {
/*Dark Stuff*/
/*dark-dark*/
html, body, .form-control, .Select-placeholder, .dropdown-toggle, .pagination li a, .dc-table-label, .dc-table-row:hover, .dropdown-menu li a:hover, .table-striped > tbody > tr:nth-of-type(2n+1), .modal-content, .well, table tbody tr:hover, .active td, .nav li a:hover, .popover, .popover-title, .list-group span, li.active a, .Select-value, .Select-menu-outer *, .tt-menu, div[style="position: absolute; inset: 0px; box-shadow: rgba(0, 0, 0, 0.12) 0px 2px 10px, rgba(0, 0, 0, 0.16) 0px 2px 5px; border-radius: 2px; background: rgb(255, 255, 255) none repeat scroll 0% 0%;"], .ace_active-line, .ace_gutter-layer, .panel-heading, .list-group-item {
    background-color: #292929 !important;
    color: #d5d5d5;
}
.btn {
    background-color: #292929;
}
/*light-dark*/
#universalsearch, .content, .dropdown-menu, .input-group-addon, code, .content-col, .text-center, .btn:focus, .ace_content, .panel-body, .btn-toolbar, .widget, .Select-control, .c3-tooltip-container * {
    background-color: #2f2f2f !important;
    color: #d5d5d5;
}
.btn:hover {
    background-color: #2f2f2f;
}
.label-default {
    background-color: #484848;
}
/*Light Stuff*/
.alert {
    background-color: #dddbda !important;
}

.btn-text{
    background-color: transparent !important;
}

/*text*/
/*text general*/
h1, h2, .btn:hover, h3, .dropdown-menu li a, .modal-title, .form-control, .btn, h4, .Select-value span, .Select-menu-outer *:hover, .title, .panel-heading, .label, .Select-menu-outer * {
    color: #d5d5d5 !important;
    background-image: none !important;
}
.axis, .domain, .axis line, .y-label, .x-axis-label, g, text {
    stroke: #fff !important;
    fill: #fff;
}
/*Green Arrows*/
path[stroke="#8DC63F"] {
    stroke: #15ad01 !important;
}
/*Graph content*/
rect {
    fill: #9b1a1f !important;
}
/*links and preview text*/
a, .message-wrapper {
    color: #b91212 !important;
}
/*selected text*/
::-moz-selection {
  background: #b91212;
  color: #dbdbdb;
}
/*Fixes*/
/*remove message preview gradient*/
.message-wrapper::after {
    background: transparent !important;
}
/*navbar clipping in half widow*/
.navbar-header {
    width: 95px;
}
.navbar-brand {
    padding-left: 0px;
    padding-right: 0px;
}
/*tooltip remove*/
.description-tooltips {
    display: none;
}
/*prevent field selector box from growing forever*/
.tab-pane div div {
    max-height: 370px !important
}
}

@-moz-document url("http://your.graylog.com/") {
/*fancy space background*/
html {
    background-color: transparent !important;
}
body {
   background: url(https://i.pinimg.com/originals/b4/3b/3a/b43b3a3839e8686b823353394ca5006d.jpg) no-repeat 50% fixed
}
}
