@-moz-document url-prefix("https://www.youtube.com/") {
/*                   */
/* By: Sllenderbrine */
/*                   */

ytd-page-manager > *.ytd-page-manager {
    background-color: #343434;
}

div#container.style-scope.ytd-masthead {
    color: #343434;
