}

paper-item.style-scope.ytd-guide-entry-renderer {
    border-radius: 10px
}

* {
    border-radius: 10px;
}

#title.ytd-shelf-renderer {
    color: white;
}

ytd-grid-video-renderer #video-title.yt-simple-endpoint.ytd-grid-video-renderer {
    color: white;
}

span {
    color: white;
}

a {
    color: white;
}

#logo-icon-container.ytd-topbar-logo-renderer {
    filter: brightness(600%)
}

yt-icon {
    filter: brightness(600%)
}

ytd-guide-renderer[is-guide-refresh] #sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child) {
    background-color: #232323;
}

span.title.style-scope.ytd-guide-entry-renderer {
    color: white;
}

body {
    background-color: black;
}

#guide-wrapper.ytd-app {
    background-color: black;
}

#content.ytd-app {
    background-color: black;
}

div.ytd-guide-renderer {
    background-color: black;
}

#contentContainer.app-drawer[opened] {
    background-color: black;
}

ytd-guide-renderer.ytd-app {
    background-color: black;
}

ytd-app {
    background-color: black;
}

yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string:visited {
    color: #ff4f00;
}

yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string {
    color: #ff4f00;
}

ytd-watch-flexy[flexy_][is-two-columns_] #columns.ytd-watch-flexy {
    color: white;
    background-color: #141414;
}

#primary.ytd-two-column-browse-results-renderer {
    background-color: #141414;
}

.date.ytd-video-secondary-info-renderer {
    color: white;
}

.title.ytd-video-primary-info-renderer {
    color: white;
}

ytd-expander > * {
    color: white;
}

.content.ytd-video-secondary-info-renderer {
    color: white;
}

.more-button.ytd-video-secondary-info-renderer,
.less-button.ytd-video-secondary-info-renderer {
    color: white;
}

.count-text.ytd-comments-header-renderer {
    color: white;
}

#content-text.ytd-comment-renderer {
    color: #c5c5c5;
}

ytd-commentbox:not([is-backstage-post]) yt-formatted-string#contenteditable-textarea.ytd-commentbox {
    color: white;
}

#contenteditable-textarea.ytd-commentbox[aria-label].ytd-commentbox:empty:before {
    color: #b4b4b4;
}

ytd-button-renderer #button.ytd-button-renderer {
    background-color: #f30;
    border-radius: 20px;
    box-shadow: 3px 3px 1px 1px #0000004a;
    color: white
}

ytd-button-renderer #button.ytd-button-renderer:hover {
    background-color: #3cd2ff;
    border-radius: 20px;
    box-shadow: 3px 3px 1px 1px #4a4a4a52;
    color: white
}

ytd-button-renderer[disabled][is-paper-button] a.ytd-button-renderer {
    background-color: transparent;
}

.more-button.ytd-comment-renderer,
.less-button.ytd-comment-renderer {
    color: #f30;
}

#name.ytd-author-comment-badge-renderer {
    color: #f6ff00;
}

yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string:hover {
    color: #f30;
    box-shadow: 3px 3px 0px 2px #00000036;
}

ytd-subscribe-button-renderer[new-subscribe-color] paper-button.ytd-subscribe-button-renderer {
    background-color: #f30;
    border-radius: 20px;
    box-shadow: 3px 3px 1px 1px #0000004a;
    color: white
}

ytd-subscribe-button-renderer[new-subscribe-color] paper-button.ytd-subscribe-button-renderer:hover {
    background-color: #3cd2ff;
    border-radius: 20px;
    box-shadow: 3px 3px 1px 1px #4a4a4a52;
    color: white
}

ytd-author-comment-badge-renderer:not(.creator) #icon.ytd-author-comment-badge-renderer {
    background-color: #000;
    filter: brightness(120%);
}

app-drawer.ytd-app:not([persistent]).ytd-app #header.ytd-app {
    background-color: #292929;
}

#guide-content.ytd-app {
    background-color: #292929;
}

ytd-toggle-button-renderer.style-default-active[is-icon-button] {
    color: #ff00f2;
}

ytd-sentiment-bar-renderer[activated_] #like-bar.ytd-sentiment-bar-renderer {
    background-color: #ff0048;
}

#container.ytd-sentiment-bar-renderer {
    background-color: #ff00f2;
}

#video-title.ytd-video-renderer {
    color: #fff;
}

#title.ytd-profile-column-user-info-renderer {
    color: #fff;
}

#container.ytd-searchbox {
    background-color: #161616;
    border-top-left-radius: 20px;
    border-bottom-left-radius: 20px;
    border: 0.5px solid #747474;
    box-shadow: inset 0 0.5px 0px #747474;
}

#container.ytd-searchbox input.ytd-searchbox {
    color: white;
}

ytd-thumbnail #thumbnail.ytd-thumbnail {
    border: 2px solid #222;
}

#progress.ytd-thumbnail-overlay-resume-playback-renderer {
    background-color: #ff1871;
}

.ytp-swatch-background-color {
    background-color: #ff1865;
}

.ytp-settings-button.ytp-hd-quality-badge:after {
    background-color: #e5348c;
    filter: brightness(100%)
}

ytd-searchbox[has-focus] #container.ytd-searchbox {
    border: 1px solid #696969;
	animation-name: SearchFade;
	animation-iteration-count: 1;
	animation-timing-function: ease-out;
	animation-duration: 0.25s;
    border: 1px solid #ff4a00;
}

ytd-searchbox #container.ytd-searchbox {
    border: 1px solid #ff4a00;
	animation-name: SearchFadeOut;
	animation-iteration-count: 1;
	animation-timing-function: ease-out;
	animation-duration: 0.25s;
    border: 1px solid #696969;
}

div.ytd-section-list-renderer {
    background-color: #242424;
}

ytd-browse[guide-persistent-and-visible] ytd-playlist-sidebar-renderer.ytd-browse {
    background-color: #2c2c2c;
}

div.ytd-app {
    background-color: black;
}

#content.ytd-playlist-video-renderer:hover {
    background-color: #626262;
}

#video-title.ytd-playlist-video-renderer {
    color: #aeaeae;
}

*::selection {
    background: #ffffff3d;
}

.ytp-videowall-still-info-title {
    color: #ff0059;
    border-radius:0px;
}

.ytp-videowall-still-info-content {
    background-image: linear-gradient(to bottom, rgba(44, 44, 44, .92) 0, #ef088354 100px)
}

.ytp-ce-video-title,
.ytp-ce-playlist-title {
    color: #f38a70;
}

div#container.style-scope.ytd-masthead {
    border-radius:0px;
    border-bottom-right-radius:10px;
    border-bottom-left-radius:10px;
}

h1 {
    border-radius:0px;
}

.ytp-videowall-still-info-duration {
    width:96%;
    position:absolute;
    line-height: 1;
    font-weight: bold;
    font-size: 20px;
    text-align: center;
}

.html5-video-player {
    font-weight: bold;
    padding: 0px;
    text-align: center;
}

::-webkit-scrollbar-track {
  background-color:#55555554;
}


::-webkit-scrollbar-thumb {
  background-color:#636363;
}

::-webkit-scrollbar-thumb:hover {
  background-color:#f00;
}

yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string:visited {
    border-radius: 0px;
}

#video-title.ytd-grid-playlist-renderer {
    color:white;
}

#tabs-inner-container.ytd-c4-tabbed-header-renderer {
    background-color:#818181;
}

#channel-header.ytd-c4-tabbed-header-renderer {
    background-color:#00000054;
    padding: 1px;
}

#title.ytd-channel-featured-content-renderer {
    color:#b9b9d6;
}

.title.ytd-mini-channel-renderer {
    color:#b9b9d6;
}

#title.ytd-vertical-channel-section-renderer {
    color:#b9b9d6;
}

ytd-c4-tabbed-header-renderer[has-channel-art][guide-persistent-and-visible] .banner-visible-area.ytd-c4-tabbed-header-renderer:hover {
    background-image: linear-gradient(to bottom, rgba(254, 254, 254, .92) 0, #ef088354 100px);
}

ytd-c4-tabbed-header-renderer[has-channel-art][guide-persistent-and-visible] .banner-visible-area.ytd-c4-tabbed-header-renderer {
    
}

:not(svg) {
    color:#fff;
}

#contentWrapper.iron-dropdown > * {
    background-color:black;
}

ytd-simple-menu-header-renderer {
 background-color:#0e0e0e;   
}

.exp-kevlar-settings .account-page .account-container {
 background-color:#232323;   
}

.exp-kevlar-settings .account-page #account-page-header-title {
    color:white;
}

.account-page-header {
    color:white;
}

.social-connector .info .social-network-name {
    color:black;
}

.sbsb_a {
    background-color:#3e3e3e;
}

.sbfl_b {
    background-color:#3e3e3e;
    color:#ff3700;
}

.gsfs {
    background-color:#3e3e3e;
}

.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
    background-color:#ff0059;
}

#search-icon-legacy.ytd-searchbox:hover {
    background-color: #242424;
    border: 1px solid #696969;
	animation-name: SearchFade;
	animation-iteration-count: 1;
	animation-timing-function: ease-out;
	animation-duration: 0.25s;
    border: 1px solid #ff4a00;
}

@keyframes SearchFade {
	0% {
          border: 1px solid #696969;
	}
	100% {
        border: 1px solid #ff4a00;
	}
}

#search-icon-legacy.ytd-searchbox {
    background-color: #242424;
    border: 1px solid #ff4a00;
	animation-name: SearchFadeOut;
	animation-iteration-count: 1;
	animation-timing-function: ease-in;
	animation-duration: 0.25s;
    border: 1px solid #696969;
}

@keyframes SearchFadeOut {
	100% {
          border: 1px solid #696969;
	}
	0% {
        border: 1px solid #ff4a00;
	}
}

.ytp-ce-size-640 .ytp-ce-video-title, .ytp-ce-size-640 .ytp-ce-playlist-title {
    margin: 8px;
    display:block;
}

#scrim.app-drawer {
    background:linear-gradient(to right, #ff000030, #ffa50033, #ffff0030, #00ff0029, #0080001f, #0000ff2b, #00ffff36, #ee82ee30, #4b008236)
}

*:hover {
	animation-name: ColorFade;
	animation-iteration-count: 1;
	animation-timing-function: ease-out;
	animation-duration: 0.25s;
    color:#e9e9e9!important;
}

@keyframes ColorFade {
	0% {
		color:#ffffffb5;
	}
	100% {
		color:#e9e9e9;
	}
}

ytd-mini-guide-renderer.ytd-app {
    background-color:#121212;
}

ytd-mini-guide-entry-renderer {
    border-radius: 0px;
    background-color:#393939;
}

ytd-mini-guide-entry-renderer:hover {
	animation-name: SelectFade;
	animation-iteration-count: 1;
	animation-timing-function: ease-out;
	animation-duration: 0.25s;
	background-color:#0000006e;
    color:white!important;
}

@keyframes SelectFade {
	0% {
        background-color:#393939;
	}
	100% {
		background-color:#0000006e;
        color:white!important;
	}
}

ytd-mini-guide-entry-renderer[active_] .title.ytd-mini-guide-entry-renderer {
 color:#ffd6d6;   
}

.title.ytd-mini-guide-entry-renderer {
 color:white;   
}

path {
    fill: #74747475!important;
}

.iv-branding {
    opacity:1;
	animation-name: OpacityFadeOut;
	animation-iteration-count: 1;
	animation-timing-function: ease-out;
	animation-duration: 0.25s;
    opacity:0.4;
}

@keyframes OpacityFadeOut {
	0% {
        opacity:1
	}
	100% {
        opacity:0.4
	}
}

.iv-branding:hover {
	animation-name: OpacityFade;
	animation-iteration-count: 1;
	animation-timing-function: ease-out;
	animation-duration: 0.25s;
    opacity:1
}

@keyframes OpacityFade {
	0% {
        opacity:0.4
	}
	100% {
        opacity:1
	}
}
}

