style.css:
@-moz-document domain("bitchute.com"), domain("disqus.com") {
/* Remove the Carrousel on the Subscribed tab */
#carousel {
    display: none !important
}

/* On "Subscribed" overview page make video title white, the channel name green and the viewcount white */
.video-card-text a {
    color: #FFF !important
}
.video-card-text a[href*="channel"] {
    color: #7CF27C !important
}
.video-views {
    color: white !important
}

/* On main page make ALL, POPULAR, SUBSCRIBED and TRENDING tab-texts red */
.nav-tabs > li > a {
    color: #EF4137 !important
}

/* On main page make ALL, POPULAR, SUBSCRIBED and TRENDING tab-texts white on hover */
.nav-tabs > li > a:hover {
    color: #fff !important
}

/* makes line under active tab white on hover */
.nav-tabs > li.active:hover {
    border-bottom: thin solid #fff
}

.nav-tabs > li > a:hover {
    background: none !important
}

/* color side-toggle for category panel red */
.side-toggle {
    color: #EF4137 !important
}

/* color side-toggle for category panel white on hover */
.side-toggle:hover {
    color: #ffffff !important
}

/* makes social media links on sidebar white */
a:link:hover {
    color: #ffffff !important
}

/* keeps visited links grey, white on hover */
a:visited {
    color: #908F90 !important
}
a:visited:hover {
    color: #ffffff !important
}

/* top-right corner userlink color grey, lighter grey on hover */
#nav-top-menu .user-link a {
    color: grey !important
}
#nav-top-menu .user-link a:hover {
    color: #dedede !important
}

/* colors social media links on main page side-bar and users own social media links if added*/
div.social-list > ul > li:nth-child(1) > a, div.social > a[href*="facebook.com"] {color: #1178F2 !important}
div.social-list > ul > li:nth-child(2) > a, div.social > a[href*="twitter.com"] {color: #00ACED !important}
div.social-list > ul > li:nth-child(3) > a, div.social > a[href*="reddit.com"] {color: #FF4500 !important}
div.social-list > ul > li:nth-child(4) > a, div.social > a[href*="gab.com"] {color: #21CF7A !important}
div.social-list > ul > li:nth-child(5) > a, div.social > a[href*="minds.com"] {color: #FED12F !important}
div.social-list > ul > li:nth-child(6) > a, div.social > a[href*="voat.co"] {color: #7E74BC !important}
div.social > a[href*="twitch.tv"] {color: #643FA7}
div.social > a[href*="youtube.com"] {color: #FF0000}
div.social > a[href*="instagram.com"] {color: #CD2F95}
div.social > a[href*="vk.com"] {color: #4A76A8}
div.social > a[href*="tumblr.com"] {color: #4C6A86}
div.social > a[href*="pinterest.com"] {color: #E60023}
div.social > a[href*="linkedin.com"] {color: #0073B0}
div.social > a[href*="flickr.com"] {color: #0063DC}
div.social > a[href*="skype.com"] {color: #00B1F0}
div.social > a[href*="snapchat.com"] {color: #FFFC00}
div.social > a[href*="deviantart.com"] {color: #00E59B}
div.social > a[href*="discordapp.com"] {color: #7289DA}
div.social > a[href*="tiktok.com"] {color: #00F2EA}
div.social > a[href*="vimeo.com"] {color: #13B8EB }


}

@-moz-document url-prefix("https://www.bitchute.com/video") {
/* remove disqus */
#disqus_thread {
    display: none !important
}

/* remove membership-banner add */
.get-membership-banner.pb15 {
    display: none !important
}

/* colors PLAYING NEXT and RECENT VIDEOS heading backgrounds grey */
h2.sidebar-heading {
    background-color: #3735387d !important;
    margin-bottom: 1px !important;
    color: #908F90 !important
}

/* recolored PLAYING NEXT on/off slider background */
.switch input:checked + .slider {
    background-color: #7CF27C;
}
.switch .slider {
    background-color: #EF4137
}


/* Remove border under video title */
#page-bar {
    border-bottom: 0px !important
}

/* remove watch tab from videopage */
.nav-tabs > li.active > a,
.nav-tabs > li > a,
.nav {
    display: none !important
}

/* widen column */
.col-xs-12 {
    width: 100% !important
}

/* adjust player size to 80% and correct it's position */
.plyr--full-ui {
    max-width: 80% !important;
    max-height: 80% !important;
    margin: 0 auto !important;
    top: -50px !important
}
/* Make video player full width if browser window is >1200px */
@media (max-width: 1200px) {
    .plyr--full-ui {
        max-width: 100% !important;
        max-height: 100% !important
    }
}

/* centers video title, smaller font */
#video-title,
#page-bar .page-title {
    text-align: center!important;
    font-size: 17px !important;
    /* position:relative;
    z-index: 1;
    top:555px!important*/
}

/* color side-toggle for category panel grey */
.side-toggle {
    color: #908F90 !important
}

/* color side-toggle for category panel white on hover */
.side-toggle:hover {
    color: #dedede !important
}

/* makes tags-bar invisible and 50px height */
.tags ul {
    height: 50px !important;
    opacity: 0
}

/* makes tags-bar visible on hover */
.tags ul:hover {
    height: 50px !important;
    opacity: 1
}

/* adjust video-information tab height and add 1px paddin on top */
.video-information,
.col-sm-6 {
    height: 35px !important;
    padding-top: 1px !important
}

/* make active share-button and share-bar grey */
.sharing-drop-open,
.sharing-bar {
    background: #3735387d!important
}

/* remove border of like-button when active */
.video-like.active {
    border: none !important
}

/* Notifications on/off bell color green when notifications on */
a[id$='-on'] {
    color: #7CF27C !important
}

/* Notifications on/off bell color red when notifications off */
a[id$='-off'] {
    color: #EF4137 !important
}

/* Color view count */
#video-view-count,
div > svg {
    color: #7CF27C !important
}

.video-like {
    color: #7CF27C !important
}

/* Color the "Subscribe" button green when subscribed */
.btn.btn-default {
    background-color: #7CF27C !important
}

/* Color subscriber count green */
p.subscribers {
    color: #7CF27C !important
}

/* Color the video author red */
.channel-banner .details .name,
p.name > a,
p.owner > a {
    color: #EF4137 !important
}
/* Watch Live-link green */
p.name > span > a {
    color: #7CF27C !important
}


/* hides video info-container, shows on hover if browser window is >1200px */
@media (max-width: 1200px) {
    div.video-container > div:nth-child(2) {
        opacity: 0 !important
    }

    div.video-container > div:nth-child(2):hover {
        opacity: 1 !important
    }

    .video-publish-date {
        opacity: 0 !important
    }
    .video-publish-date:hover {
        opacity: 1 !important
    }
}

/* colors social media links on video page sharing-bar*/
.sharing-bar.text-right.sharing-bar-open > a:nth-child(1)[href*="facebook.com"] {color: #1178F2 !important}
.sharing-bar.text-right.sharing-bar-open > a:nth-child(2)[href*="twitter.com"] {color: #00ACED !important}
.sharing-bar.text-right.sharing-bar-open > a:nth-child(4)[href*="reddit.com"] {color: #FF4500 !important}
.sharing-bar.text-right.sharing-bar-open > a:nth-child(5)[href*="voat.co"] {color: #7E74BC !important}

}

