style.css:
@-moz-document url-prefix("https://apps.derstandard.at/"), url-prefix("https://apps.derstandard.de/"), url-prefix("https://about.derstandard.at/"), regexp("https://www\\.derstandard\\.(at|de)/.*"), regexp("https://www\\.derstandard\\.(at|de)/(?!404).*") {
/************ B A S I C  S E T T I N G S *************/
/* Springe zum Editieren zur Zeile 165 */
@media only screen and (max-width: 1920px) and (min-width: 960px),
(max-width: 1920px) and (-ms-high-contrast: none) and (min-width: 960px),
(max-width: 1920px) and (-ms-high-contrast: active) and (min-width: 960px) {

	:root {
		--site--core-maxwidth: 1920px;
		--column: 1.5rem !important;
		--gap: 0.125rem !important;
		--grid-column: .819em;
		--grid-gap: 1em !important;
		--js-active-layout: desktop!important;
		--js-active-sitebar: false!important;
		--row-gap: 6px!important;
		--single-block-and-gap: 6.625rem;
		--single-column-and-gap: 2.25rem;
		--site--header-margin: 0 1.5rem!important;
		--site--header-margin-bottom: 1rem!important;
		-moz-text-size-adjust: none;
		-ms-text-size-adjust: none;
		-o-text-size-adjust: none;
		-webkit-text-size-adjust: none;
	}


	html {
		-webkit-text-size-adjust: 100%;
		font-kerning: normal;
		text-rendering: optimizeLegibility
	}

	body {
		margin: 0;
		min-height: 100vh!important;
		overflow-x: hidden!important;
		overflow-y: scroll!important;
		--grid-block-width: calc(4.4rem + 0.718vw) !important;
		--grid-block-height: calc(7rem + 1.1vw) !important;
		--site--header-minheight: calc(6.125rem + 0.125vw) !important;
		--site--header-maxheight: calc(9.70rem + 0.125vw) !important;
		--site--core-margin-left: calc(.125rem + 4vw) !important;
		--site--core-margin-right: calc(2rem + 4vw) !important;
		--site--fixed-header-height: 5rem !important;
		--letter-spacing: 0.015rem;
	}

	a {
		background-color: transparent
	}

	summary {
		display: list-item
	}

	p {
		font-size: 16px;
		margin-bottom: 1em!important;
		text-align: justify;
		word-spacing: .0125em
	}

	h1 {
		font-size: 33px;
		line-height: 52px;
		margin-bottom: 1.75em;
		word-spacing: .03em
	}

	.title {
		font-size: 42px;
		line-height: 66px;
		margin-bottom: 1.75em
	}

	h2 {
		font-size: 26px;
		line-height: 42px;
		margin-bottom: 1.063em;
		margin-top: 2.813em
	}

	h3 {
		font-size: 20px;
		font-weight: 600;
		line-height: 33px;
		margin-bottom: .688em;
		margin-top: 1.75em
	}

	h4 {
		font-size: 16px;
		font-weight: 600;
		line-height: 28px;
		margin-bottom: .438em
	}

	ul,
	ol,
	blockquote,
	pre,
	.alert,
	.note,
	.box,
	.footnotes {
		margin-bottom: auto!important
	}

	li {
		margin-bottom: .688em
	}

	blockquote {
		border-color: rgba(0, 0, 0, 0.15);
		border-style: solid;
		border-width: 0 0 0 .438em;
		padding-left: 17px
	}

	sub,
	sup {
		padding: 0 1px
	}

	code,
	pre,
	kbd {
		font-size: .938em
	}

	.alert,
	.note,
	.box {
		padding: 1.063em
	}

	.footnotes {
		border-top: 1px dotted rgba(0, 0, 0, 0.15);
		font-size: 13px;
		line-height: .938em;
		padding-top: 1.063em
	}

	.footnotes p {
		margin-bottom: 1.063em
	}

	ul ul,
	ul ol,
	ol ul,
	ol ol,
	blockquote.right p,
	blockquote.left p,
	.alert :last-child,
	.note :last-child,
	.box :last-child,
	.footnotes :last-child {
		margin-bottom: 0
	}

	article h1 b,
	article h1 strong,
	article h2 b,
	article h2 strong,
	article h3 b,
	article h3 strong,
	article h4 b,
	article h4 strong {
		font-weight: unset!important
	}

	/**************** N U R für den D E S K T O P  ********************/
	/**                    ab hier editieren                        **/
	/**@media only screen and (max-width: 1920px) and (min-width: 960px) **/
	/* Basic Fontsize */
	body {
		/* font-size: 1.5rem !important; */
		font-size: 1.42rem !important;
		/* line-height: 1.618em !important; */
		line-height: 1.62em !important;
		padding-top: 4.5em;
	}

	/* Layout-Breite für Artikel von original 615 auf 690 gesetzt*/
	.layout {
		/* --story-width: 615px; */
		--story-width: 690px;
		/*--story-negativespace: calc((100% - var(--story-width)) / 2) !important; */
	}

	/* hellerer Standard-Hintergrund */
	[data-theme="default"],
	.theme-default,
	[data-theme="user"],
	.theme-user {
		--theme-background: whitesmoke !important;
	}

	/************ S C A L I N G ************/
	IMG,
	svg,
	.grid article,
	.story-article [data-section-type="image"] figure[data-type="image"] {
		padding: calc(50% - 50vw) !important;
	}


	.site--header-next {
		font-size: calc(.938em - 0.1vw) !important;
		margin-top: calc(-.125em + 0.1vw) !important;
		line-height: calc(1.125em + 0.3vw) !important;
		padding-right: calc(1em + 0.1vw) !important;
		margin-bottom: calc(.2em + 0.1vw) !important;
	}

	header h1,
	header h2 {
		font-size: calc(1.063em + 0.15vw) !important;
		line-height: calc(1.063em + 0.05vw) !important;
	}

	header .teaser-subtitel,
	header p {
		font-size: calc(1em + 0.0175vw) !important;
		line-height: calc(1.188em + 0.0175vw) !important;
	}

	/* Story */
	h2 .article-kicker,
	.story p,
	.chronological h1,
	.story h2,
	.story h3 {
		font-size: calc(0.938em + 0.125vw) !important;
		line-height: calc(1.125em + 0.6vw) !important;
	}

	/* Forum */
	.communityform-input-text [type="text"],
	.communityform-input-textarea textarea,
	.upost-content {
		font-size: calc(1.125rem + 0.3vw) !important;
		line-height: calc(1.618rem + 0.5vw) !important;
	}

	/* Blocksatz */
	p {
		text-align: justify;
		/* text-align: justify !important; */
	}

	p .story-article {
		text-align: left !important;
		/* text-align: justify !important; */
	}

	figure figcaption p {
		text-align: justify !important;
		/* text-align: left !important; */
	}

	.teaser-subtitle {
		/*text-align: justify !important; */
		text-align: left !important;
	}

	/* Leseleiste bottom */
	@supports (height: 100vh) {
		body {
			background: linear-gradient(to right top, #343434 50%, var(--theme-background) 50%);
			background-size: 100% calc(100% - 100vh + 90px);
			background-repeat: no-repeat;
			z-index: 1;
		}

		body:before {
			content: '';
			position: fixed;
			bottom: .33em;
			top: 0;
			width: 100%;
			z-index: 0;
			/* background: whitesmoke; */
			background: var(--theme-header-background, var(--theme-background)) !important
		}
	}

	/****************** H E A D E R ********************/
	.site--header-next > div {
		margin: 0 2rem 0 5rem !important;
		--site--header-maxwidth: 1920px !important;
	}


	.compact {
		--site--header-minheight: 80px;
		--site--header-maxheight: 80px !important;
		max-height: 80px !important;
		opacity: .98;
	}

	.compact:hover {
		opacity: 1;
	}

	.site-guide-toggle,
	.login,
	.brand {
		margin-bottom: 0em !important;
	}

	.login {
		padding: 0;
	}

	.site--header-next:not(.compact) {
		/** background-color: rgb(253, 223, 201) !important; **/
		--site--header-brandingwidth: 15.625em;
		--site--header-guideoffset: 2.125em;
		--site--header-brandingheight: 80px;
		opacity: 0.98;
	}

	.site--header-next:not(.compact):hover {
		opacity: 1 !important;
	}

	.site--header-next.compact .site-guide-toggle {
		height: 80px;
	}

	.site--header-next:not(.compact) .main {
		height: 40px;
	}

	/****************** P A G E ********************/
	/* Frontseite vollflächig */
	.layout {
		--template-columns: content repeat(12, minmax(69px, 90px)) sitebar minmax(1px, 690px) !important;
	}

	main,
	.layout {
		margin-top: calc(4rem + 1.5vw) !important;
	}

	/***************** L A Y O U T *********************/
	.layout .story,
	.story-community {
		grid-column: content / span 12 !important;
		margin-top: 1em;
	}

	/* Ansicht Chronologie */
	.chronological {
		grid-column: content / span 6;
	}

	.grid,
	.overview-readmore,
	.overview-nomore,
	.grid section[data-type="date"] {
		--template-columns: repeat(12, 1fr) !important;
		z-index: 2;
	}

	.layout .site-sitebar{
		display: none;		
	}

	.site-footer-title,
	.site-footer p {
		text-align: center!important;
	}

	/***************** E N D E *********************/
	/*********** H A U P T - N A V I G A T I O N ************/
	.site--header-next > div > .site-guide-next {
		font-size: 1.45rem;
	}

	nav .main,
	.site-header-main {
		text-transform: none !important;
		font-size: 1em;
		/*font-family: "Matilda", serif; */
	}

	.main ul li a {
		line-height: -1.5rem !important;
		text-transform: uppercase !important;
		letter-spacing: 0.015rem !important;
	}

	div.teaser div.highlights-body p {
		font-size: 0.75em;
	}

	.site--header-next > div > .site-guide-next {
		top: 8rem;
		--site--guide-minheight: 66vh;
		--site--guide-maxheight: 66vh;
		--site--guide-minwidth: 20rem;
		--site--guide-maxwidth: 30rem;
	}

	.site-guide-next > div > div > nav {
		scrollbar-width: unset !important;
	}

	.js-site-portal-nav-root,
	.site--portal--search {
		font-size: 1em !important;
		line-height: 1.2rem;
	}


	.site-guide-next > div > div > nav h1,
	.site-guide-next > div > div > nav h {

		font-size: 1.5rem !important;
		line-height: 2rem !important;
		margin: 0;
	}
	.site-guide-next > div > div > nav p {
		line-height: 12px;
	}

	.site-guide-next > div > div > nav ul > li a {
		font-size: 1em!important;
		line-height: .6rem !important;
	}

	.site-guide-next > div > div h3,
	header-next > div > .site-guide-next > div > div h3 {
		font-size: 1.4em;
	}

	.site-guide-next > div > div > nav.site--portal--nav-social a {
		padding: 0 !important;
	}

	.site-guide-next > div > div {
		font-weight: lighter;
	}

	ul .js-site-portal-nav-root li a,
	.js-site-portal-nav p {
		font-size: .75em !important;
	}

	.site--header-next:not(.compact) .main ul ul a {
		color: currentColor;
		display: block !important;
		font-size: 0.938em !important;
		text-decoration: none !important;
	}

	.site--header-next button:hover {
		background-color: rgba(245, 245, 245, .5);
	}

	.site--header--usermenu,
	.site-header-fixed-visible {
		line-height: 17px !important;
		z-index: 17;
	}

	.site--header-next:not(.compact) .main ul ul a:hover,
	.site--header--usermenu ul li a:hover {
		color: var(--theme-primarytint) !important;
		background-color: var(--theme-header-background, var(--theme-background)) !important;
	}

	.site--header--usermenu {
		width: 15em !important;
	}


	/* Beschreibungen verstecken*/
	.site--header-next .site-guide-toggle button .descriptive-txt,
	.site--header-next .login button .descriptive-txt,
	.site--header-next .search .button .descriptive-txt {
		display: none !important;
	}


	/***************** E N D E *********************/
	/********** S E I T E N I N H A L T ************/
	figurecapton p,
	.story-article ul,
	.story-article li {
		font-size: 1em !important;
	}

	.teaser-postingcount {
		padding: 2px 0 8px 0 !important;
	}

	.teaser-postingcount IMG:hover,
	.teaser-title:hover {
		opacity: 0.9;
		background-color: transparent;
	}

	.teaser-postingcount IMG,
	.teaser-title {
		opacity: 1;
	}

	header .teaser-title {
		font-size: 1.188em !important;
		line-height: 1.188em !important;
	}

	.teaser-subtitle {
		/*display: block!important; */
	}

	.teaser-titel,
	.teaser-subtitle,
	.teaser-kicker,
	.storylabels {
		padding: 1px 0 0 !important;
	}

	.layout p:first-of-type:first-letter,
	.article-kicker:first-of-type:first-letter {
		font-size: 1.125em !important;
		font-weight: inherit !important;
		letter-spacing: 0.0075rem;
	}

	h1 .teaser-title {
		letter-spacing: 0.0075rem;
	}

	.grid nav[data-db="n1"] {
		margin-left: calc(var(--site--core-margin-right) + 1);
		margin-right: calc(var(--site--core-margin-left) + 1);
	}

	.grid nav h2 {
		text-align: center !important;
		padding-left: .5em;
	}

	/* Artikel-Überschriften positionieren */
	.article-header {
		text-align: center;
		margin: 0 10rem 0 0 !important;
	}

	.article-kicker,
	.article-title,
	.article-subtitle,
	.article-pubdate {
		text-align: center;
	}

	.article-subtitle {
		font-size: 1.25 !important;
	}

	.grid article h1 {
		margin: -0.5ex 0 0;
	}

	.grid nav[data-db="n1"] ul {
		padding-left: 1em !important;
		margin-bottom: -1em !important;
	}
	.article-header .article-title {
		font-size: 33px !important;
	}

	.article-header .article-kicker {
		margin-top: -1em!important;
	}

	.grid article.is-opinion {
		/*	--tile-background: var(--theme-opinion-background) !important; */
		--tile-background: transparent !important;
	}

	.storylabels,
	.article-origins,
	p.article-pubdate,
	p.article-postingcount {
		margin-top: 1.25rem !important;
	}

	.article-origins {
		font-size: 1.063em !important;
		padding-right: 0.5em;
		font-weight: 600 !important;
	}

	p.article-pubdate {
		font-size: 1em !important;
		padding: 0 1em 0 0.2em;
	}

	p.article-postingcount button {
		font-size: 1em !important;
		font-weight: 400 !important;
		color: rgb(236, 0, 140) !important;
	}

	.article-postingcount {
		text-decoration-line: underline;
		text-decoration-color: rgb(236, 0, 140) !important;
	}

	.article-postingcount:hover {
		color: rgb(236, 0, 140) !important;
	}

	.article-header .article-subtitle ~ * {
		vertical-align: top !important;
	}

	.story .story-article [data-section-type="supplemental"] {
		font-size: .9em !important;
	}


	.layout .story .story-tool {
		top: 60px;
	}

	.story-tool-forum {
		padding-left: 0px;
		display: none !important;
	}

	.container-fluid {
		display: none !important;
	}

	/************ B I L D E R **************/
	.story-article [data-section-type="image"] {
		display: flex !important;
		flex-wrap: wrap !important;
	}

	.story-article [data-section-type="image"] figure[data-type="image"],
	article [data-section-type][data-position="left"]:not([data-size="auto"]),
	.story-article [data-gallery-type="slideshow"][data-section-type="gallery"] {
		--storysection-width: var(--storysection-width-medium) !important;
		--storysection-float: center !important;
		min-width: 570px !important;
		width: 570px !important;
		max-width: 570px !important;
		margin: 0 auto 0 auto !important;
	}

	article [data-section-type][data-size="small"]:not([data-position="auto"]):not([data-position="center"]) {
		--storysection-width: 100% !important;
	}

	.layout .story .story-article [data-gallery-type="secondary"][data-section-type="gallery"] .gallery-summary {
		font-size: 1.3rem;
		line-height: 100%;
		opacity: 0.7;
		text-align: center;
	}

	article [data-section-type][data-position="left"]:not([data-size="auto"]) {
		--storysection-width: var(--storysection-width-medium) !important;
		--storysection-float: center !important;
	}

	.story-article [data-section-type][data-size="medium"]:not([data-position="auto"]):not([data-position="center"]) {
		--storysection-width: none !important;
		--storysection-float: center !important;
	}

	.story-article .article-header > figure:first-child figcaption,
	.story-article [data-section-type] figure figcaption {
		text-align: justify;
		padding: 0.8rem 0 0;
	}

	.story-article [data-section-type] figure figcaption {
		margin: 0 !important;
	}

	.story-article [data-section-type] figure figcaption + footer {
		text-align: left !important;
		padding: 1rem 0 0;
		opacity: 0.75 !important;
	}

	.story-article .swiper-slide,
	.story-article .swiper-slide-active {
		padding-top: 14px;
	}

	.swiper-button-prev,
	.swiper-button-next {
		margin-top: -5rem !important;
	}

	.story-article .swiper-pagination,
	.swiper-pagination-progress {
		display: none;
	}

	.story-article .swiper-container {
		border-bottom: 0 !important;
	}

	.story-article [data-section-type="image"] figure[data-type="image"] picture {
		max-height: 100% !important;
		max-width: 100% !important;
		vertical-align: middle !important;
		border: 0 none;
	}

	.layout .story .story-article .article-header > figure:first-child figcaption,
	.layout .story .story-article [data-section-type] figure figcaption {
		font-size: 0.875em;
		line-height: normal;
	}

	IMG:hover {
		opacity: 0.92;
		-moz-box-shadow: 0 0 10px #ccc;
		-webkit-box-shadow: 0 0 10px #ccc;
		box-shadow: 0 0 10px #ccc;
	}

	.pswp__img IMG:hover {
		opacity: 1;
	}

	/* Geöffnete Bilder-Galerie */
	.pswp__caption__center {
		text-align: justify !important;
		font-size: 1em !important;
		line-height: 28px !important;
	}

	/* RUNDE Ecken */
	nav,
	nav a,
	div.storylabels span,
	div.overview-readmore a,
	div[data-section-type='image'] img,
	section[data-type='date'] div,
	article[data-type='story'],
	article[data-type='story'] a,
	article[data-type='story'] img,
	article[data-type='story'] figure,
	article[data-type='live'],
	article[data-type='live'] a,
	article[data-type='live'] img,
	article[data-type='live'] figure {
		border-radius: 3px !important;
	}

	article[data-type='story'] header,
	article[data-type='live'] header {
		border-radius: 0 0 3px 3px !important;
	}

	/************** F E A T U R E S **********************/
	/* Werbung ausblenden */
	.supporter-widget {
		display: none;
	}
	.story-recommended {
		display: none;
	}
	.story-community-header {
		display: none;
	}

	/***********************/
	/* Social Media Links */
	.socialsharing,
	.supporter-widget,
	.story-recommended {
		display: none !important;
	}

	/***********************/
	.story-tool-sharetwitter,
	.story-tool-sharewhatsapp,
	.story-tool-sharefacebook {
		/* display: none !important; */
	}

	/***********************/
	.upost-sharemenu-facebook,
	.upost-sharemenu-whatsapp,
	.upost-sharemenu-twitter {
		display: none !important;
	}

	/***********************/
	div[data-section-type="newsletter"] {
		display: none !important;
	}

	.story .story-article figure[data-provider="unknown"] {
		
	}
	
	/*IFrames*/
	/* Iframe-Ausnahmen: Videos erlauben */
	.story-article [data-section-type="video"] figure[data-type="video"] iframe,
	.story-article [data-section-type="html"] figure[data-provider="instagram"] iframe,
	.story-article [data-section-type="html"] figure[data-provider="google"] iframe,
	.story-article [data-section-type="html"] figure[data-provider="unknown"] iframe {
		margin: 0;
		padding: 0;
		border: none;
		width: 90%!important;
		display: block !important
	}

	/* APA erlauben */
	.apa-svg svg {
		display: block !important;
	}



	/* W E R B U N G */
	ad-container[data-ad-active-format*='billboard'],
	ad-container[data-ad-active-format*='sitebar'],
	ad-container[data-ad-large*='forum_sidebar'].temp-visible {
		margin: 0;
		padding: 0;
		border: none;
		width: 1px!important;
		height: 1px!important;
		display: none !important;
	}

	/* S O N D E R W E R B U N G */
	ad-container.ad-container-used,
	ad-container.ad-nativead {
		margin: 0;
		padding: 0;
		border: none;
		width: 1px!important;
		height: 1px!important;
		display: none !important;
	}


	.grid .tile-ad.ad-active.native-ad h1 {
