html {
    min-height: 100vh;
}
body {
    background-color: #181818;
    background-image: none;
    background-size: cover;
    background-attachment: fixed;
    background-repeat: no-repeat;
    overflow: auto;
}
body,
body span,
body p,
body a,
body h1,
body h2,
body h3,
body h4,
body h5,
body h6,
body h7,
body h8,
body div {
    font-family: spotify-circular, spotify-circular-cyrillic, spotify-circular-arabic, spotify-circular-hebrew, Helvetica Neue, Helvetica, Arial, Hiragino Kaku Gothic Pro, Meiryo, MS Gothic, sans-serif !important;
}
body::-webkit-scrollbar {
    width: 15px;
}
body::-webkit-scrollbar-track {
    background: #181818;
}
body::-webkit-scrollbar-thumb {
    background: #5e5e5e;
}
body::-webkit-scrollbar-thumb:hover {
    background: #505050;
}
body #dropdown-button-53 ul:last-child {
    border-bottom: none;
}
body .loading {
    background: url("https://a-v2.sndcdn.com/assets/images/loader-db80c58.gif") no-repeat center center;
}
body .dropdownMenu {
    z-index: 9999;
}
body .dropdownMenu:not([style*="width: auto"]) {
    top: unset !important;
    left: 230px !important;
    bottom: 91px;
}
body h2.selectionModule__titleText {
    font-size: 28px;
    font-weight: 600;
    color: #fff;
}
body .sc-tag {
    color: #fff !important;
}
body a.playableTile__heading,
body .userBadge__usernameLink {
    text-align: left;
    font-weight: 400;
    font-size: 14px;
    color: #fff !important;
}
body a.playableTile__heading:hover,
body .userBadge__usernameLink:hover {
    text-align: left;
    font-size: 14px;
    color: #fff;
}
body a.playableTile__usernameHeading,
body .playableTile__usernameHeading {
    color: #fff;
    opacity: 0.6;
    font-weight: 200;
    text-align: left;
}
body a.playableTile__usernameHeading:hover {
    opacity: 1;
    color: #fff;
    text-decoration: underline;
}
body .selectionModule,
body .sc-border-light-right,
body .sc-border-light-bottom,
body .sc-border-light-top {
    border: none;
}
body .tileGallery__sliderPeekForward {
    display: none !important;
}
body .tileGallery__sliderPanel {
    display: flex;
    flex-wrap: wrap;
}
body .image__lightOutline .image__full {
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
}
body .moreActions__group:not(:first-child) {
    border-top: 0 !important;
}
body .moreActions {
    border: 1px solid rgba(0, 0, 0, 0.15);
    border-radius: 0.25rem;
    background-color: #282828;
    padding: 5px 0;
}
body .moreActions .moreActions__button.sc-button-medium {
    border: none;
    background-color: transparent;
    color: #b3b3b3;
    cursor: default;
    font-size: 14px;
}
body .moreActions .moreActions__button.sc-button-medium:before {
    filter:brightness(3);
}
body .moreActions .moreActions__button.sc-button-medium:hover {
    color: #fff;
    background-color: #333;
}
body .moreActions .moreActions__button.sc-button-medium:hover:before {
    filter:brightness(6);
}
body #app {
    display: flex;
    height: calc(100vh - 90px);
}
body #app header {
    width: 230px;
    height: calc(100vh - 90px);
    box-sizing: border-box;
    background: transparent;
}
body #app header .header__inner {
    background-color: #121212;
    display: flex;
    flex-direction: column;
    height: 100%;
    width: 230px;
    box-sizing: border-box;
}
