}
body #app header .header__inner .header__logo .header__logoLink {
    width: 100%;
    height: 65px;
    background-image: none;
    background-color: #fff;
    -webkit-mask-size: contain;
    -webkit-mask-repeat: no-repeat;
    -webkit-mask-position: center;
    -webkit-mask-image: url("https://i.imgur.com/XwAEKIA.png");
}
body #app header .header__inner .header__right {
    position: relative;
    height: 85px;
}
body #app header .header__inner .header__right .header__userNavActivitiesButton {
    position: absolute;
    top: -40px;
    right: -46px;
}
body #app header .header__inner .header__right .header__userNavMessagesButton {
    position: absolute;
