}
body #app header .header__navWrapper .header__navMenu li {
    width: 100%;
}
body #app header .header__navWrapper .header__navMenu li a {
    border: none;
    color: #B3B3B3;
    font-weight: 700;
    box-sizing: border-box;
    background-color: transparent;
    height: 40px;
    line-height: 40px;
    padding: 0 24px;
    text-align: left;
    font-size: 14px;
    width: 100%;
}
body #app header .header__navWrapper .header__navMenu li a.selected {
    position: relative;
    left: -24px;
}
body #app header .header__navWrapper .header__navMenu li a.selected:before {
    border-left: 4px solid #1ed760;
    padding: 7px 10px;
    content: "";
}
body #app .l-container {
    padding: 0;
    margin: 0;
    width: 100%;
    box-sizing: border-box;
}
body #app .l-container.l-content {
    width: calc(100% - 230px);
