style.css:
@-moz-document domain("4sucres.org") {
/* -------------------- ACCEUIL -----------------*/

/* Fond */
body {
    background-color: #464646;
    color: #eceff4
}

/*TEXT BOX*/
.form-control:focus {
    color: #eceff4;
    background-color: #606060;
    border-color: #303030;
    outline: 0;
    box-shadow: none;
}
/*TOPICS*/
.discussion-previews .discussion-preview:nth-child(odd) {
    background-color: #464646;
    color: #e5e9f0;
}
.d-inline d-lg-block {
    background-color: transparent!important;
}
/* A Gauche */
.nav-pills .nav-link,
.nav-pills .nav-link.active {
    color: #eceff4;
    background: #464646;
}
/* NOTIF */
.text-primary {
    color: #909090!important
}
/*HR*/
hr {
    color: #505050;
    background-color: #505050
}
hr {
    border-color: #505050;
}
/*NAVBAR*/
.navbar {
    background-color: #373737;
}
/*HOVER TOPIC*/
section.discussion-previews .discussion-preview:nth-child(odd):active,
section.discussion-previews .discussion-preview:nth-child(odd):focus,
section.discussion-previews .discussion-preview:nth-child(odd):hover {
    background-color: #606060;
}
section.discussion-previews .discussion-preview:nth-child(2n):active,
section.discussion-previews .discussion-preview:nth-child(2n):focus,
section.discussion-previews .discussion-preview:nth-child(2n):hover {
    background-color: #606060;
}
/*Search*/
.navbar input {
    background-color: #464646;
    color: #eceff4;
    border-color: #464646;
}
.d-inline {
    background-color: transparent;
    color: #eceff4;
    border-color: #464646;
}
.navbar button {
    background-color: #464646;
    color: #eceff4;
    border-color: #464646;
}
hr.my-2{
border-color: #414141}
/*New Discussion*/
.btn-primary {
    color: #fff;
    background-color: #464646;
    border-color: #464646;
}
section.discussion-previews .discussion-preview:nth-child(odd) {
    background-color: #464646;
    color: #e5e9f0;
}
section.discussion-previews .discussion-preview:nth-child(2n) {
    background-color: #373737;
    color: #e5e9f0;
}
.btn-primary:hover {
    color: #fff;
    background-color: #202020;
    border-color: #202020;
}

.bg-theme-primary {
    background-color: #464646!important;
}

#notifications>ul .notification-item:nth-child(odd):active, #notifications>ul .notification-item:nth-child(odd):focus, #notifications>ul .notification-item:nth-child(odd):hover {
    background-color: #505050;
}
#notifications>ul .notification-item:nth-child(odd) {
    background-color: #464646;
    color: #e5e9f0;
}

/*NOTIF*/
#notifications > ul {
    padding: 0;
    min-width: 300px;
    max-width: 100%;
    background-color: #464646;
    color: #eceff4;
}
/* Cats a droite */
.btn-outline-primary {
    color: #909090;
    border-color: #909090;
}
.nav-pills .nav-link:active,
.nav-pills .nav-link:focus,
.nav-pills .nav-link:hover {
    color: #eceff4;
    background: #404040;
}
/*Texte pin*/
.text-muted {
    color: #909090;
}
/* PIN */
.sidetag,
blockquote {
    background-color: #373737;
    border: 1px solid #303030;
}
/*passe dessus*/
.btn-outline-primary:hover {
    color: #fff;
    background-color: #909090;
    border-color: #909090;
}
/*Border Discussion*/
.card,
.card .card-header {
    background-color: #464646;
    color: #eceff4;
}
/* BG Account */
.account-block .account-details {
    padding: 10px 15px;
    height: 100%;
    background-color: #464646;
}
/* Notif */
.notification .notification-icon {
    -webkit-transition: all .2s ease;
    transition: all .2s ease;
    color: #eceff4;
    background-color: #464646
}
/* -------------------- POST -----------------*/
/*POST #1*/
section.discussion .post-container:nth-child(odd) {
    background-color: #464646;
    color: #e5e9f0;
}
section.discussion .post-container:nth-child(odd) .post :not(blockquote) > hr {
    border-color: #373737;
}
section.discussion .post-container:nth-child(odd) .post {
    border-bottom: 1px solid #464646;
}
/*POST #2*/
section.discussion .post-container:nth-child(2n) .post {
    border-bottom: 1px solid #373737;
    background-color: #373737
}
section.discussion .post-container:nth-child(2n) .post :not(blockquote) > hr {
    border-color: #373737;
}
/*QUOTE 1*/
section.discussion .post-container:nth-child(odd) .post .quote,
section.discussion .post-container:nth-child(odd) .post blockquote {
    background-color: #373737;
}
/*QUOTE 2*/
section.discussion .post-container:nth-child(2n) .post .quote,
section.discussion .post-container:nth-child(2n) .post blockquote {
    background-color: #464646;
}
/*Text muted top right*/
.text-muted {
    color: #c3c3c3!important;
}
/*QUOTE BUTTON*/
section.discussion .post-container .post .action-buttons a {
    color: #909090;
}
/*Verfier ma connerie */
.btn-secondary {
    color: #fff;
    background-color: #464646;
    border-color: #464646;
}
.btn-secondary:hover {
    color: #fff;
    background-color: #303030;
    border-color: #303030;
}
/*TM*/
.editor-textarea {
    border-top-left-radius: 0;
    border-top-right-radius: 0;
    color: #eceff4;
    background-color: #464646;
    border-color: #464646;
    border-top: none;
    margin-bottom: 15px;
}

/* Change Topic */
.select2-container .select2-selection .select2-selection__rendered {
    border-color: #464646;
}
.select2-container .select2-selection .select2-selection__rendered {
    border-color:#373737;
}

.sel
/*BUTTONS*/

.editor-buttons button:hover {
    background-color: #373737!important;
    color: #eceff4;
}


.editor-buttons button:hover {
    background-color: #393939!important;
    color: #eceff4;
}
.btn-toolbar {
    display: -webkit-box;
    background-color: #464646;
    border: 1px solid #373737
}
.editor-buttons button {
    margin-right: 5px;
    cursor: pointer;
    color: #eceff4;
    background-color: #464646;
    border: 1px solid #464646;
}
.btn-light:hover {
    color: #212529;
    background-color: #909090;
    border-color: #909090;
}
/*Risibank*/
.nav-tabs .nav-item.show .nav-link,
.nav-tabs .nav-link.active {
    color: #ffffff;
    background-color: #707070;
    border-color: #dee2e6 #dee2e6 #2e3440;
}
.img-thumbnail {
    padding: .25rem;
    background-color: #373737;
}
/*CARD*/
section.discussion-response {
    background-color: #373737;
    border-color: #303030;
}
.card-footer {
    padding: .75rem 1.25rem;
    background-color: #373737;
    border-top: 1px solid rgba(0, 0, 0, .125);
}
/*Highlight 1*/
section.discussion .post-container:nth-child(2n) .post.highlight {
    background-color: #505050;
    border: 1px solid #373737;
}
/*Highlight 2*/
section.discussion .post-container:nth-child(odd) .post.highlight {
    background-color: #505050;
    border: 1px solid #373737;
}
section.discussion .post-container:nth-child(2n) .post.highlight {
    background-color: #505050;
    border: 1px solid #373737;
}
/*Contours*/
.card,
.card .card-header {
    background-color: #373737;
    color: #505050;
}
/*LOCKED*/
.alert {
    background-color: #373737;
    color: #eceff4;
    border-color: #373737;
}
/*TRAVELER*/
.page-item.active .page-link {
    z-index: 1;
    color: #e5e9f0;
    background-color: #404040;
    border-color: #303030;
}
.page-link {
    color: #eceff4;
    background-color: #373737;
    border: 1px solid #303030;
}
.page-link:hover {
    z-index: 2;
    color: #e5e9f0;
    text-decoration: none;
    background-color: #404040;
    border-color: #404040;
}
.page-item {
    background-color: #404040
}
/*Disabled*/
.page-item.disabled .page-link {
    color: #919191;
    pointer-events: none;
    cursor: auto;
    background-color: #303030;
    border-color: #303030;
}
/*SPOILER*/
div.spoiler {
    border-radius: 4px;
    display: inline-block;
    color: #202020!important;
    background-color: #202020!important
}
/*PREVIEW*/
.modal-content {
    background-color: #464646;
    color: #eceff4;
}
.modal-content .modal-header {
    border-color: #505050;
}
/*MODIFIER*/
.form-control {
    background-color: #464646;
    border: 1px solid #373737;
}
.select2-container .select2-selection {
    background-color: #464646;
    color: #eceff4;
    border-color: #373737;
}

/* CATEGORY SELECTION */
.select2-dropdown, .select2-dropdown .select2-results, .select2-dropdown .select2-results .select2-results__option {
    background: #303030;
    color: #eceff4;
}

.select2-search--dropdown .select2-search__field {
    border: 1px solid #404040;
    border-radius: .25rem;
}

.editor-buttons {
    background-color: shade(#ffffff,1.5%)!important;
    border: 1px solid #373737;
}
.select2-dropdown .select2-results .select2-results__option--highlighted, .select2-dropdown .select2-results .select2-results__option[aria-selected=true] {
    background: #414141;
    color: #eceff4;
}
.select2-dropdown .select2-results .select2-results__option:active, .select2-dropdown .select2-results .select2-results__option:focus, .select2-dropdown .select2-results .select2-results__option:hover {
    background: #414141;
}

.select2-search--dropdown .select2-search__field {
background-color: #404040;
    color: #e5e9f0
}

.select2-container--bootstrap4.select2-container--disabled.select2-container--focus .select2-selection, .select2-container--bootstrap4.select2-container--disabled .select2-selection {
    background-color: #464646;
    cursor: not-allowed;
    border-color: #373737;
    box-shadow: none;
}
.btn-primary:not(:disabled):not(.disabled).active, .btn-primary:not(:disabled):not(.disabled):active, .show>.btn-primary.dropdown-toggle {
    color: #fff;
    background-color: #505050;
    border-color: #454545;
}

.btn-secondary:not(:disabled):not(.disabled).active, .btn-secondary:not(:disabled):not(.disabled):active, .show>.btn-secondary.dropdown-toggle {
    color: #fff;
    background-color: #505050;
    border-color: #454545;
}
/*MODIF ANNULER*/
.btn-outline-secondary {
    color: #a73737;
    border-color: #a73737;
}

.btn-outline-secondary:hover {
    color: #fff;
    background-color: #a73737;
    border-color: #a73737;
}
/* -------------------- PROFILE -----------------*/
.card hr {
    border-color: #505050;
}
/*BADGE*/
.card,
.card .card-header {
    background-color: #373737;
    color: #e5e9f0;
}
.custom-control-label:before {
    pointer-events: none;
    background-color: #464646;
    border: 1px solid #505050;
}
.custom-control-label:after {
    background-color: #464646;
    border: 1px solid #505050;
}
.bg-theme-tertiary {
    background-color: #464646;
}
.bg-theme-tertiary {
    background-color: #464646!important;
}
.border {
    border: 1px solid #303030!important;
}
/*Youtube Integration*/
.integration {
    background-color: #606060;}
section.discussion .post-container .post .post-content a {
    color: #e5e9f0;
}
section.discussion .post-container .post .post-content a {
    color: #e5e9f0;
}
/* File */
.custom-file-label, .custom-file-label:after {
background-color: #464646
}

.custom-file-label {
    left: 0;
    z-index: 1;
    height: calc(1.4em + .75rem + 2px);
    font-weight: 400;
    border: 1px solid #373737;
    border-radius: .25rem;
}

.border-bottom {
    border-bottom: 1px solid #303030!important;
}
}
