style.css:
@-moz-document domain(ocjene.skole.hr) {
  @font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 400;
    src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v19/KFOmCnqEu92Fr1Mu7GxKOzY.woff2) format('woff2');
    unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
  }
  
  html, body, thead, th, .section {
  	font-family: 'Roboto', sans-serif !important;
    background-color: #161411 !important;
  }
  
  #e-dnevnik, #footer {
  	color: white !important;
  }
  
  .login-box, #info-box, #wrap {
    background-color: #242220 !important;
    color: white !important;
  }
  
  #info-box, .login-box {
    box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23) !important;
    border-radius: 4px !important;
  }
  
  #user_login, #user_password {
  	border: none !important;
    border-bottom: 2px solid #E9EBEE !important;
    background-color: transparent;
    color: white !important;
    transition: all 1s ease;
    font-weight: normal !important;
  }
  
  #user_login:focus, #user_password:focus {
  	border: none !important;
    border-bottom: 2px solid #235694 !important;
    transition: all 1s ease;
    font-weight: normal !important;
  }
  
  #login-students input {
    font-family: 'Roboto', sans-serif !important;
    font-weight: normal !important;
  }
  
  input:focus {
    outline-width: 0;
  }
  
  .button, .navActive {
    background-position: center;
    transition: background 0.8s;
  }
  .button:hover, .navActive:hover {
    background: #325B8E radial-gradient(circle, transparent 1%, #325B8E 1%) center/15000%;
  }
  .button:active, .navActive:active {
    background-color: #284871;
    background-size: 100%;
    transition: background 0s;
  } 
  
  .button, .navActive {
    border: none !important;
    border-radius: 2px !important;
    padding: 12px 18px !important;
    text-transform: uppercase;
    cursor: pointer;
    color: white !important;
    background-color: #235694 !important;
    box-shadow: 0 0 4px #999 !important;
    outline: none !important;
  }
