  .course, .class-wrap {
    background-position: center;
    transition: background 0.8s;
  }
  
  .course:hover, .class-wrap:hover {
    color: white !important;
    background: #46A246 radial-gradient(circle, transparent 1%, #46A246 1%) center/15000% !important;
  }
  
  .course:hover {
    color: white !important;
  }

  .class-wrap:hover {
    color: white !important;
  }

  .course:active {
    background-color: #388138 !important;
    background-size: 100% !important;
    color: white !important;
    transition: background 0s;
  } 
  
  .class-wrap:active {
    background-color: #388138 !important;
    background-size: 100% !important;
    color: white !important;
    transition: background 0s;
  } 
  
  .course, .class-wrap {
    border-radius: 2px !important;
    color: white !important;
    text-transform: uppercase;
    background-color: #161411 !important;
    background-color: #161411 !important;
    box-shadow: 0 0 4px #999 !important;
    padding-top: 6px !important;
    background-color: #161411 !important;
    padding-bottom: 6px !important;
    margin-bottom: 10px !important;
  }
  
  .course, .class-wrap {
  	background-color: #161411 !important;
    background-color: #161411 !important;
    color: white !important;
  }
  
  .course-name, .notes-title, .tasks-title {
    border-radius: 2px !important;
    text-transform: uppercase;
    background-color: #388138 !important;
    color: white !important;
    box-shadow: 0 0 4px #999 !important;
    padding-top: 6px !important;
    padding-bottom: 6px !important;
    margin-bottom: 10px !important;
  }
  
  #wrap {
    border-radius: 4px !important;
  }
}
