style.css:
@-moz-document url-prefix("https://aternos.org/server/") {
/*Div white color and dark bkc*/

.server-ip {
    position: relative;
    padding: 22px 3px;
    color: #f0f0f0;
    background-color: #1e2125;
    font-size: 28px;
    font-weight: 700;
    text-align: center;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
}

/*(div block with information)*/

.server-bottom {
    display: flex;
    flex-wrap: wrap;
    margin-top: 24px;
    background-color: #1e2125;
}

.server-info-box {
    display: flex;
    background: #1e2125;
    color: #f0f0f0;
    padding: 12px 16px;
    font-size: 24px;
    border-radius: 2px;
    align-items: center;
}

.server-tutorials {
    background: #1e2125;
    color: #f0f0f0;
    border-radius: 2px;
    padding: 16px 20px 4px;
    width: 520px;
    max-width: 100%;
}

.server-info-label span {
    background: #171b1f;
    color: #f0f0f0;
    border-radius: 2px;
    padding: 0 8px;
    font-weight: 700;
}

/*Information bar (waiting, offline, online, starting)*/

.server-status .status.offline {
    background: #171b1f;
}

.server-status .status.online {
    background: #171b1f;
}

.server-status .status.queueing {
    background: #171b1f;
}

.server-status .status.loading {
    background: #171b1f;
}
}

@-moz-document url-prefix("https://aternos.org/friends/") {
.friends-share-list.list-players {
    background-color: #1e2125;
    border: 1px solid #1e2125;
    border-radius: 2px;
}

.list-item-container {
    background-color: #1e2125;
}

.list-item {
    background: #1e2125;
    color: #f0f0f0;
    border-radius: 2px;
    margin-bottom: 8px;
    display: flex;
    padding: 0 4px 0 8px;
    align-items: center;
}

.friends-access-list.list-players {
    background-color: #1e2125;
    border: 1px solid #1e2125;
    border-radius: 2px;
}

.list-input {
    margin-top: 7px;
    background: #1e2125;
    color: #f0f0f0;
    display: flex;
    border-radius: 2px;
}

.toggle-label {
    flex-grow: 1;
    font-size: 18px;
    font-weight: 700;
    color: white;
}
}

@-moz-document url-prefix("https://aternos.org/") {
/*Buttons Page Code*/

/*Buttons*/

.btn:hover {
    background-image: linear-gradient(rgba(0,0,0,0.00),rgba(0,0,0,0.00));
}

/*Button blue*/

.btn-blue {
    color: #a4a4a4;
    background-color: #ff000000;
    border-bottom: 2px solid #a4a4a4;
}

.btn-blue:hover {
    color: #2d87d3;
    border-bottom: 2px solid #2d87d3;
    background-color: #ffffff00;
}

/*Button green*/

.btn-green {
    color: #a4a4a4;
    background-color: #ffffff00;
    border-bottom: 2px solid #a4a4a4;
}

.btn-green:hover {
    color: #1fd78d;
    background-color: #ffffff00;
    border-bottom: 2px solid #1fd78d;
}

/*Button red*/

.btn-red {
    color: #a4a4a4;
    background-color: #ffffff00;
    border-bottom: 2px solid #a4a4a4;
}

.btn-red:hover {
    color: #f62451;
    background-color: #ffffff00;
    border-bottom: 2px solid #f62451;
}

/*Button Black (headstart)*/

.btn-black {
    color: #a4a4a4;
    background-color: #ffffff00;
    border-bottom: 2px solid #a4a4a4;
}

.btn-black:hover {
    color: white;
    background-color: #ffffff00;
    border-bottom: 2px solid white;
}

.btn-label {
    margin-left: 8px;
    background: #a4a4a4;
    color: #2d3943;
    padding: 0 3px;
    border-radius: 2px;
}

.btn-black:hover .btn-label {
    margin-left: 8px;
    background: white;
    color: #2d3943;
    padding: 0 3px;
    border-radius: 2px;
}

/*Buttons white*/

.btn-white {
    background-color: #ffffff00;
    color: #a4a4a4;
    border-bottom: 2px solid #a4a4a4;
}

.btn-white:hover {
    background-color: #ffffff00;
    color: white;
    border-bottom: 2px solid white;
}

/*orange buttons*/

.btn-orange {
    background-color: #ffffff00;
    color: #a4a4a4;
    border-bottom: 2px solid #a4a4a4;
}


.btn-orange:hover {
    color: #eb7b59;
    background-color: #ffffff00;
    border-bottom: 2px solid #eb7b59;
}
}

@-moz-document url-prefix("https://aternos.org/") {
/*Other Page Code*/

/* Body Color */

.content {
    flex-grow: 1;
    padding: 20px 29px 15px 20px;
    height: 100%;
    overflow-x: hidden;
    overflow-y: auto;
    width: 0;
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    max-width: 100vw;
    background: #2d3943;
}

.navigation.toggled {
    width: 80px;
    flex-basis: 80px;
    border-right: 1px solid #47535d;
}

.header {
    height: 100px;
    width: 100%;
    background: #2d3943;
    align-self: top;
    display: flex;
    align-items: center;
    border-bottom: 1px solid #47535d;
}

.ad.responsive-skyscraper {
    min-width: 0px;
    width: 0px;
    height: 0px;
}

.sidebar {
    padding: 0px;
    display: flex;
    justify-content: center;
    align-items: center;
    overflow-y: auto;
    background: #f0f0f0;
}

.global-message.blue {
    background-color: #1e2125;
}

div.sncmp-popup-content-wrapper {
    background: #2D3943;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    color: white;
}


div.sncmp-popup-footer {
    padding: 1.5em;
    background: #2d3943;
    border-bottom-left-radius: 3px;
    border-bottom-right-radius: 3px;
    display: flex;
    justify-content: space-between;
    color: white;
}

div.sncmp-popup-context-button {
    background: 0 0;
    color: white;
}

div.sncmp-popup-ok-button:hover {
    background-color: #ffffff00;
    color: #1fd78d;
    border-bottom: 2px solid #1fd78d;
    font-size: unset;
}

div.sncmp-popup-ok-button {
    color: #a4a4a4;
    background-color: #ffffff00;
    border-bottom: 2px solid #a4a4a4;
}

.friend-access-switch {
    border-bottom: 1px solid #2d3943;
    padding: 4px 8px;
    display: flex;
    align-items: center;
}

.friend-access-dropdown {
    display: none;
    position: absolute;
    background: #1e2125;
    z-index: 1000;
    border-radius: 2px;
    right: 0;
    top: calc(100% + 3px);
    border: 1px solid #2d3943;
    max-height: 500px;
    overflow-y: auto;
}

.alert main {
    background: #2d3943;
    color: white;
    padding: 20px;
    border-bottom-right-radius: 2px;
    border-bottom-left-radius: 2px;
    font-size: 18px;
    word-wrap: break-all;
}
}

@-moz-document url-prefix("https://aternos.org/worlds/") {
.world {
    flex-grow: 1;
    margin-right: 10px;
    margin-bottom: 10px;
    background: #1e2125;
    color: #f0f0f0;
    width: 48%;
    padding: 12px 22px;
    border-radius: 2px;
    display: flex;
    align-items: center;
    transition: background-image .5s;
}

.toggle-inverted label {
    background: #1e2125;
    border-color: #1e2125;
}

.toggle-save {
    padding-right: 6px;
    color: #f0f0f0;
    display: none;
}
}

@-moz-document url-prefix("https://aternos.org/account/") {
.account-setting {
    width: unset;
    flex-grow: 1;
    max-width: unset;
    margin-right: 12px;
    margin-bottom: 12px;
    background: #1e2125;
    color: #f0f0f0;
    padding: 12px;
    border-radius: 2px;
    display: flex;
    flex-direction: column;
}

.account-settings {
    display: unset;
    flex-wrap: wrap;
    align-items: flex-start;
}

.language-input-selector {
    background: #171b1f;
    color: white;
    width: 100%;
    display: flex;
    justify-content: space-between;
    padding: 8px 12px;
    border-radius: 2px;
    font-size: 16px;
    font-weight: 700;
    cursor: pointer;
}

.language-select {
    padding: 4px 8px;
    background: #171b1f;
    color: #f0f0f0;
    font-weight: 700;
    font-size: 14px;
    border-radius: 2px;
    margin-right: 6px;
    margin-bottom: 6px;
    flex-grow: 1;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
}

.account-setting-input input {
    border: none;
    outline: none;
    background: #171b1f;
    padding: 8px 12px 8px 34px;
    width: 100%;
    color: #ffffff;
    font-size: 16px;
    font-weight: 700;
    border-radius: 2px;
}

.account-setting-input i {
    position: absolute;
    color: #ffffff;
    font-size: 22px;
    bottom: 7px;
    left: 5px;
}
}

@-moz-document url-prefix("https://aternos.org/console/") {
.console {
    border-radius: 2px;
    background: #1e2125;
    padding: 0 0 8px;
    color: #f0f0f0;
}
}

@-moz-document url-prefix("https://aternos.org/options") {
.mclist-options {
    border-radius: 2px;
    padding: 20px;
    display: flex;
    flex-direction: row;
    align-items: center;
    background: #1e2125;
}

.options {
    padding: 18px 0;
    background-color: #1e2125;
    color: white;
}

.options label, .misc-options input {
    font-size: 18px;
    font-weight: 700;
    color: white;
}

.number-option input {
    background: #171b1f;
    border: none;
    outline: none;
    text-align: right;
    color: #f0f0f0;
    padding: 6px;
    height: 31px;
    width: 120px;
    border-top-left-radius: 2px;
    border-bottom-left-radius: 2px;
    border-right: #000000 solid 1px;
    -moz-appearance: textfield;
}

.input-number>div {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0 4px;
    color: #f0f0f0;
    cursor: pointer;
    background: #171b1f;
}

.input-number-up {
    border-top-right-radius: 2px;
    height: 16px;
    border-bottom: #000000 solid 1px;
}

.toggle label {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    cursor: pointer;
    border: 2px solid #ffffff00;
    border-radius: 2px;
    background: #ffffff00;
}

.select {
    width: 150px;
    background: #171b1f;
    color: #f0f0f0;
    font-weight: 700;
    border-radius: 2px;
    cursor: pointer;
    position: relative;
    overflow: visible;
}

.select .select-options {
    display: none;
    position: absolute;
    background: #171b1f;
    top: 31px;
    width: 100%;
    z-index: 100;
    border-bottom-right-radius: 2px;
    border-bottom-left-radius: 2px;
    overflow-x: hidden;
}

.option-category-options {
    position: absolute;
    display: none;
    width: 100%;
    background: #171b1f;
    top: 0;
    left: 0;
    min-height: 100%;
}

.text-input {
    background: #171b1f;
    color: #f0f0f0;
    font-weight: 700;
    border: none;
    padding: 6px 12px;
    border-radius: 2px;
    height: 31px;
    outline: none;
    font-size: 16px;
    flex-grow: 1;
}
}

@-moz-document url-prefix("https://aternos.org/addons/") {
.plugin-search {
    margin-left: auto;
    background: #1e2125;
    padding: 8px 8px 8px 12px;
    border-radius: 2px;
    margin-right: 10px;
    display: flex;
    width: 250px;
}

.plugin {
    flex-grow: 1;
    display: flex;
    flex-direction: column;
    background: #171b1f;
    color: #f0f0f0;
    border-radius: 2px;
    margin-right: 10px;
    margin-bottom: 10px;
    min-width: 250px;
    word-wrap: break-word;
    width: auto !important;
}

.pagination {
    margin-top: 12px;
    display: flex;
    justify-content: flex-end;
}

.pagination .current {
    font-weight: 700;
    font-size: 24px;
    background: #1e2125;
    color: #f0f0f0;
    padding: 4px 8px;
    border-radius: 2px;
}

}

@-moz-document url-prefix("https://aternos.org/addons/a/") {
tbody {
    background: #1e2125;
    color: white;
    border: 2px solid #1e2125;
    border-radius: 2px;
}

.plugin-description {
    color: white;
}
}

@-moz-document url-prefix("https://aternos.org/backups/") {
.file-create {
    background: #1e2125;
    display: flex;
    padding: 8px 12px;
    border-radius: 2px;
    margin-top: 12px;
}

.backups {
    border-left: 2px solid #1e2125;
    border-right: 2px solid #1e2125;
    border-bottom: 2px solid #1e2125;
    border-radius: 4px;
}

.file {
    background-color: #1e2125;
    display: flex;
    align-items: center;
    padding: 8px 12px;
    font-weight: 700;
    font-size: 16px;
    color: #f0f0f0;
    margin-top: unset;
    border-radius: 2px;
}

.filesize {
    background-color: white;
    color: #2d3943;
    font-size: 12px;
    padding: 2px 6px;
    border-radius: 2px;
}

.backup-auto {
    margin-left: auto;
    max-width: 400px;
    display: flex;
    background-color: #1e2125;
    color: white;
    padding: 7px;
}

.toggle label {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    cursor: pointer;
    border: 2px solid #1e2125;
    border-radius: 2px;
    background: #1e2125;
}

.input-number>div {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0 4px;
    color: #f0f0f0;
    cursor: pointer;
    background: #171b1f;
}

.number-option input {
    background-color: #171b1f;
    border: none;
    outline: none;
    text-align: right;
    color: #f0f0f0;
    padding: 6px;
    height: 31px;
    width: 120px;
    border-top-left-radius: 2px;
    border-bottom-left-radius: 2px;
    border-right: #000000 solid 1px;
    -moz-appearance: textfield;
}

.input-number-up {
    border-top-right-radius: 2px;
    height: 16px;
    border-bottom: #000000 solid 1px;
}

.backup-auto .number-option input {
    width: 60px;
}
}

@-moz-document url-prefix("https://aternos.org/files/") {
.directory {
    background-color: #1e2125;
}

.file {
    display: flex;
    align-items: center;
    padding: 8px 12px;
    font-weight: 700;
    font-size: 16px;
    background: #1e2125;
    color: #f0f0f0;
    margin-top: 3px;
    border-radius: 2px;
}

.file-create {
    background: #1e2125;
    display: flex;
    padding: 8px 12px;
    border-radius: 2px;
    margin-top: 12px;
}

.path .part {
    font-weight: 700;
    border-radius: 2px;
    margin-right: 6px;
    display: inline-block;
    padding: 6px 8px;
    background: #f0f0f000;
    margin: 3px;
    color: #a4a4a4;
    border-bottom: 2px solid #a4a4a4;
}

.editor {
    width: 100%;
    height: 500px;
    border: 5px solid #1e2125;
    border-left: none;
    border-radius: 2px;
}

.editor.ace-tomorrow-night {
    background: #1e2125;
}

.editor.ace-tomorrow-night .ace_gutter {
    background: #1e2125;
}

}

@-moz-document url-prefix("https://aternos.org/software/") {
.software-type {
    display: block;
    width: 32%;
    position: relative;
    background-color: #1e2125;
    min-width: 200px;
    flex-grow: 1;
    border-radius: 2px;
    color: #f0f0f0;
    margin-bottom: 10px;
    margin-right: 10px;
}

.version {
    padding: 20px;
    border-bottom-right-radius: 2px;
    border-bottom-left-radius: 2px;
    font-size: 18px;
    line-height: 20px;
    border: 5px solid #1e2125;
    border-top: none;
    color: white;
}

.version-title .version-title-name {
    flex-grow: 1;
    padding: 18px 20px;
    border-top-left-radius: 2px;
    background: #1e2125;
    color: #f0f0f0;
    margin-bottom: 0;
}

.version-title .version-title-install {
    cursor: pointer;
    display: flex;
    align-items: center;
    color: #f0f0f0;
    font-size: 32px;
    background: #141517;
    background-image: linear-gradient(rgba(0,0,0,0.08),rgba(0,0,0,0.08));
    padding: 5px 18px;
    border-top-right-radius: 2px;
}
}

@-moz-document url-prefix("https://aternos.org/start/") {
.tutorial-text .tutorial-darker {
    color: #ffffffcc;
}
}

@-moz-document url-prefix("https://aternos.org/create/") {

a.btn.btn-dark.btn-large.btn-no-margin.js-cancel-server {
    color: #a4a4a4;
    border-bottom: 2px solid #a4a4a4;
    border-radius: 2px;
}

a.btn.btn-dark.btn-large.btn-no-margin.js-cancel-server:hover {
    color: #f62451;
    border-bottom: 2px solid #f62451;
    border-radius: 2px;
}

div#create-next {
    color: #a4a4a4;
    background-color: #ff000000;
    border-bottom: 2px solid #a4a4a4;
}

div#create-next:hover {
    color: #2d87d3;
    background-color: #ff000000;
    border-bottom: 2px solid #2d87d3;
}

div#create-back {
    color: #a4a4a4;
    background-color: #ff000000;
    border-bottom: 2px solid #a4a4a4;
}

div#create-back:hover {
    color: #f62451;
    border-bottom: 2px solid #f62451;
    border-radius: 2px;
}

.social-login-buttons>.btn:first-child:hover {
    color: #f62451;
    border-bottom: 2px solid #f62451;
    border-radius: 2px;
}

}

@-moz-document url-prefix("https://aternos.org/log/") {

.problem-solution {
    background: #1E2125;
    margin-bottom: 6px;
    padding: 4px 8px;
    border-radius: 2px;
    display: flex;
    align-items: center;
    color: white;
}

.problem-header {
    background: #1e2125;
    color: #f0f0f0;
    padding: 8px 8px 8px 12px;
    display: flex;
    border-radius: 3px;
    align-items: center;
    margin-bottom: 8px;
}

}
