style.css:
@-moz-document domain('www.supraforums.com') {

.california-additional-header h1 {
    font-size: 2em !important;
    margin: 0 0 !important;
}

.message-cell.message-cell--user {
    padding-left: 10px !important;
}

.message-cell.message-cell--user, .message-cell.message-cell--action {
    background: rgba(19,20,21,0.8) !important;
}

.sticky-divider {
    background-color: gray !important;
    opacity: 0.4 !important;
}

div.california-thread-sidebar.hide-mw-responsiveWide {
    display: none !important;
}

div.message-cell.message-cell--main {
    padding: 20px 10px 10px 10px !important;
}

div.p-body {
    background-color: #1C222E !important;
}

div.p-body-inner {
    max-width: none !important;
}

div.p-header-banner {
    background-color: #1C222E !important;
    background-image: url('https://web.archive.org/web/20040511105819im_/http://www.supraforums.com/forum/images/forumlogo.gif') !important;
    background-repeat: no-repeat !important;
    background-size: contain !important;
}

div.p-header-banner img {
    visibility: hidden !important;
}

div.p-header-inner {
    max-width: none !important;
}


div.p-header-logo {
    visibility: hidden !important;
}

div.p-pageWrapper {
    background-color: #1C222E !important;
}

div.p-sectionLinks {
    background-color: #1C222E !important;
    margin-bottom: 0px !important;
}

div.p-sectionLinks-inner {
    max-width: none !important;
}

div.structItemContainer-group div.california-thread-item:nth-last-child(n+36) {
    background-color: rgba(255,0,0,0.2) !important;
}

header.p-header {
    background: rgba(7,7,7,0) !important;
}

header.p-navSticky.is-sticky {
    background: rgba(7,7,7,0.5) !important;
}

.top-photos-item {
    min-width: 0;
}

}
