@-moz-document url-prefix("https://www.kogama.com"), url-prefix("https://friends.kogama.com/"), url-prefix("https://kogama.com.br/") {
/***********************************************************************
	=== Discordama EX (v.2) ===

	Experience the wonderful Discord layout in KoGaMa
	(Name is a working title)

	By Programa Studios, Made by Aligator#9999 and Ag#5896
	- https://www.kogama.com/profile/7476907/
	- https://www.kogama.com/profile/36355/

	Design inspired by Discord
	- https://www.discordapp.com/

	Style for KoGaMa
	- https://www.kogama.com/
	- https://friends.kogama.com/
	- https://kogama.com.br/

***********************************************************************/
.kb-status-menu {
    left: 100%;
}

/*Changes elite text color to blurple*/
.text-gold{
    color: #7289DA !important;
}

/* ##### GENERAL STYLES ##### */
/* Page Body */
body {
    background-color: #36393F !important;
    background-image: none !important;
    padding-top: 0px !important;
}

/* Page Header */
header#pageheader {
    background-color: #202225 !important;
    height: 100% !important;
    width: 72px !important;
}

/* Page Content, Footer */
div#content,
footer {
    margin-left: 72px;
    width: unset !important;
}
}

@-moz-document url-prefix("https://www.kogama.com"), url-prefix("https://friends.kogama.com/"), url-prefix("https://kogama.com.br/") {
/* ##### NAVIGATION MENU ##### */
/* Navigation Menu Text */
nav.menu li span {
    display: none !important;
    position: relative;
    right: -60px;
    top: -30px;
    padding-left: 10px !important;
    padding-right: 10px !important;
    background-color: #000;
    border-radius: 5px;
    text-transform: none !important;
    font-weight: normal !important;
    font-family: Whitney, Helvetica Neue, Helvetica, Arial, sans-serif;
    width: max-content;
}
nav.menu li:hover span {
    display: block !important;
}
nav.menu li:hover span::after {
    content: " ";
    position: absolute;
    top: 50%;
    right: 100%;
    margin-top: -5px;
    border-width: 5px;
    border-style: solid;
    border-color: transparent black transparent transparent;
}

/* Navigation Menu Items */
nav.menu ol {
    margin-left: 4px;
}
nav.menu li {
    border-radius: 50%;
    background-color: #2F3136;
    width: 48px;
    height: 48px;
    text-align: center;
    margin-top: 10px;
    transition: 200ms;
}
nav.menu li:hover {
    background-color: #7289DA;
    border-radius: 25%;
    transition: 200ms;
}
nav.menu li:hover a {
    border-bottom: none !important;
}
nav.menu li.active a {
    background-color: unset !important;
    border-bottom: none !important;
}

/* Main Logo */
a.logo {
    float: unset !important;
}
.logo-image {
    display: block !important;
    border-radius: 50%;
    width: 48px !important;
    height: 48px !important;
    margin: unset !important;
    margin-top: 20px !important;
    margin-left: 4px !important;
    transition: 200ms;
}
.logo-image:hover {
    border-radius: 25% !important;
    transition: 200ms;
}

/* Player Meta Bar (XP, Gold, Profile Picture)*/
#meta-nav {
    position: unset !important;
    margin-left: 10px;
}

body#root-page-mobile header#pageheader #meta-nav > li.profile-credits {
    display: none;
}

}

