@-moz-document url-prefix("https://www.kogama.com"), url-prefix("https://friends.kogama.com/"), url-prefix("https://kogama.com.br/") {
/* #### SETTINGS HAMBURGER MENU #### */
/*Settings menu*/
ul.links,
div#profile-extended{
    background-color: #2f3136 !important;
    top: 0px !important;
    left: 72px !important;
    width: calc(100% - 72px) !important;
    max-width: unset !important;
    box-shadow: none !important;
    -webkit-box-shadow: none !important;
}
#profile-extended h4,
#profile-extended section.user-credits {
    box-shadow: none !important;
    -webkit-box-shadow: none !important;
    background-color: #2F3136 !important;
}
#profile-extended .arrow-top {
    border-color: #2F3136 transparent transparent !important;
}

/*Settings menu content*/
.profile-links ul.links li.link-group > ul > li,
.profile-links ul.links > li > a{
    background-color: #2f3136;
    text-decoration: none;
    font-weight: 400;
    padding: 8px;
    border-radius: 8px;
}
.profile-links ul.links li.link-group > ul > li > a{
    padding: 0px !important;
}

/*Settings menu content on hover*/
.profile-links ul.links li.link-group > ul > li:hover,
.profile-links ul.links > li > a:hover{
    background-color: #37393f;
    text-decoration: none;
    font-weight: 400;
    padding: 8px;
    border-radius: 8px;
}

/*Settings menu content text*/
#profile-extended .profile-links a:hover{
    color: hsla(0,0%,100%,.73)!important;
}

/*Settings menu seperator*/
.profile-links ul.links > li.link-group > a{
    border-bottom: 1px solid #3b3d42 !important;
}

/*Settings menu removing icons*/
img.menu-icon{
    visibility: hidden !important;
}

/*XP Progress Bar*/
.xp-bar{
    margin-top: -12px !important;
}

.xp-bar .progress{
    background-color: #202225 !important;
    height: 4px
}
.xp-bar .progress .progression-bar{
    background-color: #FF73FA !important;
    border-radius: 0px !important;
}
.xp-bar:hover .progress{
    background-color: #dcddde !important;
    transition: width 2s;
}
.xp-bar .xp-text{
    margin-top: 15px !important;
}
}

