}

/* Friends List: Profile Picture */
.UA3TP ._3tYRU,
.UA3TP rect {
    clip-path: circle();
}

/* Friends List: Elite Frame */
.UA3TP img[src$="svg"] {
    transform: scale(1.1);
    top: 2px;
}

/* Friends List: Status Indicator */
.UA3TP._1fWKe .Hkdag {
    clip-path: circle(18%);
