@-moz-document url-prefix("https://www.kogama.com"), url-prefix("https://friends.kogama.com/"), url-prefix("https://kogama.com.br/") {
/* ##### PROFILE PAGE ##### */
/* Profile Badges - Overflow Wrap */
#profile-badges ul.badges {
    flex-wrap: wrap !important;
    position: unset !important;
}
#profile-badges {
    width: auto !important;
    height: auto !important;
    margin-right: 32px;
}

/* Profile Background */
#profile-page .background-avatar {
    background-image: -o-linear-gradient(45deg, hsla(0, 0%, 5%, .1) 25%, transparent 0, transparent 50%, hsla(0, 0%, 5%, .1) 0, hsla(0, 0%, 5%, .1) 75%, transparent 0, transparent) !important;
    background-image: -webkit-linear-gradient(45deg, hsla(0, 0%, 5%, .1) 25%, transparent 0, transparent 50%, hsla(0, 0%, 5%, .1) 0, hsla(0, 0%, 5%, .1) 75%, transparent 0, transparent) !important;
    background-image: linear-gradient(45deg, hsla(0, 0%, 5%, .1) 25%, transparent 0, transparent 50%, hsla(0, 0%, 5%, .1) 0, hsla(0, 0%, 5%, .1) 75%, transparent 0, transparent) !important;
    filter: none !important;
}
#profile-page .section-top-background {
    background-image: none !important;
    background-color: rgba(0, 0, 0, .1) !important;
}

/* Profile: Username / Progress - Desktop */
@media only screen and (min-width: 1000px) and (orientation: landscape) {
    .section-top > .username a {
        color: var(--header-primary);
        line-height: 4rem;
        font-size: 3rem;
        font-weight: inherit;
        font-style: inherit;
        font-family: inherit;
        vertical-align: baseline;
    }
}

/* ##### FEED ##### */
#profile-news-feed ul.news-feed-thumbs > li.item {
    background-color: #2F3136 !important;
}

/*What's up textbox*/
#profile-status-update form.status-message textarea{
    background-color: rgba(114, 118, 125, .3) !important;
    /*AAAA dis hard*/
    border-top: 1px solid hsla(0, 0%, 100%, .06) !important;
    border-radius: 5px;
    margin-top: 20px;
    padding-top: 20px;
    margin-left: 20px;
    margin-right: 20px;
    margin-bottom: 30px;

    color: hsla(0, 0%, 100%, .7);
    resize: none;
    border: none;
    appearance: none;
    font-weight: 400;
    font-size: 0.9375rem;
    line-height: 1.25rem;
    letter-spacing: -0.025rem;
    max-height: 144px;
    width: 100%;
    min-height: 20px;
    margin: 2px 2px 2px 0;
    padding: 10px;
}
#profile-status-update form.status-message textarea.kb_ignore::placeholder {
    color: hsla(0, 0%, 100%, .7)
}
#profile-status-update .status-textarea-group{
    width: 830px !important;
}

/*Post button disappear*/
#profile-status-update .button-wrapper{
    visibility: hidden !important;
}

/*Profile header*/
#mobile-page #profile-page .section-top .section-top-background {
    background-image: url(https://dummyimage.com/1430x447/202225/202225.jpg) !important;
}
#profile-page .background-avatar {
    background-image: none;
}

/*Text*/
#profile-news-feed ul.news-feed-thumbs > li.item {
    color: #dcddde;
    font-weight: inherit;
    font-style: inherit;
    font-family: inherit;
    clear: inline-end;
}

/*Author*/
