style.css:
@-moz-document domain("mafiaonline.ru") {/* BASE */
 body {
    font-size: 14px;
    line-height: 1.3;
    color: #fff;
    /*background:  #131313  !important;*/
    background: url(https://images.wallpaperscraft.ru/image/tumannost_planety_zvezdy_130860_3692x2308.jpg) no-repeat fixed center top;
}
a {
    color: #e2e900;
    text-decoration: none;
    cursor: pointer;
}
a:hover {
    text-decoration: none;
}
span {
    font-size: 14px;
    line-height: 15px;
    color: #D2AED2;
}
input, textarea, select {
    font-size: 12px;
    font-family: arial, sans-serif;
}
textarea {
    overflow: auto;
}
p {
    line-height: 20px;
    font-size: 14px;
}

/*Street table partiy*/

 table.oldschool {
    border-collapse: separate;
    border-spacing: 7px 5px
}
td.players {
    text-align: left;
    vertical-align: middle;
    border-radius: 8px;
    padding: 3px;
    background : #333;
}
table.oldschool th, .ui-button, .but, .ui-dialog, .ui-dialog-titlebar, .ui-dialog-buttonpane, #comment {
    background:#2d2f2f !important;
    border-radius: 8px;
}
#dialog-form {
    background:#1A1A1B;
}
/*top menu frame */
.topmenu {
    background : #222c;
}
.row * {
    color: #000 !important;
}
/*User list*/
 .users {
    color : #3296c8 ;
}
.users_i {
    color : #D2AED2;
}
#userlist {
    border-radius: 8px;
}


/*CHAT*/
 .text, .text > a, .chat_text, .chat_text > * {
    color: #D2AED2 !important;
    font-size: 15px;
}
#topchattable {
    background : #233b;
}
.to {
    color: #F5ED88;
    font-size: 15px;
}
.to > * {
    color: #F5ED88 !important;
    font-size: 15px;
}
.chat_system {
    color: #006621 !important;
    font-size: 15px;
}
.chat_bg {
    background: #131313;
}

input[type="radio"] {
    background-color: #8aeded;
    border: 2px solid white;
    width: 20px;
    height: 20px;
    position: relative;
    display: inline-block;
    color:#5A009D;
    border-radius: 50%;
    cursor: pointer;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
}
input[type='radio']:hover {
    width: 20px;
    height: 20px;
    position: relative;
    background-color: #333;
    content:'';
    display: inline-block;
    visibility: visible;
    border: 2px solid white;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
}
/*Game */
 td.game_ramkal {
    line-height: 50px;
}
.game_ramkal img {
    height: 50px;
}
/*PHONE */
 .fas.fa-sms.phone:before {
    content:"";
    font-size: 1.5em;
}

#chat_note, div#smile_div {
    background-color: #131313 !important;
}
#chat_note, div#smile_div {
    background-color: #131313 !important;
}
/*Forum*/
.postmsg, .form-group, .dropdown-menu.open span, #pagetext, .byuser, .modlist, .usertitle, .cell-midi, .cell-maxi, .hidden-xs, .break-word, h3, .text-center, .pull-left, .table, .modlist em, .highlight-green, .btn-default i, .col-md-2.text-center span.level, .collapsed, .panel-body p, .col-sm-2 p, .col-sm-2 span, .control-label, .checkbox label, .bblinks.list-inline li, .row *  {
    color: #F5ED88;
}
.btn.btn-xs.btn-primary {color:#006621 !important; border-color:#006621 !important; background :#000 !important;}
.panel-heading.spoiler-head, .table-striped > tbody > tr:nth-of-type(2n+1) {background : #111c}
/*row*/
.panel, .pun .block .container-fluid,.container-fluid .row .block.pun, .panel-heading, .table.forum, .row.post, .breadcrumb, .block.blockform, a.btn, .btn-default, #brdstats .row, .panel-heading, .active td, .warning td, .danger td, .blockform.form-horizontal.container-fluid, #punsearch .block.container-fluid, #postform, #postreview{
    background : #333c !important;}

.resize-vertical.form-control, .row.block, .block ul {background : #333c !important; color : #F5ED88;}
.row.post a, .table.forum a, .breadcrumb a{color : #3296c8 !important;}


/*Clans*/
 #families *, .block.pun, .hidden-xs span {
    color: #F5ED88 !important;
}
/*LOG*/
 .panel-body span.text {
    color: #000
}

#log_chat, #log_chat.chat_text, .panel-body span.text {
    color: #D2AED2 !important;
}
.row * {
    color: #F5ED88 !important;
}

.move {
    color: #F5ED88 !important;
}

/*Info tables style */
 .table_info {
    background-color:#131313;
    border-collapse: separate;
    border-spacing: 7px 5px;
    border : 0px;
}
.table_info td {
    background-color:#1A1A1B !important;
    border-radius: 8px;
}
.table_info tr:nth-child(2n+1), .table_info tr:nth-child(2n) {
    background-color:#1A1A1B !important;
}
/* Profile style */
 #profilenav {
    background-color:#131313;
    border-collapse: separate;
    border-spacing: 7px 5px;
