style.css:
@-moz-document domain("hardware.info") {
.collection,
.divider--content{
    background:inherit;
}

body,
.footer__article--secondary{
   /* background-color: rgb(10,10,10);*/
/*        background-image: url('data:image/png;base64,');
  */  
           background-image: url('data:image/png;base64,')
}

.overview-item,
.showcase__header,
.showcase-item,
.showcase__footer,
.has_content, .sidebar-item,
.scroller,
.show-more__expander,
.button--default,
.search--active,
.search__item,
.search__group-show-more,
.search,
.comparison,
.comparison__header,
.comparison__row--sticky,
.comparison__aside,
.tile,
.template-login .p-body-main .p-body-content, .template-register_form .p-body-main .p-body-content, .template-register_complete .p-body-main .p-body-content, .template-login .p-body-main .p-body-pageContent, .template-register_form .p-body-main .p-body-pageContent, .template-register_complete .p-body-main .p-body-pageContent,
.template-register_form .p-body-header, .template-register_complete .p-body-header,
.block-container:not(.block-container--noStripRadius)>.block-body:last-child>.formRow:last-child>dt, .block-bottomRadiusContent.formRow>dt, .block-bottomRadiusContent>.formRow:last-child>dt,
.template-login .formRow > dt, .template-register_form .formRow > dt, .template-register_complete .formRow > dt,
.p-body-main .p-body-pageContent .formRow.formRow--input,
.p-body-main .p-body-pageContent .formSubmitRow-main .formSubmitRow-bar,
.block-container:not(.block-container--noStripRadius)>.block-body:last-child>.formRow:last-child>dd{
    background-color: rgb(20,20,20);
}

.tab__item-active,
.table tbody tr th, .table thead tr th,
.price-alert,
.accordion-box--always-open .accordion-box__header,
.accordion-box__header,
.well,
#loginBox,
a.button--default,
a.button--default:focus,
.spotlight__item{
    background-color: rgb(27,27,27);
}

textarea,
select,
input,
label.checkmark,
.checkbox .checkmark{
   background-color: rgb(27,27,27) !important;
}

.dropdown__list-item,
.dropdown--active .dropdown__avatar-wrapper, .dropdown--active .dropdown__toggle,
.dropdown__content,
.button--default:hover,
.accordion-box__header:hover,
.tooltip--lg,
.drawer__container,
.fos_comment_comment_show--anchored{
     background-color: rgb(34,34,34);
}

.pagination__pages > li > a,
.accordion-box--always-open .accordion-box__header:hover,
.block-row.block-row--highlighted,
a.button--default:hover{
    background-color: rgb(41,41,41);
}

.dropdown__list-item:hover,
.category-browser__image-list-item:hover,
.alert,
.alert--default,
.filters__selected-item{
     background-color: rgb(48,48,48);
}

.pagination__pages > li > a:hover{
    background-color: rgb(55,55,55);
}

.filters__selected-item:hover,
select.results__sort-select > optgroup{
    background-color: rgb(62,62,62);
}

.search__item-sub-title,
.prices-table__info--light{
    color: rgb(150,150,150);
}

.showcase-item__subtitle,
.stock,
.search__item-title,
.productcomp-item__shop-details{
    color: rgb(170,170,170);
}

body,
textarea,
select,
input,
.sidebar-item__content--is-link,
.pagination__pages > li > a,
.pagination__pages li,
.offer__price,
.productcomp-item__price--anchor,
.collection__item-title--black,
.alert,
.alert--default,
.block-row.block-row--highlighted,
.filters__selected-item,
.filters__selected-item:hover,
a.button--default,
a.button--default:active{
    color: rgb(200,200,200);
}

@media (prefers-color-scheme: dark) {
    .alert__title,
    .alert__content{
         color: rgb(200,200,200);
    }    
    .overview-item__image--large {
        background-color: rgba(1,1,1,0.1);
    }
    .dropdown--active .dropdown__content,
    .category-browser__sub--active{
         background-color: rgb(34,34,34);
         border-color: rgb(34,34,34);
    }
}

a,
.overview-item__details-item,
.showcase-link,
.showcase-item--small .showcase-item__subtitle,
.tab__index,
.article__author,
.dropdown__list-item > a,
.dropdown--active .dropdown__avatar-wrapper, .dropdown--active .dropdown__toggle,
.dropdown__link,
.sidebar-item__content--is-link:hover,
.overview-item__title,
.button--default,
.search__item-title>strong,
.search__group-show-more,
.search__group-sort-option,
.filters__more,
.offer__discount,
.checkbox__title>small,
.list__item--success,
.item-header__content__text .title,
.collection__title,
.reaction--1 .reaction-text{
    color: #aac8dd;
}

.list__item>svg,
.link--has-arrow>svg,
.article__index-button > svg,
.showcase-link>svg,
.icon--primary-on-hover:hover > svg, .icon--primary:hover > svg, .icon--primary > svg{
    fill: #aac8dd;
}

.checkbox:hover input~.checkmark, .radio:hover input~.checkmark, .s-check-radio:hover input~.checkmark{
    border-color: #aac8dd;
}

.dropdown__list-item  img,
.dropdown--active .dropdown__avatar-wrapper:after, .dropdown--active .dropdown__toggle:after,
.toggle{
    filter: invert(100%) grayscale(80%);
    
}


/* comments */
.fos_comment_comment_metas small>a,
blockquote{
     color: rgb(170,170,170);
}
.fos_comment_comment_show{
    background-color: rgb(20,20,20);
}
.editor > .editor__button > svg{
    filter: invert(100%);
}

.bar.table__bar{
    filter: invert(92%) hue-rotate(180deg);
}

.highcharts-container .highcharts-background, .highcharts-container .highcharts-plot-background{
    fill: rgb(20,20,20) !important;
}


.article__content p img{
    filter:brightness(50%);
}
.article__content p img:hover{
    filter:brightness(100%);
}

h1,
.sidebar-item__title,
.showcase__header-title{
    color: #d95d5b;
}

.showcase__header,
.sidebar-item{
    border-color:    #d95d5b; 
}

body{
    scrollbar-color:  rgb(60,60,60) rgb(90,90,90);
}

meter.meterBar.js-strengthMeter{
    filter: invert(90%) hue-rotate(180deg)saturate(2)
}
}

@-moz-document regexp("(.*)\\.hardware.info\\/nieuws(.*)") {
.article{
    background-color: rgb(20,20,20);
}
}

@-moz-document regexp("(.*)\\.hardware.info\\/artikel(.*)") {
.article__index,
.article__index-button{
    background-color: rgb(20,20,20);
}

.article__index-link,
.article__index__sidebar--wrapper{
    background-color: rgb(27,27,27);
}

.article__index-link:hover{
    background-color: rgb(34,34,34);
}

.article__index{
    color: #aac8dd;
}
}

@-moz-document regexp("(.*)\\.hardware.info\\/product(.*)") {
.sitearea_content .product__row .gallery img {
    filter:brightness(50%);
}

.sitearea_content .product__row .gallery img:hover {
    filter:brightness(100%);
}
}

@-moz-document regexp("(.*)\\.hardware.info\\/reviews(.*)") {
.overview-block--item-wrapper .overview-item__image--large img {
    filter:brightness(50%);
}

.overview-block--item-wrapper .overview-item__image--large img:hover {
    filter:brightness(100%);
}
}

@-moz-document regexp("(.*)\\.hardware.info\\/productvergelijker(.*)") {
.accordion-box--always-open .accordion-box__header, .accordion-box__header{
    background: inherit;
}

.accordion-box--always-open .accordion-box__header:hover, .accordion-box__header:hover{
    background: inherit;
}
}

@-moz-document regexp("(.*)\\.hardware.info\\/forum(.*)") {
body,
html,
.p-pageWrapper{
    background-color: rgb(20,20,20);
}

.block-container,
.block-row.block-row--alt,
.block--messages .message, .block--messages .block-row{
    background-color: rgb(27,27,27);
}

.block-header,
.block-footer,
.block-filterBar,
.tabs--standalone,
.structItem.structItem--note,
.menu-content,
.formRow>dt,
.memberHeader-main,
.blockMessage,
.message-cell.message-cell--user, .message-cell.message-cell--action,
.fr-box.fr-basic .fr-wrapper,
.fr-toolbar{
    background-color: rgb(34,34,34);
}

.pageNav-jump,
.pageNav-page,
.inputGroup.inputGroup--joined .inputGroup-text,
.inputGroup.inputGroup--joined .inputNumber-button:hover, .inputGroup.inputGroup--joined .inputNumber-button:active, .inputGroup.inputGroup--joined .inputNumber-button:focus,
.bbCodeBlock-title,
.bbCodeBlock,
.reactionsBar{
    background-color: rgb(41,41,41);
}

.menu-header,
.formSubmitRow-bar{
    background:  rgb(41,41,41);
}

.block-filterBar .filterBar-filterToggle:hover, .block-filterBar .filterBar-menuTrigger:hover{
    background: rgb(48,48,48);
}

.pageNav-jump:hover,
.pageNav-page:hover,
.block-filterBar .filterBar-filterToggle,
.button.button--link, a.button.button--link{
    background-color: rgb(55,55,55);
}

.block-filterBar .filterBar-filterToggle:hover,
.button.button--link, a.button.button--link:hover{
    background-color: rgb(69,69,69);
}


.block-container,
.contentRow-minor,
.structItem-parts>li:nth-child(even){
    color:rgb(170,170,170);
}

.block-minorHeader,
.block-minorHeader a,
.block-header,
.block-footer,
.pageNav-jump,
.pageNav-page,
.menu-header,
.block-filterBar .filterBar-filterToggle,
.button.button--link, a.button.button--link,
.tabs--standalone .tabs-tab:hover,
.block-row.block-row--alt,
.blockMessage,
.message-cell.message-cell--user, .message-cell.message-cell--action,
.block--messages .message, .block--messages .block-row,
.fr-box.fr-basic .fr-element,
.fr-toolbar .fr-command.fr-btn, .fr-popup .fr-command.fr-btn,
.structItem-minor{
    color: rgb(200,200,200);
}

input,
select{
    color: rgb(200,200,200) !important;
}


a,
a:hover,
.pairs>dt,
.tabs--standalone,
.tabs--standalone .tabs-tab.is-active,
.structItem.structItem--note,
.memberHeader-name{
    color: #aac8dd;
}

.tabs--standalone .tabs-tab.is-active{
    border-color: #aac8dd;
}

.inputNumber-button--down::before,
.inputNumber-button--up::before,
.iconic>input[type=radio]+i:before,
.iconic>input[type=radio]+i:after,
.iconic>input[type=checkbox]+i:before,
.iconic>input[type=checkbox]+i:after,
.inputDate .inputDate-icon::before,
.structItem-status{
    filter: invert(92%) hue-rotate(180deg);
}
}

@-moz-document regexp("(.*)\\.hardware.info\\/forum/account(.*)") {
.blockLink.is-selected{
    background-color:rgb(34,34,34);
}

.blockLink.is-selected,
.blockLink.is-selected:hover,
.blockLink:hover{
    background-color:rgb(48,48,48);
}
}
