style.css:
@-moz-document regexp("^(?!.*forums).*techreport\\.com.*$") {
body.page-template,
body.page-template-default,
body.post-template-default,
body.archive,
body.search {
    background-color: #213666;
    color: #ddd;
}

#modal-search {
    background-color: rgba(221, 221, 221, 0.85);
}

#modal-search-inner {
    background-color: rgba(33, 54, 102, 0.8);
    padding: 40px;
    border-radius: 8px;
    margin-top: 150px;
}

#modal-search-inner #s {
    color: #fff;
}

#main-nav-outer {
    margin-bottom: 10px;
}

.page-template a,
.page-template-default a,
.post-template-default a,
.post-content a {
    color: #b1c6f6;
}

.page-template a:hover,
.page-template-default a:hover,
.post-template-default a:hover,
.post-content a:hover {
    color: #8196c6;
}

#large-feat .white-caption {
    max-width: 100%;
    width: 100%;
}

.white-caption a {
    color: #fff;
    text-shadow: 2px 2px rgba(64, 64, 64, 0.5);
}

.white-caption a:hover {
    color: #b1c6f6;
    text-shadow: 2px 2px rgba(64, 64, 64, 0.5);
}

#featured-posts {
    height: 280px;
    margin-bottom: 20px;
}

.medium-feat {
    height: 134px;
}

.post-title {
    font-size: 18px;
}

.page-template h1,
.page-template h2,
.page-template h3,
.page-template h4,
.page-template h5,
.page-template h6,
.page-template-default h1,
.page-template-default h2,
.page-template-default h3,
.page-template-default h4,
.page-template-default h5,
.page-template-default h6,
.post-template-default h1,
.post-template-default h2,
.post-template-default h3,
.post-template-default h4,
.post-template-default h5,
.post-template-default h6,
.post-title a,
.menu-item a,
a.thumb-cat {
    color: #fff;
}

.section-heading h5 {
    color: #fff;
}

.post-title a:hover,
.menu-item a:hover,
a.thumb-cat:hover {
    color: #fff;
}

a.page-numbers {
    color: #213666;
}

a.page-numbers:hover,
a.post-page-numbers:hover {
    color: #ebebec;
    background-color: #516696;
}

a.post-page-numbers {
    display: inline-block;
    padding: 5px 7px 4px;
    margin-right: 3px;
    color: #213666;
    border-radius: 4px;
    background-color: #ebebec;
    text-decoration: none;
    transition: all .3s;
}

a.vglnk {
    color: #81f6c6;
    text-decoration-style: double;
}

a.vglnk:hover {
    color: #51c696;
}

span.post-page-numbers {
    display: inline-block;
    padding: 5px 7px 4px;
    margin-right: 3px;
    color: #ebebec;
    border-radius: 4px;
    background-color: #516696;
    text-decoration: none;
    transition: all .3s;
}

.sub-menu a {
    color: #b1c6f6;
}

.sub-menu a:hover {
    color: #8196c6;
}

.page-template thead tr th,
.post-template-default thead tr th {
    background-color: #7186b6;
    padding: 10px 5px;
}

.page-template tbody tr th,
.post-template-default tbody tr th {
    background-color: #516696;
    padding: 10px 5px;
}

.page-template tbody tr td,
.post-template-default tbody tr td {
    background-color: #314676;
    padding: 10px 5px;
    text-align: center;
}

#main-nav-outer {
    background-color: #213666;
}

.entry p,
.widget p,
.comment-text p,
.entry .post-nav-links {
    margin-bottom: 10px;
}

.tr-article-subhead {
    margin-top: 30px;
    color: #eee;
}

.wp-block-button .wp-block-button__link:hover,
.post-password-form input[type=submit]:hover,
#favorites-icon,
.thumb-cat:hover,
#featured-icon,
#slider-nav a:hover,
.section-heading h5:after,
.section-heading a:after,
.pagination a:hover,
.pagination .current,
.widget .mc4wp-form input[type=submit]:hover,
#main-footer .widget .mc4wp-form input[type=submit]:hover,
.widget #searchsubmit:hover,
#main-footer .widget #searchsubmit:hover,
.widget .small-post .thumb-cat:hover,
#main-footer .widget .small-post .thumb-cat:hover,
.tagcloud a:hover,
#main-footer .tagcloud a:hover,
.single-cats a,
.single-share .mail-to,
.comments-info-btn,
.comment-text .comment-reply-link:hover,
#submit,
.footer-line,
#footer-subscription h1:after,
#footer-subscription input[type=submit]:hover,
#footer-social a:hover,
#back-to-top,
.error-wrap #searchsubmit:hover,
.wpcf7-submit {
    background-color: #516696;
}

li.comment-author {
    color: #fff;
}

.comment-reply-link {
    background-color: #516696;
}

.comment-reply-link a:hover {
    background-color: #7186b6;
}

.entry {
    font-size: 15px;
}

.entry li,
.comment-text li {
    margin-bottom: 0px;
}

.comment-text {
    font-size: 15px;
}

.comments-info-btn,
.comment-body {
    margin-bottom: 20px;
}

#wpcomm .wc-comment .wc-comment-right {
    border: 2px solid #516696;
    background-color: #314676;
    border-radius: 8px;
}
#wpcomm .wc-reply .wc-comment-right {
    background-color: #415686;
}
#wpcomm .wc-footer-left .wc-reply-button {
    background-color: #516696;
    border-radius: 4px;
    color: #ddd;
}
.wpd-stat-authors-count,
#wpcomm .wc-vote-link.wc-down,
#wpcomm .wc-vote-link.wc-up,
#wpcomm .wc-comment-date,
#wpcomm .wpf-cta,
#wpcomm .wc-comment-footer .wc-vote-result.wc-vote-result-like,
#wpcomm .wc-comment-footer .wc-vote-result.wc-vote-result-dislike,
#wpcomm .wc-footer-right .wc-toggle,
#wpcomm .wc-footer-right .wc-toggle a,
#wpcomm .wc-footer-right .wc-toggle i,
#wpcomm .wpdiscuz-form-top-bar .wpdiscuz-user-settings,
#wpcomm .wpdiscuz-form-bottom-bar .wpdiscuz-fbb-left .wpdiscuz-stat,
#wpcomm .wpdiscuz-form-bottom-bar .wpdiscuz-fbb-right .wpdiscuz-stat.wpd-stat-users i {
    color: #ddd;
}
#wpcomm .wpdiscuz-readmore,
#wpcomm .wc-blog-guest > .wc-comment-right .wc-comment-author,
#wpcomm .wc-blog-guest > .wc-comment-right .wc-comment-author a {
    color: #b1c6f6;
}
#wpcomm .wc-comment-header,
#wpcomm .wc-reply .wc-comment-header {
    border-top: none;
}
#wpcomm .wpdiscuz-form-top-bar .wpdiscuz-user-settings:hover {
    color: #b1c6f6;
}
.wpd-wrapper.wpd-guest-settings .wpd-user-settings-button-wrap {
    background-color: #314676;
}
.wpd-wrapper.wpd-guest-settings .wpd-user-settings-button-wrap:hover {
    background-color: #516696;
}
.wc_comm_form .fas {
    color: #333;
}
.wc_comment_level-1 {
    margin-bottom: 6px;
    padding-top: 6px;
    border-top: 1px solid #516696;
}

blockquote {
    background-image: none;
    background-color: #516696;
    border-radius: 6px;
    padding: 1px;
}
#wpcomm .wpdiscuz-spoiler-wrap {
    background-color: #516696;
    border-color: #314676;
}

.comment-date {
    margin: 0;
}

.single-tags {
    margin: 0;
}

.single-tags a {
    background-color: #516696;
    color: #fff;
}

.single-tags a:hover {
    background-color: #e8e8e8;
    color: #516696;
}

.cld-like-dislike-wrap,
.single-elements-wrap {
    margin: 0;
    margin-bottom: 10px;
}

.dots,
.post-categories a {
    color: #fff;
}

.medium-post .post-thumb {
    width: 200px;
    max-width: 40%;
    margin: 10px;
}

.post {
    margin-right: 10px;
    margin-bottom: 24px;
}

article {
    position: relative !important;
    top: 0 !important;
    left: 0 !important;
    margin-bottom: 10px !important;
}

article.post {
    margin-bottom: 0;
}

.recent-posts .medium-post:nth-child(odd) {
    clear: left;
}

#featured-slider.featured-slider-two,
#featured-slider .caption-bg {
    height: 200px;
}

#featured-slider img {
    margin-top: -50px;
}

#featured-slider,
.section-heading,
.related-posts-header {
    margin-bottom: 20px;
}

.post-meta {
    float: left;
}

.main-sidebar {
    padding: 10px;
    background-color: #314676;
}

.single-tags {
    margin-bottom: 10px;
}

#author-name a {
    color: #fff;
}

#author-avatar {
    margin-bottom: -10px;
    transform: scale(0.75);
}

#about-author {
    padding-bottom: 10px;
    margin-bottom: 20px;
}

#author-icons {
    display: inline;
}

.single-share-center .single-share-icons {
    background-color: transparent;
}

.et_social_sidebar_networks,
.et_monarch .et_social_mobile {
    display: none;
}
}
