style.css:
@-moz-document domain(twitter.com) {

	
/*grey background, delete this is you're using dark mode*/
  body
  {
    background-color: #F1F1F1 !important;
  }

/* main tweet pane width */
.r-1ye8kvj {
	max-width: 800px !important;
}

/*heading text smaller*/
  .r-adyw6z
  {
    font-size: 15px !important;
  }

/*don't use in-house chirp font*/
*{
font-family: "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif !important;

}

/*avatar*/
  .r-sdzlij
  {
    border-radius: 5px !important;
  }

/*image box*/
  .r-t23y2h
  {
    border-radius: 8px !important;
  }

/*content box*/
  .r-1ylenci
  {
    border-radius: 8px !important;
  }

/* whole tweet pane bottom padding, 4 less*/
.r-kzbkwu {
  padding-bottom: 8px !important;
}


/* widen button div width with the new dumb views thing on there*/
.r-1mdbhws {
  max-width: 500px !important;
}

/*left side menu*/
.r-xyw6el {
padding: 8px !important;
}

/* left pane */
.r-o96wvk {
width: 225px !important;
}


/*right pane with trends*/
.r-1ovo9ad {
width: 300px !important;
}

/*above the tweet area*/
.r-14yzgew {
    padding-top: 2px !important;   
}

/*new april 2022 some extra padding*/
.r-1t982j2 {
    padding-top: 2px !important;
}

/* left menu padding */
.r-1ag2gil {
    padding-bottom: 4px !important;
    padding-top: 4px !important;
}

/* text box 1/2 the radius*/
.r-1867qdf {
border-radius: 8px !important;
}

/*left side area flex number, 1 is even*/
.r-obd0qt {
    flex-grow: 0.50 !important;  
}

/*set the flex grow to 0 here so the above tweet text won't go out of alignment from the above class that ends in qt*/
  .r-18kxxzh
  {
    flex-grow: 0 !important;
  }


/* retweet popup */
  .r-w0qc3r
  {
    padding-bottom: 6px !important;
    padding-top: 6px !important;
  }

/*between text and like, retweet*/
  .r-a1ub67
  {
    margin-top: 6px !important;
  }

  .r-psjefw
  {
    margin-top: 6px !important;
  }

  .r-1wqkr8a
  {
    padding-bottom: 0.50rem !important;
    padding-top: 0.50rem !important;
  }

  .r-l4ido4
  {
    padding-bottom: 0.50rem !important;
  }

/*thread padding*/
  .r-1k3wbuw
  {
    padding-top: 0.50rem !important;
  }

/*show more replies*/
  .r-kmv1fd
  {
    padding-bottom: 6px !important;
    padding-top: 6px !important;
  }

/*show more*/
  .r-q81ovl
  {
    padding: 6px !important;
  }


/*single tweet timestamp*/
  .r-1l7z4oj
  {
    padding-bottom: 6px !important;
  }

/*content area*/
  .r-1pdl7sx
  {
    padding-bottom: 6px !important;
    margin-bottom: 6px !important;
  }


/*single tweet button row, also sets height on suggested accounts on right pane commented bc of avatars*/
  .r-a2tzq0
  {
    height: 35px !important;
  }

/*single tweet numbers*/
  .r-1yzf0co
  {
    padding-top: 8px !important;
    padding-bottom: 8px !important;
  }

/*button row*/
  .r-1s2bzr4
  {
    margin-top: 6px !important;
    margin-bottom: 2px !important;
  }

/*show more tweets bar*/
  .r-1szxp23
  {
    min-height: 35px !important;
  }


}
